[[module CSS]]
:root {
     --header-title: "The Sanest King";
     --header-subtitle: "FUN - FEAR - FINANCE.";
}

.authorlink-wrapper {
  margin:auto !important;
  width: fit-content;
}

.openingQuote {
  font-family: Georgia;
  font-size: 1.9rem;
  padding: 1rem 10% 0 10%;
  font-style: italic;
}
.openingCitation {
  font-size: 1.1rem;
  text-align: right;
  font-weight: 200;
  margin-right: 2%;
}

.articleLink p {
  font-size:2rem;
  font-weight: bold;
  margin: 0;
  font-family: var(--header-font);
}

.articleCitation p {
  font-weight:200;
  margin: 0;
}

.articlePreview{
  font-family: Georgia;
  font-size: 1.3rem;
  font-style: italic;
}

.hatterPic {display:none;}
.hatterPic2 {width:20%;}

@media only screen and (min-width: 850px) {
.openingQuote {
  padding: 1rem 4% 0 4%;
  text-align: right;
}
}
@media only screen and (min-width: 1200px) {
.hatterPic {
  display:block;
  width: 20%;
  position: absolute;
  left: 0;
  margin: auto;
  margin-top: -25px
}
.hatterPic2 {display:none;}
}
