[[module css]]
@charset "UTF-8";
@import url("https://fonts.googleapis.com/css2?family=Kaisei+Tokumin:wght@400;500;700;800&display=swap");
/* ==== ãƒ¬ã‚¤ã‚¢ã‚¦ãƒˆèª¿æ•´ ==== */
#html-body #container-wrap {
  background: none;
}
#html-body #container {
  background: rgb(242, 255, 255);
  background: linear-gradient(90deg, rgb(245, 255, 255) 0%, rgb(230, 255, 255) 100%);
}
#html-body #header {
  display: none;
}
#html-body #header > h1,
#html-body #header > h2,
#html-body #header > #search-top-box,
#html-body #header > #top-bar {
  display: none;
}
#html-body #side-bar {
  display: none;
}
#html-body #content-wrap {
  max-width: unset;
  margin: 0 auto;
}
#html-body #main-content {
  margin: 0;
  padding: 0;
  overflow: hidden;
}
@media (max-width: 767px) {
  #html-body #main-content {
    max-width: 100%;
    margin: 0;
  }
}
#html-body #page-title {
  display: none;
}

#page-content {
  font-family: "Kaisei Tokumin", serif;
  font-weight: 400;
  font-style: normal;
  font-size: 1rem !important;
  line-height: 1.5;
  /* ==== main_visual ==== */
  /* ==== common ==== */
  /* ==== about ==== */
  /* ==== character ==== */
}
#page-content > .rate,
#page-content > .about,
#page-content > .character,
#page-content > .howto {
  padding: 0 1rem;
  max-width: 1000px;
  margin: 0 auto;
}
#page-content > .info {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-86-aktl/%E8%83%8C%E6%99%AF%E7%B4%A0%E6%9D%90.png");
}
#page-content .main_visual {
  position: relative;
}
#page-content .main_visual .main_visual_logo {
  position: absolute;
  bottom: 0;
  right: 0;
  width: 40%;
  max-width: 500px;
  margin: 0 1rem 1rem 0;
}
#page-content .main_visual .main_visual_logo .logo {
  filter: drop-shadow(0px 0px 10px rgb(255, 255, 255));
}
#page-content .main_visual .main_visual_logo .release {
  filter: drop-shadow(0px 0px 3px rgba(255, 255, 255, 0.5));
}
#page-content .page-rate-widget-box {
  box-shadow: none;
  display: flex;
  align-items: stretch;
  justify-content: center;
  align-content: stretch;
}
#page-content .page-rate-widget-box span {
  height: 100%;
}
#page-content .page-rate-widget-box > span:first-child {
  padding: 1rem;
}
#page-content .page-rate-widget-box > span.rateup a, #page-content .page-rate-widget-box > span.ratedown a {
  padding: 1rem;
  height: 100%;
  margin: 0;
}
#page-content .page-rate-widget-box > span.cancel {
  padding: 0.75rem;
}
#page-content .creditRate {
  margin: 0;
}
#page-content .creditRate .rate-box-with-credit-button {
  height: 40px;
  align-items: stretch;
}
#page-content .creditRate .rate-box-with-credit-button .creditButton {
  width: 2rem;
  height: 100%;
  border: none;
  box-shadow: none;
}
#page-content .rate {
  margin: 1rem auto;
  display: flex;
  align-items: stretch;
  justify-content: center;
  align-content: stretch;
}
#page-content .heading {
  text-align: center;
  margin: 4rem auto 2rem auto;
}
#page-content .heading span {
  display: inline-block;
  font-family: "Kaisei Tokumin", serif;
  font-weight: 800;
  font-style: normal;
  margin: 0 auto;
  text-align: center;
  padding: 0;
  font-size: 3rem;
  background: linear-gradient(transparent 70%, #E9C8B4 70%);
  line-height: 1.1;
}
#page-content .about .about_section {
  text-align: center;
}
#page-content .character .yui-navset {
  margin-top: -2rem;
}
#page-content .character .yui-navset .yui-nav {
  border-bottom: unset;
  display: flex;
  justify-content: space-around;
  align-items: stretch;
  align-content: stretch;
  flex-wrap: wrap;
  margin: 0;
  padding: 0;
}
#page-content .character .yui-navset .yui-nav li {
  box-sizing: border-box;
  flex: 1 1 25%;
  min-width: 160px;
  max-width: 200px;
  margin: 0;
  padding: 0;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  align-content: stretch;
  height: 150px;
}
#page-content .character .yui-navset .yui-nav li.selected a {
  color: black;
}
#page-content .character .yui-navset .yui-nav li:hover a::after {
  height: 160px;
}
#page-content .character .yui-navset .yui-nav li a {
  border: none;
  background: none;
  flex: 0 0 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  height: 3rem;
  position: relative;
}
#page-content .character .yui-navset .yui-nav li a::after {
  content: "";
  position: absolute;
  bottom: 0;
  right: -50px;
  width: 250px;
  height: 150px;
  z-index: 2;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: top center;
  transition: all 0.25s ease;
}
#page-content .character .yui-navset .yui-nav li a em {
  padding: 0;
  border: none;
  flex: 0 0 100%;
  top: 0;
  text-align: center;
  font-size: 1.5rem;
  font-weight: 700;
  color: white;
  z-index: 3;
}
#page-content .character .yui-navset .yui-nav li:nth-child(1) a::after {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-54-1dvs/073_default_1.png");
}
#page-content .character .yui-navset .yui-nav li:nth-child(1) em {
  text-shadow: 0 0 5px #034f93;
}
#page-content .character .yui-navset .yui-nav li:nth-child(2) a::after {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-59-7v72/076_default_1.png");
}
#page-content .character .yui-navset .yui-nav li:nth-child(2) em {
  text-shadow: 0 0 5px #64110e;
}
#page-content .character .yui-navset .yui-nav li:nth-child(3) a::after {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-70-unr8/682_default_1.png");
}
#page-content .character .yui-navset .yui-nav li:nth-child(3) em {
  text-shadow: 0 0 5px #3a3f3b;
}
#page-content .character .yui-navset .yui-nav li:nth-child(4) a::after {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-71-a0y6/clef_default_1.png");
}
#page-content .character .yui-navset .yui-nav li:nth-child(4) em {
  text-shadow: 0 0 5px #865f1c;
}
#page-content .character .yui-navset .yui-content {
  border: unset;
  background: unset;
}
#page-content .character .yui-navset .yui-content .character_wrap {
  display: flex;
  justify-content: center;
  align-items: stretch;
  align-content: center;
  flex-wrap: wrap;
  margin: 0 auto;
}
#page-content .character .yui-navset .yui-content .character_wrap .character_pic {
  flex: 1 1 40%;
  min-width: 200px;
  overflow: hidden;
}
#page-content .character .yui-navset .yui-content .character_wrap .character_pic img {
  position: relative;
  top: -1rem;
}
#page-content .character .yui-navset .yui-content .character_wrap .character_desc {
  flex: 1 1 60%;
  display: flex;
  justify-content: left;
  align-items: center;
}
#page-content .character .yui-navset .yui-content .character_wrap .character_desc > div {
  margin: 1rem;
  padding: 1.5rem;
  text-shadow: 0 0 15px rgb(255, 255, 255);
  font-weight: bold;
}
#page-content .character .yui-navset .yui-content .character_wrap .character_desc > div > span {
  font-size: 1.25rem;
  display: inline-block;
  margin-bottom: 0.5rem;
  font-weight: bold;
}
#page-content .character .yui-navset .yui-content > div {
  background-repeat: no-repeat;
  background-size: cover;
  background-color: rgba(255, 255, 255, 0.3);
  background-blend-mode: lighten;
}
#page-content .character .yui-navset .yui-content > div:nth-child(1) {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-78-pmmo/073_%E3%82%B9%E3%83%81%E3%83%AB1.png");
  background-position: top center;
}
#page-content .character .yui-navset .yui-content > div:nth-child(2) {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-79-lkbn/076%E3%82%B9%E3%83%81%E3%83%AB.png");
  background-position: 35% 0;
}
#page-content .character .yui-navset .yui-content > div:nth-child(3) {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-80-8jdy/682_%E3%82%B9%E3%83%81%E3%83%AB.png");
  background-position: 70% 0;
}
#page-content .character .yui-navset .yui-content > div:nth-child(4) {
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-81-wczu/clef_%E3%82%B9%E3%83%81%E3%83%AB.png");
  background-position: 30% 0;
}
#page-content .howto .howto_section {
  text-align: center;
}
#page-content .howto .howto_gallery {
  margin: 2rem auto;
  display: flex;
  justify-content: center;
  gap: 2rem;
  align-items: stretch;
  align-content: stretch;
  flex-wrap: wrap;
  max-width: 100vw;
}
#page-content .howto .howto_gallery img {
  max-width: 300px;
  flex: 0 0 30%;
}
#page-content .info {
  gap: 2rem;
  margin: 5rem auto 0 auto;
  padding: 5rem 2rem;
}
#page-content .info .info_container {
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: stretch;
  flex-wrap: wrap;
  margin: 0 auto;
  max-width: 1000px;
}
#page-content .info .info_container .info_pic {
  flex: 1 1 45%;
  min-width: 200px;
  overflow: hidden;
}
#page-content .info .info_container .info_pic .info_pic_heading {
  text-align: center;
  font-size: 1.5rem;
  font-weight: 700;
}
#page-content .info .info_container .info_pic img {
  display: block;
  max-width: 250px;
  margin: 0 auto;
}
#page-content .info .info_container .info_desc {
  flex: 1 1 45%;
  min-width: 235px;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-folded {
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-folded::after {
  content: "â€» PCç’°å¢ƒã§ã®ãƒ—ãƒ¬ã‚¤ã‚’æ¨å¥¨ã—ã¾ã™";
  position: absolute;
  bottom: -0.25rem;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-folded .collapsible-block-link {
  display: inline-block;
  margin: 2rem auto;
  padding: 1rem 2rem;
  background: linear-gradient(180deg, rgb(255, 39, 191) 0%, rgb(255, 145, 99) 66%);
  border-radius: 20px;
  font-weight: bold;
  font-size: 1.5rem;
  color: white;
  text-decoration: none;
  box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
  transition: all 0.25s ease;
  transform: scale(1);
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-folded .collapsible-block-link:hover {
  transform: scale(1.05);
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: 10;
  background: rgba(0, 0, 0, 0.5);
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .game {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 70vw;
  height: 70vh;
  z-index: 12;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .game .game_frame {
  width: 100%;
  height: 100%;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container {
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  align-content: stretch;
  justify-content: center;
  align-items: stretch;
  position: absolute;
  top: 5vw;
  left: 50%;
  transform: translateX(-50%);
}
@media (max-height: 770px) {
  #page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container {
    top: 1vw;
  }
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container .share-button {
  display: flex;
  margin: 0.25rem;
  flex-direction: row;
  flex-wrap: nowrap;
  align-content: center;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  transition: transform 0.2s;
  transform: scale(1);
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container .share-button:hover {
  transform: scale(1.05);
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container .share-button.x {
  background: #0F1419;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container .share-button.x .brand-logo {
  color: white;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container .share-button .brand-logo {
  display: block;
  padding: 0.25rem 0.75rem;
  font-size: 1.75rem;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .share-button-container .share-button .link-text {
  padding: 0 1rem 0 0.25rem;
  color: white;
  font-weight: bold;
  font-size: 1.05rem;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .ad_area_left {
  position: fixed;
  width: 15vw;
  height: 100vh;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  z-index: 12;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  align-content: center;
  gap: 1rem;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .ad_area_bottom {
  position: fixed;
  width: 100vw;
  height: 20vh;
  bottom: 0;
  left: 0;
  z-index: 12;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  gap: 1rem;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .game_wrap .ad_area_bottom > img {
  width: auto;
  height: 18vh;
}
#page-content .info .info_container .info_desc .collapsible-block .collapsible-block-unfolded .collapsible-block-link {
  position: fixed;
  top: 10%;
  right: 10%;
  display: inline-block;
  padding: 1rem;
  background: white;
  border-radius: 10px;
  font-weight: bold;
  font-size: 1.5rem;
  color: black;
  text-decoration: none;
  box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
  z-index: 30;
}
#page-content .footer {
  width: 100vw;
  height: 100px;
  background-color: rgb(45, 45, 45);
  background-image: url("https://scp-jp-storage.wikidot.com/local--files/file:4137401-83-p63b/%E4%BC%81%E6%A5%AD%E3%83%AD%E3%82%B4.png");
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
}

