    // ãƒ†ã‚­ã‚¹ãƒˆã‚’ä¸€æ–‡å­—ãšã¤è¡¨ç¤ºã™ã‚‹é–¢æ•°
    function animateText(element, text, index = 0) {
        isAnimating = true;

        // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¹ã‚­ãƒƒãƒ—ã®ç¢ºèª
        if (skipAnimation) {
            element.textContent = text;
            isAnimating = false;
            skipAnimation = false;
            return;
        }

        if (index < text.length) {
            element.textContent += text.charAt(index);
            // ã‚¿ã‚¤ãƒãƒ¼IDã‚’ä¿å­˜ã—ã¦å¾Œã§ã‚­ãƒ£ãƒ³ã‚»ãƒ«ã§ãã‚‹ã‚ˆã†ã«ã™ã‚‹
            animationTimerId = setTimeout(() => animateText(element, text, index + 1), 50); // 0.05ç§’ã”ã¨ã«1æ–‡å­—è¡¨ç¤º
        } else {
            isAnimating = false;
            animationTimerId = null;
        }
    }

    // HTMLãƒ†ã‚­ã‚¹ãƒˆã‚’ä¸€æ–‡å­—ãšã¤è¡¨ç¤ºã™ã‚‹é–¢æ•°ï¼ˆã‚¿ã‚°ã‚’è€ƒæ…®ï¼‰
    function animateHTMLText(element, html) {
        isAnimating = true;
        let plainText = html.replace(/<[^>]*>/g, '');
        let currentText = '';
        let textIndex = 0;
        let htmlIndex = 0;

        // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¹ã‚­ãƒƒãƒ—ã®ç¢ºèª
        if (skipAnimation) {
            element.innerHTML = html;
            isAnimating = false;
            skipAnimation = false;
            return;
        }

        function addNextChar() {
            // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¹ã‚­ãƒƒãƒ—ã®ç¢ºèª
            if (skipAnimation) {
                element.innerHTML = html;
                isAnimating = false;
                skipAnimation = false;
                return;
            }

            if (textIndex >= plainText.length) {
                element.innerHTML = html;
                isAnimating = false;
                animationTimerId = null;
                return;
            }

            // æ¬¡ã®ãƒ—ãƒ¬ãƒ¼ãƒ³ãƒ†ã‚­ã‚¹ãƒˆæ–‡å­—ã‚’æ¢ã™
            while (htmlIndex < html.length) {
                if (html[htmlIndex] === '<') {
                    // ã‚¿ã‚°ã®å§‹ã¾ã‚Šã‚’è¦‹ã¤ã‘ãŸã‚‰ã€çµ‚ã‚ã‚Šã¾ã§å…¨ã¦è¿½åŠ 
                    let tagEnd = html.indexOf('>', htmlIndex);
                    if (tagEnd !== -1) {
                        currentText += html.substring(htmlIndex, tagEnd + 1);
                        htmlIndex = tagEnd + 1;
                    } else {
                        // ã‚¿ã‚°ãŒé–‰ã˜ã‚‰ã‚Œã¦ã„ãªã„å ´åˆï¼ˆã‚¨ãƒ©ãƒ¼å‡¦ç†ï¼‰
                        currentText += html[htmlIndex];
                        htmlIndex++;
                    }
                } else {
                    // ãƒ—ãƒ¬ãƒ¼ãƒ³ãƒ†ã‚­ã‚¹ãƒˆæ–‡å­—ã‚’è¿½åŠ 
                    currentText += html[htmlIndex];
                    htmlIndex++;
                    textIndex++;
                    break;
                }
            }

            element.innerHTML = currentText;
            // ã‚¿ã‚¤ãƒãƒ¼IDã‚’ä¿å­˜
            animationTimerId = setTimeout(addNextChar, 50); // 0.05ç§’ã”ã¨ã«1æ–‡å­—è¡¨ç¤º
        }

        addNextChar();
    }

    // ã‚·ãƒŠãƒªã‚ªã‚’è¡¨ç¤ºã™ã‚‹é–¢æ•°
    function showContent(index) {
        if (index >= CONTENTS.length) {
            // ã‚·ãƒŠãƒªã‚ªãŒçµ‚äº†ã—ãŸå ´åˆã€ã‚¨ãƒ³ãƒ‰ç”»é¢ã‚’è¡¨ç¤º
            document.getElementById("main").style.display = "none";
            document.getElementById("end").style.display = "block";

            // ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠè§£é™¤
            document.getElementById("main").removeEventListener("click", setupClickEvent);


            return;
        }

        // ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ã®çŠ¶æ…‹ã‚’ãƒªã‚»ãƒƒãƒˆ
        isAnimating = false;
        skipAnimation = false;
        if (animationTimerId !== null) {
