        const characterElement = document.getElementById("character");

        // åå‰ã®è¡¨ç¤ºï¼ˆ[[USERNAME]]ã‚’ç½®ãæ›ãˆï¼‰
        nameElement.textContent = content.name.replace(/\[\[USERNAME\]\]/g, userName);

        // ãƒ†ã‚­ã‚¹ãƒˆã®åˆæœŸåŒ–
