|Huiwen-mincho=true
]]

[[include :scp-wiki-cn:theme:alexylva-university]]

[[module CSS]]


.poem {
font-size: 200%;
line-height:2.1rem;
    text-align:center;
    letter-spacing:0.05rem;
    font-family:  Huiwen-mincho, sans-serif;
}

tt {
font-size: 100%;
font-family: initial;
}

@media (max-width: 1500px){
.poem  {
    font-size: 150%;
    line-height:1.8rem;

}
}


.main-body-div {
    padding: 10px 25px;
    background: url("http://scp-wiki.wdfiles.com/local--files/theme%3Aalexylva-university/3.jpg");
        background-repeat: repeat;
        background-size: auto;
    background-repeat: repeat;
    background-size: contain;
margin-bottom: 25px;
transition: all .7s;
border: 3px double #BC4123;
}

#header h1 a {
    content: "缢王悲歌";
}

#header h1 a::before {
    content: "缢王悲歌";
}

#header h2 span::before {
    content: "如是群鸦笑不已";
}


[[/module]]
