[[module css]]
:root {
--logo:url(https://scp-sandbox-3.wdfiles.com/local--files/thebillith/synec.png);
--title:"R.S. SYNECDOCHE";
--subtitle:"SUPERVISION CONTROLS PROBLEMS";
--barColor:rgb(237, 237, 237);

 --basalt-overtone: 100, 100, 100;
    --basalt-undertone: var(--admo-color-bright);
    --basalt-UI-dark-palette: var(--admo-color-dim);
    --basalt-bright-element-color: var(--admo-color-bright);
    --basalt-dark-element-color: var(--admo-color-alt);
    --basalt-primary-color: var(--admo-color-dark);
    --basalt-secondary-color: var(--admo-color-dim);
    --basalt-tertiary-color: 40, 40, 40;
    --basalt-main-text-color: 190, 190, 190;
    --basalt-light-text-color: 0, 0, 0;

    --admo-color-bright: 194, 25, 53;
    --admo-color-dim: 10, 10, 10;
    --admo-color-dark: 0, 0, 0;
    --admo-color-alt: 150, 15, 37;
    --admo-backdrop: none;

 --window-radius: 0.875rem;
    --window-shadow: 0 0 0.625rem rgb(var(--basalt-UI-dark-palette),0.5);
    --window-border-width: 0.125rem;
    --window-buttons: radial-gradient(circle at 1rem 50%, rgb(var(--basalt-cancel-color)) 0.35rem, rgb(0,0,0,0) 0.375rem), radial-gradient(circle at 2.125rem 50%, rgb(var(--basalt-alternate-color)) 0.35rem, rgb(0,0,0,0) 0.375rem), radial-gradient(circle at 3.25rem 50%, rgb(var(--basalt-positive-color)) 0.35rem, rgb(0,0,0,0) 0.375rem);



--darkdocument-background-color: 22, 22, 22;

:is(#page-content table:not(.form), table.wiki-content-table) th :is(div.blockquote, .blockquote, blockquote) {
    font-family: var(--UI-font);
    font-weight: normal;
    text-align: left;
    background-color: rgb(var(--basalt-primary-color));
    word-spacing: 2px;

div.blockquote {
    background-color: rgb(var(--blockquote-background-color)) !important;
:is(#page-content table:not(.form), table.wiki-content-table) td {
    background-color: 48, 48, 48;
}
}
}

}
@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

[class^="skip_"] {
    box-shadow: var(--window-shadow);
    border-radius: var(--window-radius);
    background-color: rgb(var(--basalt-background-color));
    padding: 0.5rem 1.5rem;
    margin: 1.5rem 0;
}

.skip_box, .skip_side {
    position: relative;
}
.skip_box::before,
.skip_side::before {
    content: "";
    display: block;
    width: 4rem;
    height: 2rem;
    position: relative;
    margin-inline-start: -0.5rem;
    margin-block-end: -0.75rem;
    background: var(--window-buttons);
}

.skip_box.terminal {
    position: relative;
    font-family: var(--mono-font);
    background-color: rgb(var(--basalt-UI-dark-palette));
}
.skip_box.terminal::after {
    content: "Terminal";
    font-weight: bold;
    position: absolute;
    top: 0;
    left: 0;
    box-sizing: border-box;
    width: 100%;
    padding: 0.875rem 5.125rem 0;
}

div.skip_window {
    position: relative;
    padding-top: 0;
}
.skip_window::before {
    content: attr(data-title);
    font-family: var(--header-font);
    font-weight: bold;
    display: grid;
    align-items: center;
    background-color: rgb(var(--basalt-UI-dark-palette));
    text-align: center;
    min-height: var(--window-radius);
    padding: 0.625em 4rem;
    margin: 0 -1.5rem;
    border-radius: var(--window-radius) var(--window-radius) 0 0;
}
.skip_window::after {
    content: "";
    position: absolute;
    inset-inline-start: 0.5rem;
    inset-block-start: 0;
    width: 4rem;
    height: 2.5rem;
    background: var(--window-buttons);
}

div.skip_float {
    font-size: 0.9125em;
    background-color: rgb(var(--blockquote-background-color),var(--basalt-UI-opacity));
    -webkit-backdrop-filter: blur(var(--basalt-UI-blur));
    backdrop-filter: blur(var(--basalt-UI-blur));
    float: left;
    width: calc(var(--main-content-width)/2.25);
    box-sizing: border-box;
    margin: 0.25rem 1.25rem;
    margin-left: clamp(calc(var(--main-content-width)/-4.5),calc((87.5vw - 95%)/-2), 0rem);
}
div.skip_float.right {
    float: right;
    margin-left: 1.25rem;
    margin-right: clamp(calc(var(--main-content-width)/-4.5),calc((87.5vw - 95%)/-2), 0rem);
}
@media only screen and (max-width: 640px) {
    #page-content div.skip_float {
        float: none;
        margin: 0.5rem auto;
        width: 100%;
    }
}
