[[module css]]

:root {

    --body-width-on-desktop: 80rem;
    --swatch-primary-darker: 64, 96, 22;
    --swatch-menubg-color: 45, 70, 45;

    --dark-accent: 167, 220, 255;
    --medium-accent: 46, 124, 255;
    --gray-monochrome: 212, 230, 241;
    --pale-gray-monochrome: 167, 220, 255;

    --bright-accent: 84, 153, 199;
    --medium-accent: 18,26,91;
    --tab-main-bgcolor:200, 230, 245;

    --barColour: #21252e;
    --barBlock:  rgb(var(--gray-monochrome));

    --golden-color:225,196,86;
    --tool-color-text:225,225,255;

      /* logo遮罩  */
   --gradient-logo-under:  linear-gradient(130deg, #111 0%, #333 0%, #000 0%);  
   --gradient-logo-cover: linear-gradient(rgb(255,215,0,0.2) 0%, rgb(55,25,25,0.3) 30%, rgb(210,105,10,0.7) 100%);
   --gold-image: url("http://scpsandboxcn.wikidot.com/local--files/pic/BGR-Golden-pic.png");
    --logo-image: url("https://nu-scptheme.github.io/Black-Highlighter/img/logo.svg");





/* 节选卡片 */
    --golden-color:225,196,86;
    --tool-color-text:225,225,255;

      --excborder-wrap-color:rgb(var(--golden-color));
  --excborder-mid-color:rgb(var(--golden-color));
  --excborder-title-color:rgb(var(--golden-color));

    --excerpts-bgcolor:#0a0619;
    --excerpts-text:#fff;
     --excerpts-link:rgb(var(--golden-color));
     --excborder-wrap-width:5px;
  --excborder-mid-width:3px;
  --excborder-title-width:3px;
    --excerpts-wrap-margin:1rem

}

/* ----- 常规 ----- */

a:visited {
    color: rgb(var(--medium-accent));
}
a.newpage {
    color:rgb(var(--bright-accent))
}

blockquote a, div.blockquote a {
    color: rgb(var(--medium-accent));
}

a:visited {
    color: rgb(var(--medium-accent));
}



hr {
    background-image: none;
    background-color: rgb(var(--bright-accent));
}

#header h1 a::before {
    content: "基金会半月刊";
}

#page-title {
    display: none;
}

/* ----- flex 盒子 ----- */
.flex-box {
    display: -webkit-flex; /* Safari */
    display: flex;
    flex-flow: row wrap;
    justify-content: space-between;
}

.flex-box > .box {
    flex-basis: 33%;
}

@media (max-width: 1040px) {
    .flex-box > .box {
        flex-basis: 49%;
    }
}

.box {
    background-color: rgb(var(--pale-gray-monochrome));
    border: 1px solid rgb(var(--dark-accent));
    padding: 0 1em;
    box-sizing: border-box;
    position: relative;
    margin-bottom: 1em;
    color: #182026
}

.box > .collapsible-block .collapsible-block-folded {
    position: absolute;
    bottom: 10px;
}

/* 企划作品  */
.show-list a{
color: #4e5166 !important;
}

.show-list {
    margin: 0 20%;
   position:    initial;
}

@media (max-width: 900px) {
    .show-list {
    margin:0;
}

}

/* 艺术作品 */

.post-img{
    display: flex;
    flex-wrap: wrap;
    align-items: flex-start;
    justify-content: center
}


/* ----- 版头标志垫图  ----- */


/* 限电脑屏幕 */
@media screen and (min-width: 500px){

#header {
   background-image: none;
}

    /**/

 #header #header-extra-div-1,
#header #header-extra-div-2{
    display: flex;
    position: absolute;
     width: 6.250rem;
    height: 6.250rem;
    display: flex;
    position: absolute;
    left: 0.15rem;
    top:1.5rem;
}

#header #header-extra-div-1::before,
#header #header-extra-div-2::before {
    content: "";
    width:6.9rem;
    height: 6.5rem;
    position: absolute;
    background-repeat: no-repeat;
    background-position: center center;
    background-size: contain;
    flex: 1;
}

 #header #header-extra-div-1::before {
    z-index: 1;
    background: var(--gold-image),
                         var(--gradient-logo-under);
    mask:var(--logo-image);
    mask-size: cover;
    -webkit-mask:var(--logo-image);
    -webkit-mask-size: cover;
    background-size: 50%;    
}
  #header #header-extra-div-2::before {
    background:var(--gradient-logo-cover);
    mask:var(--logo-image);
    mask-size: cover;
    -webkit-mask:var(--logo-image);
    -webkit-mask-size: cover;
    z-index: 2;
}   
}


/* ----- h1 改动 ----- */
#page-content h1 {
    font-family: var(--header-font);
    font-weight: bold;
    margin-top: 0.6rem;
    margin-bottom: 0.6rem;
    display: flex;
}
#page-content h1::after {
    content: "";
    flex-grow: 1;
    height: 0.1rem;
    margin: auto auto auto 0.375rem;
    background-color: rgb(var(--bright-accent));
}

/* ----- 页面 -----  */
#content-wrap{
    margin: 0 auto;
}

#main-content{
    margin-right: 13rem;
    margin-left: -2.5rem;
}

@media (max-width: 1300px) {
    #main-content {
        margin-right: 0rem;
        margin-left:-13rem;
    }
}

@media (max-width: 890px) {
    #main-content {      
        margin-right: 0rem;
        margin-left: 2rem;
    }
}

@media (max-width: 500px) {
    #main-content {      
        margin-right: 0rem;
        margin-left: 1rem;
    }
}
/* 浮动菜单 */
#u-float-memu #toc{
  word-break: keep-all;
}
.memu-ch {
    right: 0.6%;
}


/* ----- 折叠侧边栏 ----- */
#main-content::after {      
    -webkit-filter: sepia(100%) hue-rotate(90deg);
    filter: sepia(100%) hue-rotate(190deg) ;   
    z-index: -2;
}

#main-content::before {
    background-color: #000 !important;
    z-index: -2;
}

/* ----- 侧边栏 ----- */
#side-bar{
    top: 0;
    bottom: 0;
    z-index: 1;
    opacity: 0;
    background: var(--barColour) !important;
    overflow:  scroll;
}

#side-bar:hover, #side-bar:active {
    background-color: #21252e  !important;
    background-color: var(--barColour) !important;
    opacity: 1;
}

#side-bar .side-block {
    margin-top: 1em;
    padding-left: 0.25em;
    background-color: var(--barBlock);
    border: 4px double #2d462d;
    padding: 0.5rem;
}

@media (max-width: 890px) {
    #side-bar {
        opacity: 1;
    }
}

/* ----- flex 头条 ----- */
.mmposter-wrap{
    display: flex;
    justify-content: center ;   
    flex-wrap: wrap;
}

.mmposter-cell{
    width: 45%;
}

@media (max-width: 700px) {
    .mmposter-cell{
        width: 100%;
    }    
}

/* ----- tab 里的引用块 ----- */
.yui-content blockquote, div.blockquote {
    background: rgb(var(--pale-gray-monochrome));
    border: 3px double rgb(var(--medium-accent));
}

/* ----- 改页眉 ----- */

div#extra-div-1 {
    background-image: url("https://wanderers-sandbox.wikidot.com/local--files/admin:themes/wlsb-headerbg.png");
    background-size: 100% 10rem;
}

div#extra-div-2 {
    background: none;
}

div#extra-div-3 {
    background: #0E223B;
}

#search-top-box-input {
    background-color: #0E223B;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #4C4C19;
}

/* tab */

.yui-navset .yui-content {
    border-color: rgb(var(--light-gray-monochrome));
    border-top-color: rgb(var(--medium-accent));
    background-color: rgb(var(--tab-main-bgcolor));
    font-weight: normal;
}

.yui-navset .yui-nav li:hover a {
    color: rgb(var(--light-gray-monochrome));
}

[[/module]]
