[[module CSS]]
@font-face {
    font-family: 'GongGothicMedium';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_20-10@1.0/GongGothicMedium.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

@font-face {
    font-family: 'GongGothicBold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_20-10@1.0/GongGothicBold.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

:root{
  --header-title: "초도격리개발기지";
  --header-subtitle: "회수 · 격리 · 배송";
  --titleColor: #222;
  --subtitleColor: #222;
  --lgurl: url('http://scpko.wdfiles.com/local--files/thd-glasses-art-works-page/Site-07K.png');
}

#header h1 a::before {
  font-family: 'GongGothicBold', "Montserrat", "Arial", sans-serif;
}

#header h2::before, #page-title, h1, .modalbox div:not(.credit) h2 {
  font-family: 'GongGothicMedium', "Montserrat", "Arial", sans-serif;
}

html, body {
  overflow-x: hidden;
}

a, #side-bar a, #side-bar a:visited {
  color: #222;
  font-weight: 600;
}

a:visited {
  color: #444;
}

div#container-wrap {
  background: none;
}

#header {
  height: 220px;
  background: #FFD400;
  box-shadow: 50vw 0 0 #FFD400, -50vw 0 0 #FFD400, 25vw 0 0 #FFD400, -25vw 0 0 #FFD400;
}

#header::before {
  background-position: center 10px;
  opacity: 1;
}

#header h1, #header h2 {
  transform: translateY(105px);
}

#login-status, #login-status a {
  color: #222;
}

#account-topbutton {
  border-color: #222;
}

#search-top-box-form input[type="submit"] {
  color: #222;
}

#search-top-box-form input[type="submit"],
#search-top-box-form input[type="submit"]:hover,
#search-top-box-form input[type="submit"]:focus {
  border-color: #222;
  background: none;
}

#top-bar {
  top: 230px;
  background: #222;
  box-shadow: 50vw 0 0 #222, -50vw 0 0 #222, 25vw 0 0 #222, -25vw 0 0 #222;
}

#page-title, h1 {
  color: #222;
}

#page-title {
  border-bottom: 2px solid #222;
}

hr {
  height: 2px;
  background-color: #222;
}

blockquote hr,
div.blockquote hr {
  height: 1px;
}

.page-rate-widget-box {
  margin-right: 0;
}
.creditRate {
  width: 100%;
  justify-content: center;
  margin-right: 0 !important;
}

.page-rate-widget-box, .rate-box-with-credit-button {
  box-shadow: none !important;
  border: none;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .cancel,
.rate-box-with-credit-button {
  background: #FFD400 !important;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel,
.rate-box-with-credit-button {
  color: #222;
}

.rate-box-with-credit-button {
  border: 2px solid #222 !important;
}
.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
  border-color: #222;
  border-width: 2px;
}

.rate-box-with-credit-button .page-rate-widget-box .rate-points,
.rate-box-with-credit-button .page-rate-widget-box .rateup,
.rate-box-with-credit-button .page-rate-widget-box .ratedown,
.rate-box-with-credit-button .page-rate-widget-box .cancel {
  border-width: 0;
}

.rate-box-with-credit-button a,
.page-rate-widget-box a {
  font-weight: normal;
}

.rate-box-with-credit-button .page-rate-widget-box,
.rate-box-with-credit-button .page-rate-widget-box .cancel,
.rate-box-with-credit-button .page-rate-widget-box .cancel a {
  border-radius: 0;
}

.page-rate-widget-box .rate-points a:hover,
.page-rate-widget-box .cancel a:hover {
  background: #FFF !important;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #FFD400 !important;
}

.page-rate-widget-box a,
.rate-box-with-credit-button a {
  color: #222 !important;
}

.creditButton p a {
  border: none !important;
}

.creditButton p a:hover {
  color: #FFF !important;
}

.modalbox {
  background: linear-gradient(#FFD400 51px,#fefefe 51px,#fefefe) !important;
}

#top-bar .open-menu a {
  color: #222;
  border-color: #222;
}

#side-bar {
  background: #FFD400 !important;
}

#side-bar .red,
#side-bar .green,
#side-bar .blue {
  background: #FFF;
}

.ch.raisa {
  background: #FFD400;
  border-color: #222;
}

.ch.raisa h1 {
  padding: 0 15px;
}

.ch.raisa + p {
  height: 70px;
}

blockquote, div.blockquote {
  border: 1px solid #222;
}

.yui-nav {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: flex-end;
}

.yui-nav li {
  display: flex;
  flex-grow: 2;
  margin: 0;
}

.yui-nav li a {
  width: 100%;
  text-align: center;
}

.yui-navset .yui-nav a {
  background: #FFF;
  color: #222;
}

.yui-navset .yui-nav .selected {
  margin-bottom: 0;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
  background: #FFD400;
  color: #222;
}

.yui-navset .yui-nav {
  border-color: #222;
  border-bottom-width: 2px;
}

.yui-navset .yui-content {
  background: #FFF;
}
[[/module]]

