[[code type="css"]]
/*
    A Midnight Invitation CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish and Denevola

    Background-image: https://pixabay.com/illustrations/mountains-valley-nature-fog-clouds-6742185/;

    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC&display=swap');

/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/theme:a-midnight-invitation/background.png') center top repeat-x;
    background-size: 2000px;
}

#header {
    height: 230px;
    background-image: url('https://scpko.wikidot.com/local--files/theme:a-midnight-invitation/logo.png');
    background-position: 50% 37%;
    background-repeat: no-repeat;
    background-size: 200px;
}

#header h1 a {
    position: absolute;
    font-size: 100%;
    text-align: center;
    text-shadow: none;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

#header h1 a::before {
    content: " ";
    color: #00000000;
    width: 100%;
    height: 100%;
}

#header h2,
#header h1 a span,
#header h2 span {
    display: none;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 3px;
    color: #fff;
    text-shadow: -1px 0 #000, 0 1px #000, 1px 0 #000, 0 -1px #000;
    z-index: 2;
}

#login-status a {
    color: #fff;
    text-shadow: -1px 0 #000, 0 1px #000, 1px 0 #000, 0 -1px #000;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #ffffff;
}

#login-status ul a {
    color: #000 !important;
    text-shadow: -1px 0 #fff, 0 1px #fff, 1px 0 #fff, 0 -1px #fff;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #5A5A5A;
    border: solid 1px #D8D8D8;
    color: #DBBFA6;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #000000 !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #6C6C6C;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #ffffff;
    color: #ffffff;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #6C6C6C;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 219px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #ffffff;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #fff;
    border-color: transparent;
    color: #000;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #fff;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #92ffff;
}

/* Page Elements */
#page-title,
.meta-title {
    color: #000;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #000;
    font-weight: bold;
}

body {
    background-color: #F6F6F6;
    color: #000000;
}

a:link {
    color: #484848;
}

a:visited {
    color: #5A5A5A;
}

.hovertip {
    background-color: #EAEAEA !important;
    border: 2px solid #5A5A5A !important;
    color: #000000 !important;
}

.scp-image-block {
    background: #FBFBFB;
    border: 10px solid #EAEAEA;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    padding-top: 1em;
    background: #EAEAEA;
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #F6F6F6;
}

::-webkit-scrollbar-thumb {
    background: #484848;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #6C6C6C;
}

textarea::-webkit-scrollbar-track {
    background: #F6F6F6;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources {
    background-color: #F6F6F6;
    border: 2px solid #484848;
    border-radius: 10px 0;
}

#side-bar .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
    color: #000000;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #A2A2A2;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #000000;
}

#side-bar .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: hue-rotate(200deg);
}

#side-bar .collapsible-block-folded a:link {
    filter: hue-rotate(-200deg);
}

#side-bar a:link {
    color: #484848;
}

#side-bar a:visited {
    color: #5A5A5A;
}

#side-bar a:link:hover,
#side-bar a:visited:hover {
    color: #6C6C6C;
}

.scpnet-interwiki-frame {
    filter: invert(1) hue-rotate(245deg) saturate(.8);
}

#top-bar .open-menu a {
    border-color: #000000;
    color: #000000;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #808080;
    color: #808080;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #FBFBFB;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    border: 2px solid #C4C4C4;
    background: #323232;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #323232 !important;
    color: #fff !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #323232;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #fff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #C4C4C4;
    color: #323232;
}

.page-rate-widget-box .cancel {
    background: #323232;
}

.page-rate-widget-box .cancel a {
    color: #fff;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #C4C4C4;
    border-radius: 0;
    color: #323232;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #323232 !important;
    border: 2px solid #C4C4C4 !important;
    box-shadow: none !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: none !important;
    color: #fff !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #C4C4C4;
    border-radius: 0 3px 3px 0;
    color: #323232 !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #ffffff;
    border-color: #909090;
}

.owindow .modal-header {
    background-color: #ffffff;
}

.owindow .modal-body img {
    background-color: #ffffff !important;
    color: #000000;
}

.owindow .title {
    background-color: #ffffff;
}

.owindow a,
.owindow a:visited {
    color: #CECECE;
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: #727272 !important;
}

.owindow .button-bar a {
    background-color: #ffffff;
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #ffffff !important;
    border-color: #999999;
    color: #000000;
}

table.page-files .highlight {
    background-color: #FBFBFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #ffffff !important;
    border-color: #999999;
    color: #000000;
}

textarea {
    background-color: #FEFEFE;
    color: #000000;
}

.change-textarea-size a {
    background-color: #ffffff;
    color: #000000;
}

#lock-info {
    background-color: #ffffff;}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #FAFAFA;
    border-style: dotted;
}

/* etc. */
@media (min-width: 581px) and (max-width: 767px) {
    #header {
        background-size: 180px;
  }
}

@media (min-width: 480px) and (max-width: 580px) {
    #header {
        background-size: 200px;
  }
}

@media (min-width: 321px) and (max-width: 479px) {
    #header {
        background-size: 180px;
  }
}

@media (max-width: 320px) {
    #header {
        background-size: 180px;
  }
}

[[/code]]

[[/iftags]]
