[[code type="CSS"]]
/* 标准版本 */

@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans+Condensed:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Podkova:wght@400..800&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300..800;1,300..800&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Nanum+Gothic+Coding&display=swap');
@import url("https://cdn.jsdelivr.net/gh/IBM/plex@master/packages/plex-sans-sc/fonts/split/woff2/hinted/IBMPlexSansSC-Medium.css");
@import url("https://cdn.jsdelivr.net/gh/IBM/plex@master/packages/plex-sans-sc/fonts/split/woff2/hinted/IBMPlexSansSC-Regular.css");
@import url("https://cdn.jsdelivr.net/gh/IBM/plex@master/packages/plex-sans-sc/fonts/split/woff2/hinted/IBMPlexSansSC-Text.css");
@import url("https://cdn.jsdelivr.net/gh/IBM/plex@master/packages/plex-sans-sc/fonts/split/woff2/hinted/IBMPlexSansSC-Bold.css");
@import url("https://cdn.jsdelivr.net/npm/misans@4.0.0/lib/Normal/MiSans-Regular.min.css");
@import url("https://cdn.jsdelivr.net/npm/misans@4.0.0/lib/Normal/MiSans-Bold.min.css");
@import url("https://cdn.jsdelivr.net/npm/misans-webfont/misans-l3/misans-l3/result.css");
@font-face {
    font-family: 'Hangeuljaemin';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/Hangeuljaemin_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'ChillRoundF';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundF_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'ChillRoundF';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundF_Bold_3500.woff2') format('woff2');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
}
@font-face {    
    font-family: "Punctuation";
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/GlowSansSCBook_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
    unicode-range: U+2018-2019, U+201C-201D, U+2014, U+2026, U+00B7, U+FF1A-FF1B, U+FF01, U+FF0C, U+FF1F, U+3002;     
}
@font-face {    
    font-family: "Punctuation";
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/GlowSansSCBold_3500.woff2') format('woff2');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
    unicode-range: U+2018-2019, U+201C-201D, U+2014, U+2026, U+00B7, U+FF1A-FF1B, U+FF01, U+FF0C, U+FF1F, U+3002;     
}

    :root {
        /* S-CSS-P 集成 */
        --theme-base: "black-highlighter";
        --theme-id: "a-problematic-theme";
        --theme-name: "A Problematic Theme";

        /* 黑白配色 */
        --white: 252, 252, 252;
        --darker: 48, 48, 52;
        --yet-darker: 27, 28, 31;
        --darkest: 20, 22, 24;
        --black: 0, 0, 0;

        /* 标准配色 */
        --main-green: 151, 206, 142;
        --sage-green: 103, 146, 103;
        --christmas-green: 0, 135, 62;
        --celadon: 172, 225, 175;
        --chetwode: 240, 255, 240;
        --artichoke: 75, 111, 68;

        /* 暗色配色 */
        --gloomy-green: 74, 93, 35;
        --laurel-green: 169, 186, 157;
        --army-green: 75, 83, 32;
        --kombu-green: 53, 66, 48;
        --gin: 216, 228, 188;

        /* 红色配色 */
        --bloody-stain: 186, 9, 42;
        --light-red: 255, 204, 203;
        --salmon: 250, 128, 114;
        --desire: 234, 60, 83;
        --maroon: 128, 0, 0;
        --sangria: 94, 25, 20;
        --prismatic: 208, 61, 51;
        --light-coral: 240, 128, 128;

        /* 字体 */
        --header-font: "IBM Plex Sans Condensed", sans-serif;
        --title-font: "Podkova", serif;
        --body-font: "Open Sans", sans-serif;
        --mono-font: "Nanum Gothic Coding", monospace;
        --UI-font: var(--title-font);

        /* 页眉元素 */
        --logo-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Aa-problematic-theme/standard37.svg");
        --header-title: "FOUNDATION SITE-37";
        --header-subtitle: "We've Got Problems";

        /* 页眉渐变色 */
        --header-gradient-color-bottom: var(--main-green);
        --header-gradient-color-middle: var(--main-green);
        --header-gradient-color-top: var(--main-green);
        --diagonal-stripes: unset;

        /* 主题颜色 */
        --bright-accent: var(--celadon);
        --medium-accent: var(--main-green);
        --dark-accent: var(--main-green);
        --gray-monochrome: var(--sage-green);
        --dark-gray-monochrome: var(--artichoke);

        --link-color: var(--christmas-green);
        --hover-link-color: var(--sage-green);
        --visited-link-color: var(--artichoke);

        /* 菜单颜色 */
        --swatch-menubg-color: var(--artichoke);
    }
/* 汉化 */
    :root:lang(cn){
    --theme-base: "黑色标记笔";
    --theme-id: "某个故障版式";
    --theme-name: "某个故障版式";
    --header-title: "基金会 SITE-37";
    --header-subtitle: "我们有麻烦了";
    --body-font: "Punctuation", "Open Sans", "MiSans", "MiSans L3", "Noto Sans SC", Verdana, "Microsoft YaHei", "微软雅黑", Geneva, sans-serif, STXihei, "华文细黑";
    --UI-font: var(--title-font);
    --header-font: "IBM Plex Sans Condensed", "IBM Plex Sans SC", sans-serif;
    --title-font: "Podkova", "Hangeuljaemin", serif;
    --mono-font: "Nanum Gothic Coding", "ChillRoundF", monospace;
    }

    #header {
        --swatch-headerh1-color: var(--black);
        --swatch-headerh2-color: var(--black);
        --search-icon-color: var(--black);

        --login-line-divider-color: var(--black);
        --login-username-color: var(--black);
        --login-myaccount-color: var(--black);

        --login-dropdown-text-color: var(--black);
        --login-mobile-icon-color: var(--black);
    }

    #interwiki,
    #side-bar {
        --sidebar-media-bg-color: var(--chetwode);
        --sidebar-links-text: var(--white);
    }

    #main-content {
        --toc-body-link-color: var(--white);
        --toc-body-link-hover-color: var(--white);
        --toc-body-hover-arrow-color: var(--white);
    }
[[/code]]

@@ @@

[[code type="CSS"]]
/* 暗色版本 */

    :root {
        /* 页眉元素 */
        --logo-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Aa-problematic-theme/dark37.svg");

        /* 页眉渐变色 */
        --header-gradient-color-bottom: var(--gloomy-green);
        --header-gradient-color-middle: var(--gloomy-green);
        --header-gradient-color-top: var(--gloomy-green);

        /* 主题颜色 */
        --bright-accent: var(--laurel-green);
        --medium-accent: var(--gloomy-green);
        --dark-accent: var(--gloomy-green);
        --gray-monochrome: var(--army-green);
        --dark-gray-monochrome: var(--kombu-green);

        --swatch-background: var(--darkest);
        --background-gradient-color: var(--darkest);
        --rating-module-bg-color: var(--white);

        --swatch-text-light: var(--white);
        --swatch-text-general: var(--white);
        --link-color: var(--celadon);
        --hover-link-color: var(--gin);
        --visited-link-color: var(--laurel-green);

        /* 菜单颜色 */
        --swatch-menubg-color: var(--kombu-green);
    }

    #header {
        --swatch-headerh1-color: var(--white);
        --swatch-headerh2-color: var(--white);
        --search-icon-color: var(--white);

        --login-line-divider-color: var(--white);
        --login-username-color: var(--white);
        --login-myaccount-color: var(--white);

        --login-dropdown-text-color: var(--white);
        --login-mobile-icon-color: var(--white);
    }

    #interwiki,
    #side-bar {
        --sidebar-media-bg-color: var(--gin);
        --sideblock-heading-text-color: var(--gin);
    }

    #main-content {
        --tabs-content-bg-color: var(--yet-darker);
        --tabs-content-border-color: var(--darker);

        --blockquote-bg-color: var(--yet-darker);
        --blockquote-border-color: var(--darker);
        --blockquote-colorbar-color: var(--darker);

        --tables-border: var(--darker);
        --footnotes-footer-bg-color: var(--kombu-green);
    }

    .scp-image-block .scp-image-caption,
    .code {
    color: rgb(var(--black));
    }

    .page-source {
    background: rgb(var(--yet-darker));
    }

    form#edit-page-form .wd-editor-toolbar-panel a::before {
    background-color: rgb(var(--laurel-green));
    }
[[/code]]

