#header h1 a::before,
#header h2 span::before,
h1,
#page-title {
    font-family: 'Playfair Display SC', 'UnShinmun', serif;
}

#header h2 span::before {
    content: "서부에서 갓 전달된 경천동지할 이야기들!";
}
