/* ===修复火狐浏览器上的显示效果=== */

@-moz-document url-prefix() {
    #main-content::before {
        content: " ";
        border: 1px solid rgb(var(--white-monochrome));
        border-radius: 10px;
        position: absolute;
        width: 106%;
        height: calc(100% + 2rem);
        background: var(--background-image);
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-size: cover;
        background-position: center;
        display: block;
        filter: var(--main-aero-glass-filter);
        z-index: -6;
        left: calc(-3% - 1px);
        box-shadow: 0px 0px 8px 4px rgba(var(--dark-gray-monochrome), 0.8);
        top: -1rem;
    }

    #main-content::after {
        content: " ";
        border: 1px solid rgb(var(--white-monochrome));
        border-radius: 10px;
        position: absolute;
        width: 106%;
        height: calc(100% + 2rem);
        background: rgba(var(--gray-monochrome), 0.25);
        display: block;
        z-index: -6;
        left: calc(-3% - 1px);
        box-shadow: 0px 0px 8px 4px rgba(var(--dark-gray-monochrome), 0.8);
        top: -1rem;
    }

    @media only screen and (min-width: 768px) {
        #main-content::before {
            width: calc(100vw - 20px);
            height: 100%;
            top: 1rem;
            left: calc((100vw - 20px - 100%) / (-2) - 1px);
        }

        #main-content::after {
            width: calc(100vw - 20px);
            height: 100%;
            top: 1rem;
            left: calc((100vw - 20px - 100%) / (-2) - 1px);
        }
    }

    @media only screen and (min-width: 975px) {
        #main-content::before {
            width: 110%;
            left: calc(-5% - 1px);
            top: 1rem;
        }

        #main-content::after {
            width: 110%;
            left: calc(-5% - 1px);
            top: 1rem;
        }
    }

    @media only screen and (max-width: 768px) {
        #main-content::before {
            border: 1px solid rgba(var(--white-monochrome), 0.5);
            border-radius: 6px;
        }
    }
}

