    --bright-accent: 203, 219, 250; /* 修改此变量来改变链接颜色 */
    --pale-accent: 255, 75, 255; /* 修改此变量来改变鼠标移到链接上时的链接颜色 */
    --newpage-color: 238, 212, 255;  /* 修改此变量来改变指向不存在的页面的链接颜色 */

