     border-left: 0.0625rem solid rgb(var(--swatch-border-color));
    }

    #interwiki div.menu-item a,
    #interwiki  div.menu-item a:visited,
    #interwiki  div.menu-item .text,
    #side-bar div.menu-item a,
    #side-bar div.menu-item a:visited,
