[[module CSS show="true"]]
/* 글꼴 */
@import url('https://scpko.wdfiles.com/local--files/unfont/UnGraphic.css');
@font-face {
    font-family: 'MapoHongdaeFreedom';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2001@1.1/MapoHongdaeFreedomA.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@font-face {
    font-family: '설립체';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2112@1.0/establishRetrosansOTF.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

@supports((display: -ms-grid) or (display: grid)) {

    :root:lang(ko) {
        --header-title: 'SCP 재단';
        --header-subtitle: '확 보　격 리　보 호';

        /* Typefaces */
    --body-font: 'prenton', 'UnGraphic', serif;
    --header-font: 'flood-std', Arial, 'MapoHongdaeFreedom', sans-serif;
    --title-font: 'eurostile-extended', Arial, '설립체', sans-serif;
    --mono-font: 'novel-mono-pro', monospace;
    }
}

@media (max-width: 565px) {
#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: "확보 격리 보호";
}
}

textarea {
   font-family: var(--mono-font);
}
input#edit-page-title.text {
   font-family: var(--header-font);
}
