    --header-h3-height: 40px;
    --header-icon-width: 50px;
    --sidebar-width: 270px;
    --sidebar-icon-height: 200px;
    --sidebar-banner-height: 100px;
    --kurotsurubami-accent: 37, 40, 37;
    --sobakiri-accent: 200, 207, 200;
    --aonibi-accent: 85, 102, 85;
    --hakuji-accent: 252, 255, 252;
    --usumoegi-accent: 189, 224, 189;
    --header-font: 'Noto Serif JP', serif;
    ;
    --UI-font: 'Noto Serif JP', serif;
    ;
    --scrollbar-width: 9px;
}
@media (max-width: 767px) {
    :root {
        --scrollbar-width: 0px;
        --header-icon-width: 40px;
        --sidebar-width: 70vw;
        --header-h1-height: 35px;
        --header-h2-height: 20px;
        --header-h3-height: 35px;
    }
}
/* Links */
a {
    color: rgb(var(--usumoegi-accent));
}
a.newpage {
    color: rgb(var(--sobakiri-accent));
}
a:hover {
    text-decoration: underline;
    background-color: transparent;
}
#main-content a {
    transition: .3s;
}
/* Collapsibles */
#main-content a.collapsible-block-link {
    padding: 0.17em;
    color: rgb(var(--hakuji-accent));
    background: rgb(var(--aonibi-accent));
    border-radius: 3px;
}
#page-content .collapsible-block {
    margin: 0.5em auto;
}
#page-content .collapsible-block-folded .collapsible-block-link::before,
#page-content .collapsible-block-unfolded .collapsible-block-link::before {
    content: "▶ ";
    font-weight: bold;
    display: inline-block;
    margin-right: 0.25em;
    color: rgb(var(--sobakiri-accent));
}
#page-content .collapsible-block-folded .collapsible-block-link:hover::before,
#page-content .collapsible-block-unfolded .collapsible-block-link:hover::before,
#page-content .collapsible-block-folded .collapsible-block-link:focus::before,
#page-content .collapsible-block-unfolded .collapsible-block-link:focus::before {
    color: rgb(var(--sobakiri-accent));
}
#page-content .collapsible-block-unfolded .collapsible-block-link::before {
    animation-name: collapsible-arrow-spin;
    animation-duration: 0.15s;
    animation-iteration-count: 1;
    animation-timing-function: cubic-bezier(.32, .38, .39, .94);
    animation-fill-mode: forwards;
}
@keyframes collapsible-arrow-spin {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(90deg);
    }
}
div.page-rate-widget-box {
    box-shadow: none;
    border-bottom: 2px solid rgb(var(--sobakiri-accent));
    border-radius: 0;
}
.creditRate .rateBox {
    display: inline-flex;
    border-bottom: 2px solid rgb(var(--sobakiri-accent));
}
.creditRate .rateBox div.page-rate-widget-box {
    border-bottom: none;
}
div.page-rate-widget-box .rate-points {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: rgb(var(--hakuji-accent));
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: transparent;
    border: none;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: rgb(var(--hakuji-accent))
}
#page-content .page-rate-widget-box .rateup a:hover,
#page-content .page-rate-widget-box .ratedown a:hover {
    background: transparent;
    color: rgb(var(--usumoegi-accent));
}
.page-rate-widget-box .cancel {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: rgb(var(--hakuji-accent));
}
#page-content .page-rate-widget-box .cancel a {
    color: rgb(var(--hakuji-accent));
}
#page-content .page-rate-widget-box .cancel a:hover {
    color: rgb(var(--usumoegi-accent));
    background: transparent;
}
.modalbox .page-rate-widget-box {
    position: absolute;
    left: 50%;
    transform: translateX(-50%)
}
#page-content .rate-box-with-credit-button .creditButton {
    background-color: transparent;
    border: none;
    border-radius: 0;
    box-shadow: none;
}
a.fa.fa-info {
    color: rgb(var(--hakuji-accent))
}
a.fa.fa-info:hover {
    color: rgb(var(--usumoegi-accent))
}
#page-content .modalbox .modalbox-title {
    background: rgb(var(--kurotsurubami-accent));
    color: rgb(var(--hakuji-accent));
    border-radius: 0;
}
#page-content .modalbox {
    border-radius: 0;
    background-color: rgb(var(--kurotsurubami-accent));
    box-shadow: none;
}
.modalcontainer .cc-by-sa:before {
    color: rgb(var(--hakuji-accent));
}
/* Tag */
#main-content .page-tags a {
    line-height: inherit;
    background-color: rgb(var(--aonibi-accent));
    padding: 0.15em 0.75em;
    border-radius: 2px 6px;
    color: rgb(var(--hakuji-accent));
    font-family: 'Noto Serif JP', serif;
    font-weight: 500;
}
.page-tags span {
    border-top: none;
}
/* ==== header ==== */
#header {
    display: grid;
    grid-template-rows: var(--header-h1-height) var(--header-h2-height) var(--header-h3-height);
    grid-template-columns: var(--sidebar-width) 1fr var(--header-icon-width) var(--header-icon-width);
    grid-template-areas:
        ". header_h1 search login"
        ". header_h2 search login"
        ". topbar topbar topbar";
    height: unset;
}
#header h1, #header h2 {
    font-family: var(--header-font);
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
}
#header h1 {
    grid-area: header_h1;
    line-height: var(--header-h1-height);
    padding-right: calc(var(--scrollbar-width) + var(--sidebar-width) - var(--header-icon-width) * 2);
    top: 5%;
}
#header h1 a {
    font-size: 0;
    line-height: 0;
}
#header h1 a:before {
    content: var(--header-title, '青鈍色の空');
    display: block;
    width: 100%;
    height: 100%;
    font-size: calc(18px + (43 - 24) * ((100vw - 300px) / (1600 - 300)));
    line-height: calc(20px + (43 - 24) * ((100vw - 300px) / (1600 - 300)));
    color: rgb(var(--hakuji-accent));
    font-weight: 900;
    position: relative;
    top: 10px;
}
#header h2 {
    grid-area: header_h2;
    line-height: var(--header-h2-height);
    padding-right: calc(var(--scrollbar-width) + var(--sidebar-width) - var(--header-icon-width) * 2);
    top: 5%;
}
#header h2 span {
    display: none;
}
#header h2:before {
    content: var(--header-subtitle, '気の沈む天気。');
    display: block;
    width: 100%;
    height: 100%;
    font-size: calc(10px + (20 - 12) * ((100vw - 300px) / (1600 - 300)));
    line-height: calc(12px + (20 - 12) * ((100vw - 300px) / (1600 - 300)));
    color: rgb(var(--hakuji-accent), .75);
    font-weight: 800;
    position: relative;
    top: 3px;
}
.header-link {
    display: none;
}
@media (min-width: 768px) {
    #side-bar {
        padding-top: var(--sidebar-banner-height);
    }
    #side-bar:before {
        position: fixed;
        top: 0;
        left: 0;
        z-index: 33;
        width: var(--sidebar-width);
        height: var(--sidebar-banner-height);
        background-color: rgb(var(--kurotsurubami-accent));
    }
    .header-link {
        display: block;
        position: fixed;
        top: 0;
        left: 0;
        z-index: 33;
        width: var(--sidebar-width);
        height: calc(var(--sidebar-banner-height) + 1px);
        font-size: 0;
    }
}
#search-top-box {
    grid-area: search;
    inset: 0;
    position: relative;
}
#search-top-box-form input.button:hover {
    cursor: pointer;
}
#search-top-box:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 100%;
    inset: 0;
    top: -100%;
    background: rgb(var(--aonibi-accent));
    z-index: -2;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#search-top-box:hover:before {
    top: 0%;
}
#search-top-box:after {
    content: '\f002';
    position: absolute;
    display: block;
    width: 100%;
    height: 100%;
    inset: 0;
    color: rgb(var(--hakuji-accent));
    font-family: 'FontAwesome';
    font-size: 15px;
    line-height: calc(var(--header-h1-height) + var(--header-h2-height));
    text-align: center;
    z-index: -1;
}
#search-top-box:hover:after {
    color: rgb(var(--hakuji-accent));
}
#search-top-box-input {
    display: none;
}
#search-top-box-form input.button {
    padding: 0;
    margin: 0;
    border: none;
    background: none;
    font-size: 0;
    width: var(--header-icon-width);
    height: calc(var(--header-h1-height) + var(--header-h2-height));
}
#top-bar {
    grid-area: topbar;
    right: 0;
    font-family: var(--UI-font);
}
#login-status {
    grid-area: login;
    width: 100%;
    height: 100%;
    inset: 0;
    position: relative;
}
#login-status {
    font-size: 0;
}
.printuser a {
    margin: unset;
    font-family: 'Noto Serif JP', serif;
}
#login-status .printuser img.small {
    background: none !important;
    width: var(--header-icon-width);
    height: calc(var(--header-h1-height) + var(--header-h2-height));
    padding: calc((var(--header-h1-height) + var(--header-h2-height) - var(--header-icon-width) * 0.8) / 2) calc(var(--header-icon-width) / 10);
    vertical-align: unset;
    box-sizing: border-box;
    margin: unset;
    filter: grayscale();
}
#login-status:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 100%;
    inset: 0;
    top: -100%;
    background: rgb(var(--aonibi-accent));
    z-index: -2;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#login-status:hover:before {
    top: 0%;
}
#login-status:has(.login-status-create-account) {
    display: grid;
    width: var(--header-icon-width);
    grid-template-columns: 150px 0 150px;
    grid-template-rows: 45px;
    grid-template-areas: 'create . signin';
    justify-content: end;
    align-content: end;
}
#login-status:has(.login-status-create-account):after {
    content: '\f090';
    position: absolute;
    width: 100%;
    height: 100%;
    inset: 0;
    font-family: 'FontAwesome';
    font-size: 17px;
    text-align: center;
    color: rgb(var(--hakuji-accent));
    cursor: pointer;
    display: grid;
    place-items: center;
}
#login-status:has(.login-status-create-account):hover:after {
    color: rgb(var(--hakuji-accent));
}
#login-status:has(.login-status-create-account) .login-status-create-account, #login-status:has(.login-status-create-account) .login-status-sign-in {
    font-size: 13px;
    font-family: var(--UI-font);
    font-weight: bold;
    text-decoration: none;
    color: rgb(var(--hakuji-accent));
    width: 100%;
    height: 100%;
    text-align: center;
    position: relative;
    top: 45px;
    display: grid;
    place-items: center;
    max-height: 0px;
    overflow: hidden;
}
#login-status:has(.login-status-create-account):hover a {
    max-height: 100vh;
}
#login-status:has(.login-status-create-account) .login-status-create-account:before, #login-status:has(.login-status-create-account) .login-status-sign-in:before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    inset: 0;
    background: rgb(var(--aonibi-accent));
    z-index: -2;
}
#login-status:has(.login-status-create-account) .login-status-create-account:after, #login-status:has(.login-status-create-account) .login-status-sign-in:after {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--kurotsurubami-accent));
    z-index: -2;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#login-status:has(.login-status-create-account) .login-status-create-account:hover, #login-status:has(.login-status-create-account) .login-status-sign-in:hover {
    color: rgb(var(--hakuji-accent));
}
#login-status:has(.login-status-create-account) .login-status-create-account:hover:after, #login-status:has(.login-status-create-account) .login-status-sign-in:hover:after {
    height: 100%;
}
#login-status:has(.login-status-create-account) .login-status-create-account {
    grid-area: create;
}
#login-status:has(.login-status-create-account) .login-status-sign-in {
    grid-area: signin;
}
#account-topbutton {
    display: none;
}
#account-options {
    display: block !important;
    width: fit-content;
    max-height: 0px;
    overflow: hidden;
    transition: all .5s cubic-bezier(.5, .0, .5, 1);
    border: none;
    background: rgb(var(--aonibi-accent));
}
#login-status:hover #account-options, #account-options:hover {
    max-height: 100vh;
}
#account-options ul {
    display: grid;
    grid-template-columns: repeat(2, minmax(120px, 1fr));
}
#account-options li a {
    position: relative;
    font-size: 12px;
    font-family: var(--UI-font);
    color: rgb(var(--hakuji-accent));
    text-decoration: none;
    text-align: center;
    padding: 12px;
    line-height: normal;
}
#account-options li a:hover {
    color: rgb(var(--hakuji-accent));
}
#top-bar>*:not(.top-bar):not(.mobile-top-bar) {
    display: none;
}
#top-bar ul li a {
    position: relative;
    color: rgb(var(--hakuji-accent));
    font-weight: 600;
    line-height: var(--header-h3-height);
    padding: 0 12px;
    text-decoration: none;
}
#account-options li a:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--kurotsurubami-accent));
    z-index: -2;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#account-options li a:hover:before {
    height: 100%;
}
#top-bar {
    grid-area: topbar;
    right: 0;
}
#top-bar li {
    margin: 0;
}
#top-bar ul li {
    position: static;
}
#top-bar li a:hover {
    background: none;
    color: rgb(var(--hakuji-accent));
}
#top-bar li:hover ul, #top-bar li.sfhover ul {
    position: absolute;
    width: calc(100vw - var(--sidebar-width) - var(--scrollbar-width));
    right: 0;
    background: rgb(var(--aonibi-accent));
    z-index: -2;
    max-width: 700px;
}
#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
    background: unset;
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    width: 100%;
    box-sizing: border-box;
    color: rgb(var(--hakuji-accent));
    text-align: end;
    line-height: var(--header-h3-height);
    padding: 0 12px;
}
#top-bar li ul li a:hover {
    color: rgb(var(--hakuji-accent));
}
.top-bar ul li a:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--aonibi-accent));
    z-index: -1;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
.top-bar ul li a:hover:before {
    height: 100%;
}
.top-bar ul li ul li a:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--kurotsurubami-accent));
    z-index: -1;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
.top-bar ul li ul li a:before {
    height: 0%;
}
.open-menu {
    display: none;
}
.mobile-top-bar {
    max-width: unset;
    left: unset;
    right: 0;
    width: max-content;
}
.mobile-top-bar ul li a:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--aonibi-accent));
    z-index: -1;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
.mobile-top-bar ul li a:hover:before {
    height: 100%;
}
#top-bar .mobile-top-bar ul li ul {
    width: 100vw;
    max-width: unset;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    right: 0;
}
.mobile-top-bar ul li ul li a {
    --header-h3-height: 30px;
    font-size: 11px;
}
.mobile-top-bar ul {
    position: relative;
}
.mobile-top-bar ul li ul li a:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--kurotsurubami-accent));
    z-index: -1;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
.mobile-top-bar ul li ul li a:hover:before {
    height: 100%;
}
/* ==== content-wrap ==== */
::-webkit-scrollbar {
    width: var(--scrollbar-width);
}
::-webkit-scrollbar-thumb {
    background: rgb(var(--sobakiri-accent));
}
body {
    background: rgb(var(--kurotsurubami-accent));
}
#main-content {
    min-height: calc(1000px + 20vh - var(--header-h1-height) - var(--header-h2-height) - var(--header-h3-height));
    max-width: 1040px;
    background: rgb(var(--kurotsurubami-accent));
    border-radius: 25px 0 0 0;
    color: rgb(var(--hakuji-accent));
    font-family: var(--UI-font);
    box-sizing: border-box;
    margin: 0 min(5%, 17vw);
    align-items: center;
    justify-content: center;
}
#main-content:before {
    content: '';
    width: 100%;
    height: calc(100%);
    position: absolute;
    inset: 0;
    border-radius: 25px 0 0 0;
    pointer-events: none;
}
#content-wrap {
    margin: unset;
    margin-left: var(--sidebar-width);
    max-width: unset;
    min-height: unset;
}
@media (min-width: 768px) {
    #content-wrap {
        top: -1.5em;
    }
}
@media (max-width: 767px) {
    #content-wrap {
        margin: 0;
    }
    #main-content {
        width: 100%;
        border-radius: 0;
        max-width: 90%;
        margin: 0 5%;
        padding: 0;
    }
    #main-content:before {
        border-radius: 0;
    }
    #header {
        grid-template-areas:
            "header_h1 header_h1 search login"
            "header_h2 header_h2 search login"
            "topbar topbar topbar topbar";
        grid-template-columns: 1fr var(--header-icon-width) var(--header-icon-width);
    }
    #header h1, #header h2 {
        padding-right: 0;
        text-align: start;
        padding-left: calc(var(--header-icon-width) + 10px);
    }
}
#side-bar,
#side-bar:target {
    position: fixed;
    left: 0;
    width: var(--sidebar-width);
    height: 100vh;
    overflow: auto;
    top: 0;
    z-index: 33;
    transition: all .3s cubic-bezier(0.3, 1, 0.3, 1);
    background: rgb(var(--kurotsurubami-accent));
    box-sizing: border-box;
    border: none;
    padding: unset;
    font-family: var(--UI-font);
}
#side-bar:before {
    content: var(--sidebar-banner-title, '');
    position: relative;
    display: block;
    width: 100%;
    height: var(--sidebar-banner-height);
    background: var(--sidebar-icon) center 20px no-repeat;
    background-size: var(--sidebar-icon-height);
    text-align: center;
    line-height: calc(var(--sidebar-banner-height) + 60px);
    font-size: 15px;
    font-family: var(--header-font);
    padding-bottom: 1px;
    text-decoration: underline 2px;
    text-underline-offset: 3px;
}
#side-bar:target {
    left: 0;
}
#side-bar:target .close-menu {
    opacity: 0;
    visibility: hidden;
    display: block;
    position: fixed;
    width: calc(100% - var(--sidebar-width));
    height: 100vh;
    left: var(--sidebar-width);
    top: 0;
    z-index: -1;
    background: linear-gradient(90deg, rgba(var(--aonibi-accent), .6), transparent);
    transition: all .3s cubic-bezier(0.3, 1, 0.3, 1);
    opacity: 1;
    visibility: visible;
}
#side-bar::-webkit-scrollbar {
    width: 8px;
    border: none;
}
#side-bar::-webkit-scrollbar-thumb {
    background: rgb(var(--sobakiri-accent));
}
#side-bar .side-block, #interwiki .side-block {
    border: none;
    box-shadow: none;
    background: none !important;
    padding: 0;
}
#side-bar div.menu-item, #interwiki .menu-item {
    margin: 0;
    position: relative;
}
#side-bar div.menu-item a, #interwiki .menu-item a {
    color: rgb(var(--hakuji-accent));
    text-decoration: none;
    display: inline-block;
    width: 100%;
    padding: 5px 10px;
    box-sizing: border-box;
    font-size: 12px;
    position: relative;
}
#side-bar .side-block:not(.sns) div.menu-item {
    font-size: 0;
}
#side-bar div.menu-item a:hover, #interwiki .menu-item a:hover {
    color: rgb(var(--hakuji-accent));
}
#side-bar div.menu-item a:before, #interwiki .menu-item:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--aonibi-accent));
    z-index: -1;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#side-bar div.menu-item a:hover:before, #interwiki .menu-item:hover:before {
    height: 100%;
}
#side-bar .heading, #interwiki .heading {
    color: rgb(var(--hakuji-accent));
    border: unset;
    background: rgb(var(--aonibi-accent));
    margin: 0;
    padding: 4px 10px;
}
.side-block:not(.sns) img {
    display: none;
}
#side-bar div.menu-item .sub-text {
    font-size: 10px;
    font-weight: 600;
    font-family: var(--UI-font);
    color: rgb(var(--hakuji-accent));
    position: absolute;
    right: 0;
    height: 100%;
    display: inline-flex;
    place-items: center;
    pointer-events: none;
    padding-right: 10px;
}
#side-bar div.menu-item:hover .sub-text {
    color: rgb(var(--hakuji-accent));
}
#side-bar hr {
    display: none;
}
#side-bar .collapsible-block-link {
    display: inline-block;
    margin: 0;
    padding: 5px 0 5px 15px;
    width: 100%;
    box-sizing: border-box;
    background: rgb(var(--aonibi-accent));
    color: rgb(var(--hakuji-accent));
    text-decoration: none;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#side-bar .collapsible-block-folded {
    background: none;
}
#side-bar .collapsible-block-folded:hover .collapsible-block-link {
    color: rgb(var(--usumoegi-accent));
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    display: inline-block;
    margin: 0;
    padding: 5px 0 5px 15px;
    width: 100%;
    box-sizing: border-box;
    background: rgb(var(--aonibi-accent));
    color: rgb(var(--hakuji-accent));
    text-decoration: none;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
#side-bar .collapsible-block-unfolded-link:hover .collapsible-block-link {
    color: rgb(var(--usumoegi-accent));
}
#side-bar .collapsible-block-unfolded-link {
    border: none;
}
/* ==== extra ==== */
/* TITLE */
#page-title {
    color: rgb(var(--hakuji-accent));
    font-family: 'Noto Serif JP', serif;
    font-size: 220%;
    transition: 0.5s;
    border-bottom: 2px solid rgb(var(--aonibi-accent));
}
#page-content {
