.side-nav {
    position: relative;
    top: 0;
    left: 0;
    width: 100%;
}
.side-nav .yui-nav {
    position: absolute;
    display: grid;
    left: calc(var(--sidebar-width) * -1);
    top: 0;
    border: none;
    width: var(--sidebar-width);
    height: calc(1000px + 40vh - var(--header-h1-height) - var(--header-h2-height) - var(--header-h3-height));
    grid-template-rows: repeat(auto-fill, minmax(50px, 1fr));
    overflow: auto;
}
.side-nav .yui-navset .yui-nav .selected, .yui-navset .yui-navset-top .yui-nav .selected {
    margin: unset;
}
.side-nav .yui-navset .yui-nav li, .yui-navset .yui-navset-top .yui-nav li {
    margin: unset;
    padding: unset;
}
.side-nav .yui-navset .yui-nav a em {
    border: none;
    padding: 0;
    inset: 0;
    font-size: 20px;
    font-family: var(--UI-font);
    color: rgb(var(--hakuji-accent));
    line-height: 20px;
}
.side-nav .yui-navset .yui-nav a,
.side-nav .yui-navset .yui-nav li.selected a,
.side-nav .yui-navset .yui-nav li.selected a:hover {
    background: none;
    border: none;
    width: var(--sidebar-width);
}
.side-nav .yui-navset .yui-nav .selected a em {
    padding: unset;
}
.side-nav .yui-navset .yui-nav li {
    height: 100%;
    display: grid;
    place-items: center;
    text-align: center;
    position: relative;
}
.side-nav .yui-navset .yui-nav li:before {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 0%;
    inset: 0;
    background: rgb(var(--aonibi-accent));
    z-index: 0;
    transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
}
.side-nav .yui-navset .yui-nav li:hover:before {
    height: 100%;
}
.side-nav .yui-navset .yui-nav li:hover em {
    color: rgb(var(--hakuji-accent));
}
.side-nav .yui-navset .yui-content {
    background: none;
    border: none;
    padding: unset;
}
@media (max-width: 767px) {
    .side-nav {
        z-index: 30;
    }
    .side-nav .yui-nav {
        position: fixed;
        inset: unset;
        right: 0;
        bottom: 0;
        width: 70vw;
        margin-bottom: 110px;
        margin-right: 30px;
        z-index: 1;
        overflow: hidden;
        pointer-events: auto;
        height: 0;
        transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
    }
    .side-nav .yui-nav:hover {
        height: 70vh;
        overflow: auto;
    }
    .side-nav .yui-nav:after {
        content: '\f103';
        font-family: 'FontAwesome';
        font-size: 40px;
        position: fixed;
        bottom: 0;
        right: 0;
        margin-bottom: 30px;
        margin-right: 30px;
        width: 50px;
        height: 50px;
        display: grid;
        place-items: center;
        background: rgb(var(--aonibi-accent));
        color: rgb(var(--hakuji-accent));
        border-radius: 50%;
        box-shadow: 0 0 10px rgb(var(--aonibi-accent));
        z-index: 3;
        pointer-events: auto;
        transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
    }
    .side-nav .yui-nav:hover:after {
        color: rgb(var(--link-color));
        transform: rotate(180deg);
    }
    .side-nav .yui-nav:hover:before {
        content: '';
        position: fixed;
        width: 100%;
        height: 100vh;
        inset: 0;
        z-index: -1;
        pointer-events: none;
        background: rgb(var(--aonibi-accent), .75);
        backdrop-filter: blur(3px);
    }
    .side-nav .yui-nav li:first-child:after {
        content: '';
        position: fixed;
        right: 0;
        bottom: 0;
        width: 70vw;
        height: 0;
        margin-bottom: 110px;
        margin-right: 30px;
        z-index: -1;
        background: rgb(var(--kurotsurubami-accent), .5);
        border-radius: 15px;
        box-shadow: 0 0 15px rgb(var(--aonibi-accent), .25);
        transition: all .3s cubic-bezier(0.1, 0.9, 0.1, 0.9);
    }
    .side-nav .yui-nav:hover li:first-child:after {
        height: 70vh;
    }
    .side-nav .yui-navset .yui-nav li:first-child:hover:before {
        border-radius: 15px 15px 0 0;
    }
}
