[[module CSS]]
/* 青鈍色の空CSSテーマ by KanKan, based on AAPA New CSS Theme by NatVoltaic and stormbreath (Edited for SCP財団 by SCP-JP Technical Staff(Dr Devan)), based on code by Cyantreuse and InitHello */
/* Licensed as CC-by-SA-3.0 */
/* Header image is publicdomain, having been taken by https://www.publicdomainpictures.net/en/view-image.php?image=6494&picture=minneapolis. Header icon created by Woedenaz. */

@import url('https://fonts.googleapis.com/css?family=M+PLUS+1p:400,800');
@import url('https://fonts.googleapis.com/css?family=Noto+Serif+SC&display=swap');

/* COLOR ROOT */

:root {
    --1-color: #556655;
    --2-color: #252825;
    --3-color: #fff;
    --4-color: #ccc;
    --5-color: #a9cca9;
}

/* HEADER */
div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: url("http://scp-jp-sandbox3.wikidot.com/local--files/draft:2859452-134-5016/buildings.jpg") /*var(--2-color)*/;
    background-size: cover;
    background-position: 50% 50%;
} /* SPACE */

div#container-wrap {
    background-image: none;
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: var(--1-color);
} /* Red-top bar */

#header {
    background: url("http://scp-wiki.wdfiles.com/local--files/component%3Aad-astra-theme/logo.svg") 15px 40px no-repeat;
    background-size: 6rem;
}
@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em;
        background-size: 66px /*66px*/;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em;
        background-size: 55px /*55px*/;
    }
}

#header h1 a::before {
    content: "青钝色的天空";
    font-size: 80%;
    font-family: 'Noto Serif SC', serif;
    font-weight: 800;
    /*text-shadow: 4px 4px 4px rgba(0,0,0,.8);*/
}

#header h1 a span{
    display: block;
    visibility: hidden;
}

#header h2 span {
    display: block;
    margin: 0;
    margin-left: 0px;
    padding: 19px 0;
    font-weight: bold;
    color: transparent;
    text-shadow: 0px 0px 0px;
}

#header h2 span::before {
    content: "心情沉闷的天气";
    color: var(--1-color);
    font-family: 'Noto Serif SC', serif;
    font-weight: 700;
    font-size: 120%;
    /*text-shadow: 3px 3px 3px rgba(0,0,0,.9);*/
}

/* Log-In Info */
#login-status {
    color: var(--1-color);
}
#login-status a {
    background: transparent;
    color: var(--1-color);
}

#login-status ul a {
    color: var(--1-color);
    background: var(--2-color);
    border-color: var(--1-color);
}

#login-status ul a:hover {
   color: var(--1-color);
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: var(--1-color);
    border: none;
}

#account-options {
   border: 1px solid var(--1-color);
}

/* Search Bar */
#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px var(--1-color);
    background-color: var(--2-color);
}

#search-top-box-input {
    color: var(--3-color) !important;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    color: var(--3-color) !important;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px var(--1-color);
    background: var(--2-color);
    color: var(--5-color);
}

/* TITLE */

#page-title {
    color: var(--3-color);
    font-family: 'Noto Serif SC', serif;
    font-weight: 900;
}

#page-title {
    border-color: var(--1-color);
}

/* TOP-BAR SETTINGS */
#top-bar a {
    color: var(--3-color)
}

#top-bar ul li ul {
    border: solid 1px var(--1-color);
    border-radius: 0px 0px 10px 10px;
    overflow: hidden;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
   border-top-color: var(--1-color);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--1-color);
    color: var(--3-color);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--2-color); /* top bar hover background color */
    color: var(--4-color);
}

/* SIDEBAR SETTINGS */
#side-bar .side-block {
    border: 1px solid var(--1-color);
    box-shadow: none;
    background-color: var(--2-color) !important;
    border-radius: 0;
    color: var(--4-color);
}

#side-bar .heading {
    border-bottom: 1px solid var(--1-color);
    color: var(--4-color);
}

/* Side-Bar Images */
.side-block .menu-item > .image {
    filter:hue-rotate(60deg) brightness(400%);
}

/* Mobile Sidebar */
@media (max-width: 767px) {
    #side-bar {
        background-color: var(--2-color) !important;
    }

    .open-menu a {
        border: 0.2em solid var(--1-color) !important;
        background-color: var(--2-color) !important;
        color: var(--4-color) !important;
    }
}

/* BODY */
body {
    background-color: var(--2-color);
    color: var(--4-color);
    font-family: 'Noto Serif SC', serif;
}

/* FOOTER */
#footer {
    background: var(--2-color);
    color: var(--3-color);
}

#footer a {
    color: var(--5-color);
}

#license-area {
     color: var(--4-color);
}

/* OTHER STRUCTURAL ELEMENTS */

/* Links */
a {
    color: var(--5-color);
}

a.newpage {
    color: var(--5-color);
}

#side-bar a:visited {
    color: var(--5-color);
}

 a:visited {
     color: var(--5-color);
}

/* Footnotes */
.hovertip {
     background-color: var(--2-color) !important;
     border: 1px solid var(--1-color) !important;
}

.footnotes-footer .title {
    color: var(--3-color);
}

/* Scrollbar */
::-webkit-scrollbar-thumb {
    background: var(--2-color);
}

/* Pop-Up Windows */
.owindow {
    background-color: var(--2-color);
    border: 1px solid var(--1-color);
}

.owindow .modal-header {
    background-color: var(--2-color);
}

.owindow .modal-body img {
    background-color: transparent !important;
}

.owindow .button-bar a {
    background-color: var(--2-color);
    border: 1px solid var(--1-color);
}

.owindow .button-bar a:hover {
    background-color: #26152e;
}

/* Edit Buttons */
.buttons .btn {
    background-color: var(--2-color);
    border: 1px solid var(--1-color);
    color: var(--3-color);
    padding: 3px 5px;
}

.buttons .btn:hover {
    background-color: #333333;
    color: var(--4-color);
}

/* Edit Lock Info*/
#lock-info {
    background-color: var(--2-color);
    border: 1px solid var(--1-color);
}

/* Code */
.code {
    border: 1px dashed #666;
    background-color: transparent;

    font-family: "Consolas", "Courier New", "Roboto Mono", monospace;
    font-size: 14px;
}

/* code highlighter stuff */

.hl-identifier, .hl-code, .hl-brackets {
    color: var(--4-color);
}

.hl-var {
    color: #21ff00;
}

.hl-special {
    color: #ffff00;
}

.hl-reserved {
    color: #00b2ff;
}

.hl-number, .hl-string {
    color: #ff0020;
}

del {
    color: var(--4-color);
}
ins {
    color: var(--4-color);   
}

/* RATING AND INFO MODULES */
.page-rate-widget-box {
     box-shadow: 0px 0px 0px 2px var(--1-color);
     background-color: var(--2-color);
     padding: .1em;
    display: inline-flex;
    align-items: center;
}

.page-rate-widget-box .rate-points {
    background-color: var(--2-color) !important;
    border: none;
    color: var(--1-color) !important;
    text-transform: capitalize;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: var(--2-color);
    border-top: none;
    border-bottom: none;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: var(--5-color);
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: var(--1-color);
    color: var(--2-color);
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: var(--2-color);
    border: none;
}
.page-rate-widget-box .cancel a {
    color: var(--5-color);
}
.page-rate-widget-box .cancel a:hover {
    background: var(--1-color);
    color: var(--2-color);
}

/* Credit Module */
#page-content .creditRate {
    margin: 0 0 10px 0;
}

#page-content .rate-box-with-credit-button .creditButton {
    box-shadow: 0px 0px 0px 2px var(--1-color);
    background-color: var(--2-color);
    padding: .1em;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: var(--1-color);
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
     border-color: var(--1-color) ;
     color: var(--1-color) ;
}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: white;
}

#page-content .modalbox {
     background-color: var(--2-color) !important;
     color: var(--4-color);
     box-shadow: 0px 0px 0px 2px var(--1-color);
}

#page-content .modalbox .modalbox-title {
    background-color: var(--2-color);
}

.close-credits,
.credit-back {
    filter: hue-rotate(60deg) brightness(400%);
}

.credit-license a.cc {
    color: #fff;
}

/* Ayer's Info Module */
:root {
    --barColour: var(--2-color);
    --linkColour: var(--1-color);
}

.info-container .collapsible-block-unfolded {
    border-color: var(--1-color);
}

/* PAGE ELEMENTS */
blockquote{
    margin: 1em 0;
    border: 1px solid var(--1-color);
    background-color: var(--2-color);
}

.scp-image-block {
    border-color: var(--1-color);
}

.scp-image-block .scp-image-caption {
    background-color: var(--2-color);
    border-color: var(--1-color);
}

.wiki-content-table tr th {
    background: var(--2-color);
    color: var(--3-color);
    border-color: var(--1-color);
}

h1, h2, h3, h4, h5, h6 {
    color: var(--3-color);
    font-family: 'Noto Serif SC', serif;
}

strong {
    color: var(--3-color);
}

hr {
    background-color: var(--1-color);
}

/* TABVIEW - ここから入らんとする者はすべて希望を捨てよ */

.yui-navset * {
    -webkit-transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    zoom: unset;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    border-color: var(--1-color);
    width: -webkit-calc(100% - 0.125rem);
    width: calc(100% - 0.125rem);
    margin: 0 auto;
    border: 0;
    -webkit-box-shadow: 0 -webkit-calc(0.0625rem * 5) 0 0 var(--1-color);
    box-shadow: 0 calc(0.0625rem * 5) 0 0 var(--1-color);
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    zoom: unset;
    background-image: none;
    background: var(--4-color);
    border: unset;
    color: var(--5-color);
    font-weight: bold;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    zoom: unset;
    background-image: none;
    background: var(--2-color);
    font-weight: bold;
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    background: var(--4-color);
    border-color: transparent;
    max-width: 100%;
    padding: 0;
    margin: 0;
    zoom: unset;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    position: relative;
    -webkit-flex-basis: 7vw;
    -ms-flex-preferred-size: 7vw;
    flex-basis: 7vw;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    -webkit-box-shadow: 0 0 0 0.0625rem var(--1-color);
    box-shadow: 0 0 0 0.0625rem var(--1-color);
}

.yui-navset .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset-bottom .yui-nav li a {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    vertical-align: bottom;
    zoom: unset;
    width: 100%;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    padding: 0.35em 0.75em;
    white-space: nowrap;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    overflow: hidden;
}

.yui-navset .yui-nav li em {
    /* Undoes border set by wikidot's default layout */
    border: unset !important;
}

/* Selected Tab */

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected,
.yui-navset .yui-nav .selected:hover,
.yui-navset .yui-navset-top .yui-nav .selected:hover {
    padding: 0;
    margin: 0;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    color: var(--5-color);
    background-image: none;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border: none !important;
    border-color: transparent;
    color: var(--5-color);
}

.yui-navset .yui-nav .selected a {
    /* Undoes a color set by wikidot's default layout */
    color: rgb(252, 252, 252) !important;
    width: 100%;
    background-image: none;
}

.yui-navset .yui-content {
    background-color: var(--2-color);
    /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: var(--1-color);
    /* content border */
}

.yui-navset-left .yui-content {
    border-left-color: var(--1-color);
    /* different border color */
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
        background-image: none;
        background-color: var(--2-color);
        color: var(--5-color);
        cursor: default;
        margin-bottom: -2px;
        font-weight: bold;
}

/* Interwiki */
#side-bar .scpnet-interwiki-wrapper iframe.scpnet-interwiki-frame {
    border: 2px solid #b01;
    box-shadow: none;
    background-color: hsl(102, 24%, 82%) !important;
    filter: hue-rotate(220deg) saturate(122%) invert(1) contrast(83%);
}
[[/module]]
