[[collapsible]]
[[code type="css"]]
@import url('https://fonts.googleapis.com/css2?family=Teko:wght@300..700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Rokkitt:ital,wght@0,100..900;1,100..900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+Buginese&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Sometype+Mono:ital,wght@0,400..700;1,400..700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Dosis:wght@200..800&display=swap');

    :root {
        /* S-CSS-P INTEGRATION */
        --theme-base: "black-highlighter";
        --theme-id: "bedbugs-theme";
        --theme-name: "Bedbugs Theme";

        /* Colors */
        --ent-red: 228, 0, 43;
        --ent-red-transparent: 247, 224, 228;

        --ent-blue: 1, 32, 105;
        --sky-blue: 173, 216, 230;
        --dark-sapphire: 8, 37, 103;
        --air-force: 0, 79, 152;

        --castleton-green: 0, 86, 59;
        --paris-green: 80, 200, 120;
        --bottle-green: 0, 106, 78;

        /* Fonts */
        --header-font: "Teko", sans-serif;
        --title-font: "Rokkitt", serif;
        --body-font: "Noto Sans Buginese", sans-serif;
        --mono-font: "Sometype Mono", monospace;
        --UI-font: "Dosis", sans-serif;

        /* Header Elements */
        --logo-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Abedbugs-theme/entlogo.png");
        --header-title: "Department of Entomology";
        --header-subtitle: "Odd Is To Be Expected";
        --header-h2-font-size: 1.2rem;

        /* Header Gradient */
        --header-gradient-color-bottom: var(--air-force);
        --header-gradient-color-middle: var(--ent-blue);
        --header-gradient-color-top: var(--ent-blue);
        --diagonal-stripes: unset;

        /* Theme Colors */
        --bright-accent: var(--ent-red);
        --medium-accent: var(--ent-red);
        --dark-accent: var(--dark-sapphire);
        --gray-monochrome: var(--dark-sapphire);
        --dark-gray-monochrome: var(--dark-sapphire);

        --link-color: var(--bottle-green);
        --visited-link-color: var(--castleton-green);
        --hover-link-color: var(--paris-green);

        --swatch-menubg-color: var(--ent-red-transparent);
        --code-color: var(--sky-blue), 0.20;
    }

    #page-title,
    .meta-title {
        color: rgb(var(--bottle-green));
    }

    #main-content {
        --blockquote-bg-color: var(--sky-blue), 0.20;
        --rating-module-bg-color: var(--ent-red), 0.05;
        --tabs-bg: var(--ent-red), 0.05;

        --toc-body-link-color: 0, 0, 0;
        --toc-body-link-hover-color: 0, 0, 0;
        --toc-body-hover-arrow-color: 0, 0, 0;
        --toc-body-bg-color: var(--sky-blue), 0.20;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        color: rgb(var(--bottle-green));
    }

    .code {
        background-color: rgba(var(--code-color));
    }

    .redstain {
        --blockquote-bg-color: var(--ent-red), 0.05;
        --code-color: var(--ent-red), 0.05;
        --tables-header-bg: var(--ent-red);
    }

    .redblock {
        background: rgb(252, 252, 252);
        border: rgb(var(--ent-red)) solid;
        border-radius:10px;
        padding: 1px 15px;
        margin: .5em 1.25rem;
    }

    .blueblock {
        background: rgb(252, 252, 252);
        border: rgb(var(--ent-blue)) solid;
        border-radius:10px;
        padding: 1px 15px;
        margin: .5em 1.25rem;
    }
[[/code]]
