/* code highlighter stuff */

.hl-identifier,
.hl-code,
.hl-brackets {
    color: rgb(102, 102, 102);
}

.hl-var {
    color: rgb(33, 255, 0);
}

.hl-special {
    color: rgb(255, 255, 0);
