/* Black Highlightyear | To The Stars */
/* 2019 Wikidot Theme */
/* By Rounderhouse, based off Ad Astra theme by Stormbreath and NatVoltaic*/
/* Logo licensed under CC BY SA 3.0 */
/* Header background by NASA, public domain. */

/* Fonts */
@import url('https://fonts.googleapis.com/css?family=Noto+Sans+KR');
@font-face {
     font-family: 'DungGeunMo';
     src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_six@1.2/DungGeunMo.woff') format('woff');
     font-weight: normal;
     font-style: normal;
}
@font-face {
    font-family: 'GmarketSansBold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2001@1.1/GmarketSansBold.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@font-face {
     font-family: 'NIXGONM-Vb';
     src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_six@1.2/NIXGONM-Vb.woff') format('woff');
     font-weight: normal;
     font-style: normal;
}

/* All Vars Used */

:root:lang(ko) {

     /* Header */
    --logo-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Aad-astra/logo.svg");
    --header-title: "Ad Astra Per Aspera";
    --header-subtitle: "별을 향해서 역경 헤치며";

    /* Typefaces */
    --body-font: 'Lato', sans-serif;
    --header-font: 'Russo One', 'GmarketSansBold', sans-serif;
    --title-font: 'Exo', 'NIXGONM-Vb', sans-serif;
    --mono-font: 'PT Mono', 'DungGeunMo', monospace;
}

/* Info module fix */
#page-content .rate-box-with-credit-button>.creditButton>p>a:hover:before {
    background-color: rgb(var(--rating-module-button-color));
}

.creditButton a {
    transition: background-color .1s ease-in-out;
}

.creditButton a:hover {
    background-color: rgba(var(--rating-module-button-cancel-color),.8);
}
