@supports(display: grid) {
	:root {
		/* ===S-CSS-P INTEGRATION=== */
		/* If you're making a new CSS theme, please include the following three variables at minimum. */
		--theme-base: "black-highlighter";
		/* must be either "black-highlighter" or "sigma9" */
		--theme-id: "black-rain";
		/* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
		--theme-name: "Black Rain Theme";
		/* set this to your theme's full name */

		/* ===HEADER ELEMENTS=== */
		--logo-image: none;
		--header-title: "黑 王";
		--header-subtitle: "神拘黑白 王宴绯青";

		/* ===STANDARD THEME COLORS=== */
		--bright-accent: 192, 5, 4;
		--medium-accent: 144, 46, 47;
		--dark-accent: 57, 10, 22;

		/* ===HEADER GRADIENT=== */
		--gradient-header: linear-gradient(to top, rgb(var(--black-monochrome)), rgb(var(--black-monochrome)));

		/* ===TOPBAR GRADIENT=== */
		--swatch-topmenu-bg-color: var(--swatch-menubg-dark-color);

		/* ===UI BUTTONS=== */
		--ui-button-hover-bg: var(--swatch-primary-darkest);
		--ui-button-hover-outline: var(--swatch-primary-darkest);

		/* ===RATING MODULE=== */
		--rating-module-text-color: var(--swatch-text-secondary-color);
	}

	:root:lang(cn) {
		/* ===HEADER ELEMENTS=== */
		--header-title: "黑 王";
		--header-subtitle: "神拘黑白 王宴绯青";
	}

	/* ===BUTTONS=== */

	*:not(.page-rate-widget-box):not(#search-top-box-form) > .owindow .button-bar a,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > div.buttons input,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > input.button,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > button,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > .button,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > div.buttons input,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > input.button,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > button,
	*:not(.page-rate-widget-box):not(#search-top-box-form) > .btn {
		border-color: rgb(var(--swatch-menubg-black-color),0.05);
	}

	/* ===HEADER ELEMENTS=== */

