		background-image: url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%22375%22 height=%22120%22%3E%3Cstyle%3E.c0%7Banimation:rain var(--d) linear infinite%7D.c1%7Bfill:rgba(252,252,252,.35)%7D%40keyframes rain%7B0%25%7Btransform:translateY(0)%7Dto%7Btransform:translateY(100%25)%7D%7D%3C/style%3E%3Cg%3E%3Cg class=%22c0%22 style=%22--d:400ms%22%3E%3Cpath class=%22c1%22 d=%22M246.6 85.3h.2v13.2h-.2zM246.6-34.7h.2v13.2h-.2z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:377ms%22%3E%3Cpath class=%22c1%22 d=%22M265.6 2.8h.2v7.5h-.2zM265.6-117.2h.2v7.5h-.2z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:278ms%22%3E%3Cpath class=%22c1%22 d=%22M229.6-3.3h.5v18h-.5zM229.6-123.3h.5v18h-.5z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:228ms%22%3E%3Cpath class=%22c1%22 d=%22M141.6 24.2h.2v8.3h-.2zM141.6-95.8h.2v8.3h-.2z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:204ms%22%3E%3Cpath class=%22c1%22 d=%22M289.7 125.7h.7V138h-.7zM289.7 5.7h.7V18h-.7z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:205ms%22%3E%3Cpath class=%22c1%22 d=%22M257.7 97.2h.7v33.1h-.7zM257.7-22.8h.7v33.1h-.7z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:335ms%22%3E%3Cpath class=%22c1%22 d=%22M314.9 71.9h.5v5.7h-.5zM314.9-48.1h.5v5.7h-.5z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:341ms%22%3E%3Cpath class=%22c1%22 d=%22M368.9 21.5h.7v18.7h-.7zM368.9-98.5h.7v18.7h-.7z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:393ms%22%3E%3Cpath class=%22c1%22 d=%22M282 112h.7v39.8h-.7zM282-8h.7v39.8h-.7z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:233ms%22%3E%3Cpath class=%22c1%22 d=%22M61 96.7h.5v6.2H61zM61-23.3h.5v6.2H61z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:270ms%22%3E%3Cpath class=%22c1%22 d=%22M255.1 58.6h.2v23.5h-.2zM255.1-61.4h.2v23.5h-.2z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:383ms%22%3E%3Cpath class=%22c1%22 d=%22M367.1 3.7h.7v14.6h-.7zM367.1-116.3h.7v14.6h-.7z%22/%3E%3C/g%3E%3Cg class=%22c0%22 style=%22--d:254ms%22%3E%3Cpath class=%22c1%22 d=%22M87.8 23.