.boreal-paper {
background: white url(https://scp-wiki.wdfiles.com/local--files/spc-001-ex/spc-logo-trans3.png) top right no-repeat;
float: center;
box-shadow: 0 1px 3px rgba(0,0,0,.2);
border: solid 1px #000000;
padding: 5px 15px 15px 15px;
margin: 2% 4% 4% 4%;
}

.darkbox {
background-color: rgb(var(--dark-accent));
border: solid 3px rgb(var(--bright-accent));
border-radius: 10px;
color: rgb(var(--bright-accent));
font-family: var(--header-font);
padding: 7px;
float: center;
margin-bottom: 10px;
box-shadow: 0 1px 3px rgba(0,0,0,.2);
}

.boreal-notice {
border: 2px solid;
padding: 7px;
margin-bottom: 10px;
box-shadow: 0 1px 3px rgba(0,0,0,.2);
}

.boreal-notice.spc {
background: lightblue url(https://scp-wiki.wdfiles.com/local--files/spc-001-ex/spc-logo-trans2.png) center no-repeat;
border-color: #999999;
color: black;
}

.boreal-notice.foundation {
background: white url(http://scp-wiki.wdfiles.com/local--files/the-great-hippo/scp_trans.png) center no-repeat;
border-color: #000000;
color: black;
}

:is(blockquote, div.blockquote, [class*="blockquote"]) {
background-color: rgb(var(--white-monochrome));
border: solid 1.5px rgb(var(--dark-accent));
}

