/*
=========================================================
   故障数据库 主题版式
   [2022 Wikidot Theme]
   by Etinjat

 像素点代码来源于 http://scp-wiki-cn.wikidot.com/scp-style-resource

遵循CC-BY-SA协议
==========================================================
*/


@import url("https://etinjat.wikidot.com/component:collapsible-sidebar/code/1");
@import url("https://etinjat.wikidot.com/component:centered-header-bhl/code/2");

@import url('https://fonts.googleapis.com/css2?family=ZCOOL+XiaoWei&amp;amp;amp;display=swap');


:root:lang(cn){
         --body-font: 'Dotted Songti Square', 'PT Sans', '等线', sans-serif;
   --header-font:'Dotted Songti Square',  'ZCOOL XiaoWei', 'Times New Roman', sans-serif;
   --title-font: 'Dotted Songti Square', 'Times New Roman', '华文中宋',  monospace;
   --mono-font:'ZCOOL XiaoWei','Dotted Songti Square',  'PT Sans', '微软雅黑', monospace;     

}

:root {

 /* ----------------------
   修复
 --------------------- */

     --hdh1-top: 1.5rem;
      --hdh2-top:6rem;
       --hdh1-top-mob:2.5rem;
      --hdh2-top-mob:6rem;

  --topbar-height-on-mobile:2.5rem;

 /* ----------------------
   特效
 --------------------- */  

   --fade-in-delay: 4s;   
--typing-speed:6s;

/* ----------------------
   基准色以及其他
 --------------------- */    

                --white-monochrome: 3, 3, 3;
            /*↑ 标准色 ↑*/
                  --pale-gray-monochrome:50, 50, 50;
            /*↑ 非常浅的灰色，用于引用块 ↑*/

              --light-pale-gray-monochrome: 150,150,150;/**/
            /*↑ 浅灰色 ↑*/
               --very-light-gray-monochrome:190,187,156;/**/
            /*↑ 浅灰色 ↑*/

                  --light-pale-gray-monochrome: 205, 205, 205;
            /*↑ 浅灰色 ↑*/
                  --very-light-gray-monochrome: 215, 215, 215;
            /*↑ 浅灰色 ↑*/
                  --light-gray-monochrome: 240, 230, 230;
            /*↑ 浅灰色 ↑*/
                  --black-monochrome: 50, 240, 240;
            /*↑ 文本色 ↑*/

              --pale-accent: 210,107,92;/**/
            /*↑ 浅红色，链接颜色 ↑*/
                  --bright-accent: 100, 120, 225;/**/
            /*↑ 链接 ↑*/
                  --medium-accent:000,000,000;
            /*↑ 红色 - 基准颜色 ↑*/
                  --dark-accent: 122,145,165;
            /*↑ 深色 ↑*/

    --alt-accent: 221, 102, 17;
            /*↑ 橘色，页面不存在的链接颜色 ↑*/

                  --very-dark-accent: 591,5,4;
                  /*↑ 非常深的红色 ↑*/

                  --alt-accent: 204,102,0;
            /*↑ 橘色，页面不存在的链接颜色 ↑*/

                  --alt-accent: 204,102,0;
            /*↑ 橘色，页面不存在的链接颜色 ↑*/

/*---------------------- 其他颜色 ---------------------*/ 
      --swatch-headerh1-color:var(--swatch-text-general);
      --swatch-headerh2-color:var(--swatch-headerh1-color);

      --swatch-text-light:230,240,250;

 /*
--swatch-menubg-medium-dark-color:50,50,100;
--swatch-primary-darkest:000,000,000;
*/

      --swatch-tertiary-color:150,158,200;
      --swatch-text-secondary-color:255,245,255;

/*---------------------- 自设颜色 ---------------------*/ 

      --display-green:23,230,16;
      --darkest-blue: 22,45,65;
      --pale-blue:122,145,165;

      --sel-txt-color:rgb(var(--display-green));
      --sel-bg-color:rgba(0,0,0,0.8);

      --table-border-color:rgba(var(--pale-blue),0.8);
      --table-th-color:rgba(255,255,255,0.8);
      --table-th-bg:rgba(var(--darkest-blue),0.8);

      --color-options-btn:#000;

   /*--------- 修复改色 ------------*/    


      --dark-grey:70,67,58;

      --background-gradient-color:0,0,0;

      --header-gradient-color-top:0,0,0;
      --header-gradient-color-middle:0,0,0;
      --header-gradient-color-bottom:0,0,11;

      --swatch-topmenu-bg-color:var(--dark-grey);
      --swatch-topmenu-border-color:var(--pale-blue);

/*------------------------------
   版头标题   
-------------------------- */    

      --header-h1-font-size:170%;
      --header-h2-font-size:110%;

     --hdh1-size-mob:200%; 
     --hdh2-size-mob:120%; 

   /*------------- 颜色 -----------------*/   
      --hdtt-color-11:rgb(var(--darkest-blue));
      --hdtt-color-22:rgb(var(--swatch-text-general));

      --hdtt-flcl-11:yellow;
      --hdtt-flcl-22:purple;  

  /*------------- 滤镜 -----------------*/   

      --hdtt-filter-000:drop-shadow(1px 0px 0px var(--hdtt-flcl-11)) drop-shadow(1px 2px 0px var(--hdtt-flcl-22)) saturate(100%) brightness(100%);
      --hdtt-filter-aaa:drop-shadow(5px 2px 1px var(--hdtt-flcl-11)) drop-shadow(2px 2px 1px var(--hdtt-flcl-22)) saturate(500%) brightness(150%);
      --hdtt-filter-bbb:drop-shadow(1px 0px 1px var(--hdtt-flcl-11)) drop-shadow(1px 0px 0px var(--hdtt-flcl-22)) saturate(100%) brightness(100%);
      --hdtt-filter-ccc:var(--hdtt-filter-000);
      --hdtt-filter-ddd:drop-shadow(1px 0px 0px var(--hdtt-flcl-11)) drop-shadow(1px 0px 0px var(--hdtt-flcl-22)) saturate(200%) brightness(100%)
      --hdtt-filter-eee:drop-shadow(1px 0px 0px var(--hdtt-flcl-11)) drop-shadow(1px 2px 0px var(--hdtt-flcl-22)) saturate(100%) brightness(100%);
      --hdtt-filter-fff:var(--hdtt-filter-000);
      --hdtt-filter-ggg:drop-shadow(2px 2px 1px var(--hdtt-flcl-11)) drop-shadow(1px 2px 0px var(--hdtt-flcl-22)) saturate(500%) brightness(150%);
      --hdtt-filter-hhh:var(--hdtt-filter-000);

/* -------------- 文字背景色 ------------------ */
 /* w 指 -webkit- 另作一项 */

--hdtt-line-a:linear-gradient(90deg, var(--hdtt-color-11) 40%, var(--hdtt-color-11) 41%, transparent 41.5%,transparent 65%, var(--hdtt-color-22) 65.5%, var(--hdtt-color-22) 90%);
--hdtt-line-aw:-webkit-linear-gradient(90deg, var(--hdtt-color-11) 40%, var(--hdtt-color-11) 41%, transparent 41.5%,transparent 65%, var(--hdtt-color-22) 65.5%, var(--hdtt-color-22) 90%);

      --hdtt-line-0: linear-gradient(90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-22) 90%);
      --hdtt-line-0w: -webkit-linear-gradient(90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-22) 90%);

      --hdtt-line-aa: linear-gradient(-90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 21%, transparent 21.5%,transparent 25%, var(--hdtt-color-22) 25.5%, var(--hdtt-color-22) 90%);
      --hdtt-line-aaw: -webkit-linear-gradient(-90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 21%, transparent 21.5%,transparent 25%, var(--hdtt-color-22) 25.5%, var(--hdtt-color-22) 90%);

      --hdtt-line-bbw: linear-gradient(-90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 30%, transparent 31.5%,transparent 35%, var(--hdtt-color-22) 35.5%, var(--hdtt-color-22) 90%);
      --hdtt-line-bbw: -webkit-linear-gradient(-90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 30%, transparent 31.5%,transparent 35%, var(--hdtt-color-22) 35.5%, var(--hdtt-color-22) 90%);

      --hdtt-line-cc: linear-gradient(-90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 50%, transparent 50.1%,transparent  65%, var(--hdtt-color-22) 65.5%, var(--hdtt-color-22) 90%);
      --hdtt-line-ccw: -webkit-linear-gradient(-90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 50%, transparent 50.1%,transparent  65%, var(--hdtt-color-22) 65.5%, var(--hdtt-color-22) 90%);

      --hdtt-line-dd: linear-gradient(90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 50%, transparent 50.1%,transparent  65%, var(--hdtt-color-22) 65.5%, var(--hdtt-color-22) 90%);
      --hdtt-line-ddw: -webkit-linear-gradient(90deg, var(--hdtt-color-11) 0%, var(--hdtt-color-11) 50%, transparent 50.1%,transparent  65%, var(--hdtt-color-22) 65.5%, var(--hdtt-color-22) 90%);

      --hdtt-line-1:linear-gradient(-30deg, var(--hdtt-color-11) 40%, var(--hdtt-color-22) 90%);
      --hdtt-line-1w:-webkit-linear-gradient(-30deg, var(--hdtt-color-11) 40%, var(--hdtt-color-22) 90%);      

/* -------------------------
      版头logo
-------------------------- */  
  --logo-bg-111:rgba(var(--darkest-blue),1);
  --logo-bg-222:rgba(255,215,0,0.5);

      --logo-mask-size:7rem;    

/*
------------------------------
版头背景
------------------------------
*/
    --content-mask-size:7.5rem;  
--header-mask-size:8.5rem;    
--header-bg-color:rgba(var(--darkest-blue),0.8);
--scanline-filter-color:rgba(var(--display-green),0.5);


  --scanline-cl-111:rgb(var(--darkest-blue));
   --scanline-cl-222:rgb(var(--swatch-text-general));   


      --scanline-header-height:3rem;
      --scanline-hd-start:calc(0% - var(--scanline-header-height));
      --scanline-hd-end:calc(100% + var(--scanline-header-height));



  --scanline-speed:6s;    


  --scanline-filter:brightness(300%) saturate(120%);

   --hdbg-scanline-000:linear-gradient(0deg, transparent 0%, transparent 2%,var(--scanline-cl-111) 5%, var(--scanline-cl-222) 20%, var(--scanline-cl-222) 30%, var(--scanline-cl-111) 50%, transparent 90%, transparent 100%);

      --hdtt-scanline-00w:-webkit-linear-gradient(0deg, transparent 0%, transparent 2%,var(--scanline-cl-111) 5%, var(--scanline-cl-222) 20%, var(--scanline-cl-222) 30%, var(--scanline-cl-111) 50%, transparent 90%, transparent 100%);

/*--------------------------------------------  
      版头杂项 
------------------------------------------- */ 

          --account-op-bg:#333;
      --account-op-bg222:#000;
      --account-op-bd000: 5px double #000;
      --account-op-bd111: 2px solid #000;
      --account-btn-color: #fff;

      --search-color:inherit;
       --search-bg:#000;
       --search-bg-hover:#333; 

/*
--------------------------------
正文部分
--------------------------------
*/    

/*-------------- 引用块 ------------------*/      
      --quote-bg:rgba(var(--swatch-secondary-color),0.8);

      --quote-bd-c11:rgba(var(--swatch-text-general),0.8);

      --quote-bg-box111:rgba(var(--swatch-secondary-color),0.8);
      --quote-bghv-box111:rgba(33,33,33,0.8);

       --quote-bd-sz11:2px;
       --quote-bd:4px solid rgba(33,33,33,0.8);

/*-------------- 扫描引用块 ------------------*/      

      --scanlineletter-end:200%;/*文本扫描尽头*/

      --scanline-ct-bg:var(--header-bg-color);


     --scanline-size-base:3rem;
      --scanline-size-width:var( --scanline-size-base);
      --scanline-size-height:var( --scanline-size-base);
      --scanline-lgsz-horiz:2rem;
      --scanline-lgsz-verti:2rem;


      --scanline-strat-verti:calc(0% - var(--scanline-size-height)) ;
      --scanline-strat-horiz:calc(0% - var(--scanline-size-width)) ;  
      --scanline-limit-verti:calc(100% + var(--scanline-size-height) + 2rem) ;
      --scanline-limit-horiz:calc(100% + var(--scanline-size-width) + 2rem) ;


        --hdbg-scanline-111:linear-gradient(-90deg, transparent 0%, transparent 2%,var(--scanline-cl-111) 5%, var(--scanline-cl-222) 20%, var(--scanline-cl-222) 40%, var(--scanline-cl-111) 60%, transparent 90%, transparent 100%);
       --hdtt-scanline-11w:-webkit-linear-gradient(0deg,transparent 2%,var(--scanline-cl-111) 5%, var(--scanline-cl-111) 10%, var(--scanline-cl-222) 20%, var(--scanline-cl-222) 40%, var(--scanline-cl-111) 60%, transparent 90%, transparent 100%);


      --scanline-letter-speed:5s;
      --scanline-block-speed:10s;

      --scanline-cl-444:rgba(0,255,255,1);
      --scanline-cl-333:rgba(0,191,255,1);


      --hdbg-scanline-222:linear-gradient(-90deg, transparent 0%, transparent 2%,var(--scanline-cl-333) 5%, var(--scanline-cl-444) 20%, var(--scanline-cl-444) 40%, var(--scanline-cl-333) 60%, transparent 90%, transparent 100%)

      --hdbg-scanline-22w:-webkit-linear-gradient(-90deg, transparent 0%, transparent 2%,var(--scanline-cl-333) 5%, var(--scanline-cl-444) 20%, var(--scanline-cl-444) 40%, var(--scanline-cl-333) 60%, transparent 90%, transparent 100%);


     --hdbg-scanline-333:linear-gradient(180deg, transparent 0%, transparent 60%,var(--scanline-cl-333) 80%, var(--scanline-cl-444) 85%, var(--scanline-cl-444) 90%, var(--scanline-cl-333) 95%, transparent 98%, transparent 100%);

      --hdbg-scanline-33w:-webkit-inear-gradient(180deg, transparent 0%, transparent 60%,var(--scanline-cl-333) 80%, var(--scanline-cl-444) 85%, var(--scanline-cl-444) 90%, var(--scanline-cl-333) 95%, transparent 98%, transparent 100%);

       --hdbg-scanline-444:linear-gradient(0deg, transparent 0%, transparent 2%,var(--scanline-cl-111) 5%, var(--scanline-cl-222) 20%, var(--scanline-cl-222) 40%, var(--scanline-cl-111) 60%, transparent 90%, transparent 100%);
       --hdbg-scanline-44w:-webkit-linear-gradient(0deg,transparent 2%,var(--scanline-cl-111) 5%, var(--scanline-cl-111) 10%, var(--scanline-cl-222) 20%, var(--scanline-cl-222) 40%, var(--scanline-cl-111) 60%, transparent 90%, transparent 100%);

/* ------------------- 图标数据 ---------------------- */

--header-image:var(--grid-image); 

--logo-image: var(--icon-scp) ; 

--icon-scp: url('https://fastly.jsdelivr.net/gh/Etinjat/CSS@SCP/LOGO_SCP.svg');


--grid-image:url("data:image/png;base64,");


}

/*
===================================
主体
==================================
*/

p{
      font-size: 1.2em
}


#edit-page-textarea::selection ，
::selection { 
    color:#cc6600;
    color: var(--sel-txt-color) ;
    background:#5a100e; 
    background:var(--sel-bg-color) ;
}

::-moz-selection { 
    color:#cc6600;
    color: var(--sel-txt-color) ;
    background:#5a100e; 
    background:var(--sel-bg-color) ;
}

#edit-page-textarea::-moz-selection,
#edit-page-textarea::selection { 
    color:#000;
    background:rgb(var(--pale-blue)) ;
}

.blinkblock ::-moz-selection,
.blinkblock ::selection {
   background: green;
   color:#000 ;
}

div:is(.linebolck-inbox, .blockquote, blockquote) ::-moz-selection,
div:is(.linebolck-inbox, .blockquote, blockquote)  ::selection {
     background-color:rgb(var(--swatch-text-general),0.6) ;
      color: #000;
}

div:is(.et-typing, .scanline-letter-verti, .scanline-letter) *::-moz-selection,
div:is(.et-typing, .scanline-letter-verti, .scanline-letter) *::-moz-selection{
      background-color: rgba(var(--pale-blue), 1) ;
      -webkit-text-fill-color: #000;
}



/*-------------------------*/

#skrollr-body, #container {
  background-image: none;
 letter-spacing: 0.5px;
}

#container-wrap-wrap::before {
  content: "";
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background: linear-gradient(rgba(18, 16, 16, 0.03) 50%, rgba(0, 0, 0, 0.45) 50%), linear-gradient(90deg, rgba(255, 0, 0, 0.22), rgba(0, 255, 0, 0.11), rgba(0, 0, 255, 0.22));
    background-size: auto, auto;
  z-index: -1;
  background-size: 100% 2px, 5px 100%;/*2 3 */
}


@media (max-width: 500px){
#container-wrap-wrap::before {
  background-size: 100% 2px, 4px 100%;
}
}

/*
===================================
版头标题
h1:before 修改为 after
==================================
*/


#header h1 a:after,
#header h2 span:before{

    font-weight: 500;

      background-image:var(--hdtt-line-1) ;
background-image:var(--hdtt-line-1w) ;
      background-size: 1rem;
     background-repeat:  repeat-x;
       text-shadow: none;

-webkit-background-clip: text;
   background-clip: text;
-webkit-text-fill-color: transparent;
text-fill-color: transparent;

    -webkit-animation: flashing 5s infinite; 
      animation: flashing 5s infinite; 

      animation-timing-function:ease-out;
     -webkit-animation-timing-function:ease-out;
 -webkit-filter:var(--hdtt-filter-000); 
      filter:var(--hdtt-filter-000);
}

@keyframes flashing{
0%{ opacity: 1; 
     -webkit-filter:var(--hdtt-filter-000); filter:var(--hdtt-filter-000);
      background-image:var(--hdtt-line-0); background-image:var(--hdtt-line-0w);
      }    
1%{ opacity: 0.8; 
    -webkit-filter:var(--hdtt-filter-aaa); filter:var(--hdtt-filter-aaa);
    background-image:var(--hdtt-line-dd);background-image:var(--hdtt-line-dd);
      }    
2%{ opacity: 1; 
      -webkit-filter:var(--hdtt-filter-bbb);filter:var(--hdtt-filter-bbb);
      }    
40%{ opacity: 1; 
      -webkit-filter:var(--hdtt-filter-ccc);filter:var(--hdtt-filter-ccc);
      background-image:var(--hdtt-line-0);background-image:var(--hdtt-line-0w);
      }     
43%{ opacity: 1; 
      -webkit-filter:var(--hdtt-filter-ddd);filter:var(--hdtt-filter-ddd);
      }    
44%{ opacity: 1; 
      -webkit-filter:var(--hdtt-filter-eee); filter:var(--hdtt-filter-eee);
      }        
80%{ opacity: 1; 
      -webkit-filter:var(--hdtt-filter-fff); filter:var(--hdtt-filter-fff);
      background-image:var(--hdtt-line-0) ; background-image:var(--hdtt-line-0w) ; 
      }    
83%{ opacity: 1; 
      -webkit-filter:var(--hdtt-filter-ggg);filter:var(--hdtt-filter-ggg);
      background-image:var(--hdtt-line-cc) ;background-image:var(--hdtt-line-ccw) ;
      }        
84%{ opacity: 1;
      -webkit-filter:var(--hdtt-filter-hhh); filter:var(--hdtt-filter-hhh);
      background-image:var(--hdtt-line-0);  background-image:var(--hdtt-line-0w); 
      }  
100%{ opacity: 1;
      -webkit-filter:var(--hdtt-filter-hhh);filter:var(--hdtt-filter-hhh);
      }    
}

@media (max-width: 500px){
#header h1 a {
  letter-spacing: 2px;
  }

  #header h1 a {
  font-size: var(--header-h1-mob-size);
  }

  #header h2 span {
     font-size: var(--header-h1-mob-size);
  }
}

/*
===================================
版头logo
==================================
*/

#header::before {
 display: none
}

#header-extra-div-1:after,
#header-extra-div-1:before,
#header-extra-div-1{
      display: block;
  top:0;
  position: absolute;
  width: 100%;
  height:var(--header-height-on-desktop);
  z-index: -1;
      background: var(--logo-image) ;
      background-repeat: no-repeat;
      background-position: 50% 50%;
      background-size: var(--logo-mask-size);
       filter: brightness(100%) drop-shadow(0 0 1px #333) drop-shadow(0 0 0px #333) drop-shadow(0 0 0px #333);
}

#header-extra-div-1:after,
#header-extra-div-1:before{
   content: ' ';
    mask:var(--logo-image);
    mask-size:var(--logo-mask-size);
   mask-repeat: no-repeat;
   mask-position: center;   
    -webkit-mask:var(--logo-image);
    -webkit-mask-size: var(--logo-mask-size);
    -webkit-mask-repeat: no-repeat;
    -webkit-mask-position: center;   
            background-size: 50%;
      opacity: 0.4;

}

#header-extra-div-1:after{
 background: var(--logo-bg-111);
 -webkit-animation: lg-aft 4s infinite; 
 animation: lg-aft 4s infinite; 
      opacity: 0.9;
}

#header-extra-div-1:before{
 background: var(--logo-bg-222);
-webkit-animation: lg-bf 4s infinite; 
 animation: lg-bf 4s infinite; 
 opacity: 0.5;  
}

@keyframes lg-bf{
      0%{left:0rem; top:0rem}
      0.01%{left:0.5rem; top:-0.5rem}
      2%{left:0.5rem; top:-0.5rem}
      2.01%{left:0rem; top:0rem}
      40%{left:0rem; top:0rem}
      40.01%{left:0.5rem; top:0rem}
      42%{left:0.5rem; top:0rem}
      42.01%{left:0rem; top:0rem}
      80%{left:0rem; top:0rem}
      80.01%{left:-0.5rem; top:0.2rem}
      83%{left:-0.5rem; top:0.2rem}
      83.01%{left:0rem; top:0rem}
      100%{left:0rem; top:0rem}
}

@keyframes lg-aft{
     0%{left:0rem; top:0rem}
      0.01%{left:0.5rem; top:0rem}
      3%{left:0.5rem; top:0rem}
      3.01%{left:0rem; top:0rem}
      40%{left:0rem; top:0rem}
      40.01%{left:-1rem; top:0rem}
      43%{left:-1rem; top:0rem}
      43.1%{left:0rem; top:0rem}
      80%{left:0rem; top:0rem}
      80.01%{left:0.5rem; top:-0.5rem}
      83%{left:0.5rem; top:-0.5rem}
      83.01%{left:0rem; top:0rem}
      100%{left:0rem; top:0rem}
}

/*
===================================
版头背景
==================================
*/

#extra-div-6:before,
#extra-div-6:after,
#extra-div-6{
   position: absolute;
   display: block;
   top:0;
   left: 0;
height: var(--header-height-on-desktop);
width: 100%;
}

#extra-div-6{  
  background-color:var(--header-bg-color);
          mask:var(--header-image);
    mask-size:var(--header-mask-size);
   mask-repeat: no-repeat;
   mask-position: center;   
    -webkit-mask:var(--header-image);
    -webkit-mask-size: var(--header-mask-size);
    -webkit-mask-repeat: repeat;
    -webkit-mask-position: center;   
      opacity: 0.8;
      overflow-y: hidden;
} 


#extra-div-6:before{
      content: '';
      display: block;
