#header h1 {
    animation:anim_h1 5s ease-in-out;
}
#header h2 {
    animation:anim_h2 5s ease-in-out;
}
@keyframes anim_h1 {
    0% {opacity: 0}
    58% {margin-left: -50px; opacity: 0}
    73% {margin-left: 0; opacity: 1}
    100% {opacity: 1}
}
