/* 正文部分↓ */
#page-content {
    background: rgba(var(--dark-accent), 0.8);
}

/* 侧边栏↓ */
#side-bar {
    background: rgba(var(--dark-accent), 0.9) !important;
}
#edit-page-textarea {
    background: transparent !important;
}
.side-block {
    background: rgba(var(--dark-accent), 0.6) !important;
}
@media screen {
    .side-block {
        background: rgba(var(--dark-accent), 0.8) !important;
    }
}

/* 顶栏↓ */
#top-bar {
    background: rgba(var(--dark-accent), 0.8) !important;
}

/* 账户样式↓ */
#account-options {
    background: rgba(var(--dark-accent), 0.8) !important;
}
#login-status {
    background: rgba(var(--dark-accent), 0.8);
}
@media screen {
    #login-status {
        background: transparent;
    }
    #login-status:hover {
        background: rgba(var(--dark-accent), 0.8);
    }
}

/* 评分栏↓ */
.page-rate-widget-box {
    background: rgba(var(--dark-accent), 0.5);
}
.rate-box-with-credit-button {
    background: rgba(var(--dark-accent), 0.5) !important;
}

/* 著作信息块↓ */
#u-credit-view .modalbox {
    background: rgba(var(--dark-accent), 0.8) !important;
}

/* 插图块↓ */
.scp-image-block {
    background: rgba(var(--dark-accent), 0.5);
    border: none !important;
    box-shadow: none !important;
}
.scp-image-caption {
    border-bottom: solid 2px rgba(var(--bright-accent));
    box-shadow: none !important;
}

/* 折叠动画↓ */
#page-content .collapsible-block-unfolded .collapsible-block-content {
    top: 0;
    overflow: hidden;
    transform-origin: 0 0;
}
#page-content .collapsible-block-unfolded .collapsible-block-content[style*="none"] {/* 展开 */
    display: block !important;
    animation: anim-appear 0.5s ease-in-out 0s 1 both;
}
#page-content .collapsible-block-folded, #page-content .collapsible-block-unfolded-link {
    border-bottom: solid 2px rgba(var(--bright-accent)) !important;
    background: rgba(var(--dark-accent), 0.7) !important;
    padding: 0.5rem 1rem;
    width: min-content;
    white-space: nowrap;
    overflow: hidden;
    margin: auto;
    margin-bottom: 1rem;
}
#page-content .collapsible-block-folded .collapsible-block-link::before {
    content: "▷ ";
}
#page-content .collapsible-block-unfolded .collapsible-block-link::before {
    content: "▽ ";
}
#page-content .collapsible-block-link {
    text-decoration: none;
    color: rgb(var(--white-monochrome));
    font-weight: bold;
}

/* tab动画↓ */
.yui-navset {
    background: rgba(var(--dark-accent), 0.7);
}
.yui-navset .yui-content > div {
    display: block;
    top: 0;
    overflow: hidden;
    transform-origin: 0 0;
}
#page-content .yui-navset .yui-content > div[style*="block"] {
    display: block !important;
    animation: anim-appear 0.5s ease-in-out 0.5s 1 both;
}
#page-content .yui-navset .yui-content > div[style*="none"] {
    display: block !important;
    animation: anim-disappear 0.5s ease-in-out 0s 1 both;
}
@keyframes anim-disappear {
    0% { max-height: 9999rem; opacity: 1; }
    1% { max-height: 100vh; }
    100% { max-height: 0; opacity: 0; }
}
