[[code type="CSS"]]
/*=========================================================
              ⚙️ 羽天一的破碎世界BHL版主题 ⚙️
       ------------------------------------------------
         破碎之神教会的LOGO由Sunyclocwork设计
          侧边栏和版头logo的衬图由Woednaz制作
                       遵循CC-BY-SA协议
==========================================================*/

@import url("https://fastly.jsdelivr.net/gh/Etinjat/CSS@Theme/Comportment_collapsible-sidebar_000.css");

@import url('https://fonts.googleapis.com/css2?family=ZCOOL+XiaoWei&display=swap');
@import url('https://fonts.googleapis.com/css?family=Iceberg');

/* ============================================
赋值 
============================================*/

:root:lang(cn), :root {
   --body-font: 'Iceland', '微软雅黑', '等线', 'Noto Sans SC',  'Noto Sans TC',  'Merriweather', 'Iceberg', 'Noto Serif SC', monospace;
    --header-font: 'Noto Serif SC', 'Noto Serif TC', 'Noto Serif',  '思源宋体 CN',   '华文中宋', '宋体', lisu, '楷体', 'ZCOOL XiaoWei', 'Iceberg' , 'Noto Serif SC', sans-serif;
    --sub-header-font: '楷体', 'Noto Serif SC', 'ZCOOL XiaoWei', 'Iceberg' ,sans-serif;
    --page-title-font: 'ZCOOL XiaoWei', 'Iceberg',  '装甲明朝',  '华文中宋',  '思源宋体 CN', 'Noto Serif SC', CTWeiBeiSJ, '楷体', 'Ivymode' , 'Iceberg' ,sans-serif;
    --title-font:  'Noto Serif SC', '思源宋体 CN',  'Noto Sans SC', 'Noto Serif SC',  'ZCOOL XiaoWei',  '新宋体', 'Kumar One', 'Turnip' , 'Iceberg' , sans-serif;
    --mono-font: '仿宋', 'pennsylvania ', monospace, 'ZCOOL XiaoWei', sans-serif;

/* 版头文字 */
     --header-title:"破碎之神教会";
     --header-subtitle:"修补这个破碎的世界";
    --phone-title: var(--header-title);
     --phone-subtitle: var(--header-subtitle);

--cobg-image:url(https://fastly.jsdelivr.net/gh/Etinjat/CSS@SCP/LOGO_COBG-Broken_white.svg);
}


:root{
   --theme-base: "black-highlighter";
   --theme-id: " broken-world-bhl";
   --theme-name: "Etinjats Broken world BHL theme";

    /* logo遮罩  */
   --gradient-logo-under:  linear-gradient(180deg,#2e2c2c 0%, #333 0%, #000 0%);  
   --gradient-logo-cover: linear-gradient(rgb(145, 145, 145,0.5) 0%, rgb(44,46, 44,0.8) 30%, rgb(51, 51, 51,0.8) 100%);

    /* 版头遮罩  */
    --gradient-header: linear-gradient(50deg, #151515, #141414,#3b3b3b,#342f2f,#2a2a2a,#000);  
   --gradient-header-line: repeating-linear-gradient(50deg,hsla(0,0%,90%,0),hsla(0,0%,100%,0) .25vh,rgba(88,88,88,0.1) .35vh,rgba(88,88,88,0.2) .4vh);
    --header-title-cover: linear-gradient(120deg,#e8e8e8 0%, #a2a2a2 10%, #fff 50%);
      --gradient-header-webkit:  -webkit-linear-gradient(50deg, #151515, #141414,#3b3b3b,#342f2f,#2a2a2a,#000);  
      --header-title-cover-webkit: -webkit-linear-gradient(120deg,#e8e8e8 0%, #a2a2a2 10%, #fff 50%);

 /* */                        
 /* 颜色与阴影遮罩 */  
 /* */

  /* 侧边栏遮罩  */
   --gradient-sidebar-cover: linear-gradient(150deg,#2e2c2c 0%, #333 20%, #000 100%);     
   --gradient-sidebar-line: repeating-linear-gradient(60deg,hsla(0,0%,90%,0),hsla(0,0%,100%,0) .25vh,rgba(88,88,88,0.1) .35vh,rgba(88,88,88,0.2) .6vh); 
   --gradient-sidebar-btn: linear-gradient(10deg, rgba(44,46,46,0.1),rgba(88,76,26,0.2),rgba(0,0,0,0.6));    
  --gradient-sidebar-txt-line:repeating-linear-gradient(20deg,hsla(0,0%,90%,0),hsla(0,0%,100%,0) .25vh,rgba(88,88,88,0.1) .35vh,rgba(88,88,88,0.2) .6vh);  

/* 基准色以及其他 */    
    --bright-accent:0,0,0;
    --medium-accent:grey;
    --dark-accent:0,0,0;
                --white-monochrome: 250, 250, 250;
                  --pale-gray-monochrome: 244, 244, 244;
                  --light-pale-gray-monochrome: 225, 225, 225;
                  --very-light-gray-monochrome: 215, 215, 215;
                  --light-gray-monochrome: 150, 150, 150;
                  --black-monochrome: 5, 5, 5;
     --swatch-menubg-light-color:180,180,180;   

        --tabs-selected-txt:#000;


    /* 评分颜色 */
                 --rating-module-button-plus-color: none;
            /*↑ 好评背景颜色 - 绿色 ↑*/
                  --rating-module-button-negative-color: none;
            /*↑ 差评背景颜色 - 红色 ↑*/
                  --rating-module-button-cancel-color: none;



    /*
 --gray-monochrome: 30, 36, 30;
 --dark-gray-monochrome: 25, 25, 25;
        --dark-gray-monochrome:77,77,77;
*/

 /* 链接颜色   */
                  --link-color:118,36,36;
                  --visited-link-color:90,90,90;
                  --hover-link-color:165,42,42;
                  --newpage-color: 105,32,32;
                  --sidebar-links-text:255,255,255;

 --sidebar-headlink-color:grey;

 /*------------------ 图片  ---------------------- */

  --logo-image: var(--cobg-image);

        --ironhide-image: url("http://scp-wiki-cn.wdfiles.com/local--files/theme:broken-world/Bgr-wanderers-goldfoil.png");

      --scp-image: url("https://nu-scptheme.github.io/Black-Highlighter/img/logo.svg");



/* --------------------------著作信息文件夹 -----------------------*/
   --linkColour: #999 !important;
   --barColour:  linear-gradient(0deg, #555, #3b3b3b,#342f2f,#2a2a2a,#333)  !important;

/*  滚动条 */
     scrollbar-color: rgba(128,128,128, 1) rgba(51,51,51, 0.50);
}

 /*
=====================================
主体
=====================================
*/

  #page-content{
       letter-spacing:0.5px;
  }

@media (max-width: 479px) {    
  #page-content{
       letter-spacing:auto;
  }
}

/* 
============================================
版头 
============================================
*/
div#container-wrap {
    background-image: var(--gradient-header-line);
}

#header h2 span {
    line-height: 2.5;
    font-weight: 500;
    padding: 0.05rem 0rem 0rem 0.2rem;
    font-family: var(--sub-header-font);
}

@media (max-width: 500px) {
   #header h1 a::before {
    padding: 0 0 0 0.5rem;
    content: var(--header-title);
    content: var(--phone-title);
   }


 #header h2 span::before {
    content: var(--header-subtitle);
    content: var(--phone-subtitle);
  } 

    #header h1 a::before,#header h2 span{
    font-size:120% ;

}

 #header h2 span { 
    font-size:100% ;
} 
}




/* 
============================================
3d 版头标题 
============================================
*/

/* 限电脑屏幕 */
 @media screen and (min-width: 475px){
#header h1 a::before,#header h2 span::before{
     background-image:var(--header-title-cover);
   -webkit-background-size: cover;
   background-size: cover;
   -webkit-background-clip: text;
   -webkit-text-fill-color: transparent;
   overflow-wrap: normal; 

--tt-text-shadow: rgb(var(--swatch-text-dark,12,12,12,));
text-shadow: .063rem .063rem .063rem #0c0c0c;
text-shadow: .063rem .063rem .063rem var(--tt-text-shadow);

       filter: brightness(160%);
   -webkit-filter: brightness(160%);
    }    
}




 @media screen and (max-width: 475px){
#header h1 a::before,#header h2 span{
    font-size:120% ;

}

 #header h2 span a:before{ 
    font-size:100% ;
     padding: 0;
     margin: 0;
} 
}
/*
============================================
