/* 限电脑屏幕 */
@media screen and (min-width: 500px){

#header {
   background-image: none;
}

/* 插入div格式，背景图片 */
#header #header-extra-div-1,
#header #header-extra-div-2{
    display: flex;
    position: absolute;
     width: 6.250rem;
    height: 6.250rem;
    display: flex;
    position: absolute;
    left: 0.15rem;
    top:0.55rem;
}

#header #header-extra-div-1::before,
#header #header-extra-div-2::before {
    content: "";
    width: 6.250rem;
    height: 6.250rem;
    position: absolute;
    background-repeat: no-repeat;
    background-position: center center;
    background-size: contain;
    flex: 1;
}

/* 层叠1 主要底图*/
 #header #header-extra-div-1::before {
    z-index: 1;
    background: var(--ironhide-image),
                         var(--gradient-logo-under);
    mask:var(--logo-image);
    mask-size: cover;
    -webkit-mask:var(--logo-image);
    -webkit-mask-size: cover;
    background-size: cover;    
}

/* 层叠2 滤镜*/
 #header #header-extra-div-2::before {
    background:var(--gradient-logo-cover);
    mask:var(--logo-image);
    mask-size: cover;
    -webkit-mask:var(--logo-image);
    -webkit-mask-size: cover;
    z-index: 2;
}


}

