/* 
============================================
tab格式 
============================================
*/

/*  tab：内容页  */
.yui-navset .yui-content{
    border: 0.2rem inset #5E5E5E;
}


/*  tab：边框  */
.yui-navset .yui-nav a, 
.yui-navset .yui-nav .selected, .yui-navset .yui-navset-top .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav a{
     border: 0.2rem outset #5E5E5E;
    color: #444;
}

.yui-nav{
    background:#888;


}


/*  tab：未被选中 */
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
     filter: drop-shadow(2px 10px 10px #333);
     -webkit-filter: drop-shadow(2px 10px 10px #333);   
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a,
.yui-navset .yui-nav a::before, .yui-navset .yui-nav a:hover::before,
.yui-navset .yui-nav .selected:active a::before, .yui-navset .yui-nav .selected:focus a::before, .yui-navset .yui-nav .selected:hover a::before, .yui-navset .yui-nav .selected a::before {
    background:radial-gradient(#ecd5d5,#afb3af,grey);
    transition: none ;
}



/*  tab：被选中时 */

.yui-navset .yui-nav .selected, 
.yui-navset .yui-navset-top .yui-nav .selecteda:focus,
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, 
.yui-navset .yui-nav .selected a:hover {  
      filter: sepia(15%) drop-shadow(0px -3px 1px #333);
    -webkit-filter: sepia(15%) drop-shadow(0px -3px 1px #333);
 }

.yui-navset .yui-nav .selected {
    z-index: 10;
    margin: 0;
    padding: 0;
    box-shadow: none;
}


/* tab：鼠标移过 */
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    filter: sepia(15%) drop-shadow(2px 8px 8px #000);
    -webkit-filter: sepia(15%) drop-shadow(2px 8px 8px #000);
}


/*  tab：移过和选择 */

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav li:hover,
.yui-navset .yui-nav .selected {
       color: #333;
}


.yui-navset .yui-nav li:hover,
.yui-navset .yui-nav .selected {
       color: #333;

     transform-origin: bottom;    
    -webkit-transform-origin: bottom;
    -moz-transform-origin: bottom;  

      transform:rotateX(25deg);
    -webkit-transform:rotateX(25deg); 
    -moz-transform:rotateX(25deg); 
}


/*  tab：被选择时，顶部选页的文字样式 */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
   color: #000 !important;
}

