#main-content .page-tags span a:not([href^="/system:page-tags/tag/_"]), #main-content .page-tags span a:visited:not([href^="/system:page-tags/tag/_"]){
    padding: 0.2rem 0.5rem  0.2rem 1rem;
    margin-right:0.4rem;
}

#main-content .page-tags,
#main-content .page-tags span {
     display: block ;
    height: auto;
    width: 100%;
    visibility: visible;
    clip-path:none;
     display: block;
}

#main-content .page-tags span a,
#main-content .page-tags span a:not([href^="/system:page-tags/tag/_"]):not(:last-child), 
#main-content .page-tags span a:visited:not([href^="/system:page-tags/tag/_"]):not(:last-child) {
        border: 2px solid #333;
        border-right: 2px solid #333;
}



 @media (max-width: 600px) {
#main-content .page-tags::before {
padding: 0.5rem 0.5rem 0.1rem 0.5rem;
top: -1.5rem;
    width: 3.5rem;
    font-size: 0.6rem;
}   
}


/* 
============================================
底部按钮 
============================================
*/
.page-options-bottom a {
    display: inline-block;
    white-space: nowrap;
}

div[id*="page-options-bottom"]{
  justify-content:flex-end ;
    margin-top: 0rem;
}


/* 因为nu主题受修改 */
#main-content .page-options-bottom a,
#main-content #action-area div #edit-page-form .buttons.alignleft input {
    cursor: pointer;
    padding: 0.2rem;
    margin: 0.1rem;
    text-decoration:none;
    transition: filter 0.5s;
    filter: drop-shadow(2px 2px 3px #000);
    -webkit-filter: drop-shadow(2px 2px 3px #000);
}

#content-wrap #main-content .page-options-bottom a,
#content-wrap #main-content #action-area div #edit-page-form .buttons.alignleft input {
    border: 3px double #343434;
}

 #content-wrap #main-content .page-options-bottom a:hover,
#main-content .page-options-bottom  a:hover {
border: 3px double #e8e8e8;
     filter:  drop-shadow(0.4em 0.6em 0.1em #333);
    -webkit-filter: drop-shadow(2px 5px 2px #333);
}

 #content-wrap #main-content .page-options-bottom a:hover:before,
#main-content .page-options-bottom  a:hover:before {
    content: '|';
    color: #e8e8e8;
       position: absolute;
    left: 1.5em;
font-size: 1.2rem;
}

@media (max-width: 500px) {
  div[id*="page-options-bottom"] {
    justify-content: center;
  }
    #content-wrap #main-content .page-options-bottom a:hover:before,
    #main-content .page-options-bottom  a:hover:before {
    left: calc(var(--icon-size) - .6em);
    }
 }

/* 
============================================
底部
============================================
 */
#footer {
    background: linear-gradient(120deg, #030303, #2c2c2c,#323131,#545353,#1c1919,#1b1818);
    width:inherit;
    flex-wrap: revert;
}


#footer .options {
   width: 100%;
    justify-content: right
}

#license-area {
    background-color: #666;
}


/* 
============================================
其他
============================================
 */

.page-tags *, #page-options-container, 
#page-options-container #page-options-bottom a,
#page-options-container #page-options-bottom-2 a,
 .hovertip * {
  font-family: var(--body-font) ;
    }



table.wiki-content-table th {
    background: #bfbfbf;
    color: #000;
}
/* 懒人组件 */
.list-pages-box div.preview {
    display: block;
}

/* 页面修改之处的颜色 */
div.inline-diff del {
    background-color: #FCC;
}
div.inline-diff ins {
    background-color: #DDF;
}

/* 悬浮脚注里的引用块 */
.footnote blockquote, .footnote  div.blockquote {
    background-color: rgb(var(--light-gray-monochrome));
    border: 1px solid  rgb(var(--dark-gray-monochrome));
}

.footnote .f-heading{  
        background-color: rgb(var(--dark-gray-monochrome));
}

