[[iftags +theme]]
[[module css]]

@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/normalize.min.css");
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/black-highlighter.min.css");

/* 
=====================
Title 
=====================
*/

:root {
	--img-line-1: url("https://scp-wiki.wikidot.com/local--files/theme:magazine/ZZZ-Chapter-Line.png");
	--img-1: url("https://scp-wiki.wikidot.com/local--files/theme:magazine/ICON_FlyDragon.svg");

	--left-1: 0 0.8rem 0 0;
	--right-1: 0 0 0 0.8rem;

	--img-height: 2.8rem;
	--img-width: 5rem;

	--phone-tt-size: 1.5rem;
	--img-height-phone: 1.5rem;
	--img-width-phone: 3rem;
	--left-phone: 0 0.5rem 0 0;
	--right-phone: 0 0 0 0.5rem;
}

.tt-ch {
	margin: 6rem 0 4rem 0;
}

.tt-line-top,
.tt-line-bottom {
	background: var(--img-line-1) no-repeat scroll center transparent;
	background-size: contain;
	height: 2rem;
	margin: 0rem 0rem 0 0rem;
	padding: 1rem 0rem 0 0rem;
	width: 100%;
	opacity: 0.8
}

.tt-txt h1 {
	text-align: center;
	font-size: 2.5rem;
	margin: 0;
	font-weight: 500;
}

.tt-txt h1::before,
.tt-txt h1::after {
	content: ' ';
	background-size: contain;
	height: var(--img-height);
	width: var(--img-width);
	display: inline-block;
	background-image: var(--img-1);
	background-repeat: no-repeat;
}

.tt-txt h1::before {
	margin: var(--left-1);
	transform: scaleX(-1);
}

.tt-txt h1::after {
	margin: var(--right-1);
}

@media (max-width: 479px) {
	.tt-ch {
		margin: 5rem 0 2rem 0;
	}

	.tt-txt h1 {
		font-size: var(--phone-tt-size);
	}

	.tt-line {
		margin: 0 0 1rem 0;
	}

	.tt-txt h1::before,
	.tt-txt h1::after {
		content: ' ';
		background-size: contain;
		height: var(--img-height-phone);
		width: var(--img-width-phone);
		display: inline-block;
	}

	.tt-txt h1::before {
		margin: var(--left-phone);
	}

	.tt-txt h1::after {
		margin: var(--right-phone);
	}
}

.tt-txt h1 {
	font-family: 'Kumar One';
}

.my-font h1,
.my-font h2,
.my-font h3,
.my-font h4,
.my-font h5,
.my-font h6 {
	font-family: 'Kumar One';
}

/* 
=====================
Semibox
=====================
 */

.mmposter-cell .page-rate-widget-box {
	float: right;
}

.mmposter-wrap {
	display: flex;
	justify-content: center;
	flex-wrap: wrap;
}

.mmposter-cell {
	flex-basis: 50%;
	justify-self: center;
}

/**/
.help tt,
.no-sel tt {
	-moz-user-select: none;
	-webkit-user-select: none;
	-ms-user-select: none;
	user-select: none;
	color: #555;
	padding: 0 0rem 0 0rem;
}

.no-sel tt:before {
	content: " ←  "
}

/**/

/* 
=====================
COLLAPSIBLE SIDEBAR
=====================
 */

@media only screen and (min-width: 47.9375rem) {
	#page-content {
		max-width: 100%;
	}
}

#page-content {
	width: 100%;
}

#main-content {
	min-width: {$main-width};
}

@media (max-width: 700px) {

	#main-content {
		min-width: 0rem;
	}
}

#side-bar,
#side-bar:hover {
	border: 0.5rem groove #2e2c2c;
	background-color: grey;
	background-image: var(--gradient-sidebar-line);
}

#side-bar {
	opacity: 0;
}

#side-bar:hover {
	opacity: 1;
}

@media screen and (max-width: 475px) {

	#side-bar,
	#side-bar:not(:target) {
		opacity: 0;
		transition: all 0.5s;
	}

	#side-bar:target {
		opacity: 1;
		transition: all 0.5s;
	}
}

/*
-----------------------
other
-----------------------
*/

.unmargined {
	background-color: #5f5f5f !important;
}

#breadcrumbs {
	text-align: center;
}

.heritage-rating-module {
	display: none !important
}

.help {
	display: flex;
	align-content: stretch;
	padding-top: 1em;
}

.help blockquote {
	margin: 0;
	font-size: 85%;
}

.help blockquote>p {
	display: flex;
	flex-wrap: wrap;
	align-items: center;
	align-content: center;
	margin-right: 0;
	min-height: 2rem;
	margin: 0;
}

.help blockquote>p * {
	margin: 0 0.1rem;
	height: 100%;
}

.help blockquote:nth-of-type(1) {
	background: transparent;
	border: unset;
	box-shadow: unset;
	text-align: right;
	margin-right: 0;
	padding: 0;
}

.help blockquote:nth-of-type(1)>p {
	justify-content: flex-end;
}

.help blockquote:nth-of-type(2) {
	margin-left: 1em;
	width: 50%;
}

.logo-list {
	display: flex;
	flex-wrap: wrap;
	align-items: flex-start
}

.logo-list .scp-image-block {
	width: 28%;
	min-width: 0;
}
