	--swatch-primary-darker: var(--pale-accent);

	/* Primary Menu Colors */
	--swatch-menubg-medium-dark-color: var(--medium-accent);

	/* Colors for Secondary & Tertiary items like Blockquote and YUI Tabs */
	--swatch-secondary-color: var(--bright-accent);
	--swatch-tertiary-color: var(--pale-accent);

	/* Link Colors */
	--link-color: 110, 110, 170;
	--visited-link-color: var(--medium-accent);
}
/* Side Bar Touch Up */
#interwiki .heading {
	background: rgba(var(--pale-accent), 0.35);
	font-family: var(--title-font);
	min-height: 1.5rem;
	height: 1.5rem;
	display: flex;
	align-items: center;
	justify-content: center;
	text-size: 125%;
}

#interwiki .heading p {
	color: rgb(62, 62, 104);
	font-family: var(--title-font);
	text-shadow: none;
	--text-shadow: none;
	text-align: center;
}

/* Reduce Motion Check */
@media screen and (prefers-reduced-motion: reduce) {

	body::before,
	#header-extra-div-1,
	#header-extra-div-2,
	#header-extra-div-3 {
		-webkit-animation: none;
		animation: none;
	}
}
