[[module CSS]]
/* CANDELA Theme */
/* [2024 Wikidot Theme] */
/* Based on Lumina Crash Theme */

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@100;300;400;700;900&display=swap');

:root:lang(cn) {
    --header-title: "深空探索部";
    --header-subtitle: "控制 - 收容 - 保护";
}

:root {
    --header-logo: url('https://scptestwiki.wdfiles.com/local--files/sigma%3Aspace/spacelogo.png');

    --base-header-height: min(calc(12.5rem + 14.5vmin), 24.5rem);
    --title-size: min(calc(1.5rem + 3.75vw), 4.25rem);
    --subtitle-size: min(calc(1.25rem + 1.75vw), 2.75rem);

    --base-border-width: 0.175rem;

    --lumina-background: url("https://ah-sandbox.wikidot.com/local--files/theme:space-ii/bg_r.jpg");
    --lumina-footer-image: url("https://ah-sandbox.wikidot.com/local--files/theme:space-ii/spacefooter.png");

    --basalt-sub-text-color: 216,213,190;
    --basalt-bright-element-color: 255,182,72;
    --basalt-dark-element-color: 233,120,20;


    --basalt-primary-color: 34,28,20;
    --basalt-secondary-color: 130,114,102,.1325;
    --basalt-tertiary-color: 65, 57, 51;
    --basalt-alternate-color: 199, 199, 199;

    --basalt-UI-dark-palette: var(--basalt-undertone);

    --login-status-background-color: var(--basalt-primary-color);
    --top-bar-link-color: var(--basalt-main-text-color);
    --top-bar-dropdown-link-color: var(--basalt-main-text-color);
    --top-bar-dropdown-link-color-hover: var(--link-color);

    --title-border-color: var(--basalt-undertone);

    --footer-link-color: var(--basalt-undertone);
    --bottom-area-text-color: var(--basalt-main-text-color);

    --header-title-color: Made by;
    --header-subtitle-color: Dr Hormress;

    --side-bar-button-mask: url('data:image/svg+xml;base64,');
}

div#header {
    background-image: none;
    box-shadow: none;
}

div#header h1 a::before {
    width: calc(var(--base-header-height) * 0.6);
    margin: 0;
}

div#header h1 a span {
    text-align: center;
}

div#header h1 a span::before {
    font-weight: 100;
}

div#header h1 a span::after {
    font-weight: 900;
}

.owindow .title,
#side-bar .side-block .heading,
#side-bar .collapsible-block-unfolded a.collapsible-block-link,
#side-bar .collapsible-block-folded a.collapsible-block-link {
    color: rgb(var(--basalt-undertone));
    background-color: transparent;
    border-bottom: none;
}

.owindow .button-bar > a[onclick*="cleanAll"]::after {
    background-color: rgb(var(--basalt-undertone));
}

div#page-content {
    grid-template-columns: [full-width-start] var(--main-content-side-margin) [breakout-start] minmax(0,1fr) [content-start] minmax(0,4fr) [content-end] minmax(0,1fr) [breakout-end] var(--main-content-side-margin) [full-width-end];
}

:is(.blockquote, blockquote, .code) :is(.blockquote, blockquote, .code) {
    box-shadow: none;
    background-color: transparent;
}

:is(:where(#page-content) table:not(.form), table.wiki-content-table) th {
    color: rgb(var(--basalt-light-text-color));
}

div#main-content > .page-tags > span a {
    border-radius: 0;
}

a#more-options-button {
    background-color: rgb(var(--basalt-primary-color));
}

.bibitems .title,
.footnotes-footer .title,
.hovertip .content .footnote .f-heading,
.hovertip .content .reference .r-heading {
    --basalt-light-text-color: var(--basalt-primary-color);
}

div#footer {
    border-top: none;
}

/* YUI TAB */

.yui-navset.yui-navset-top > ul.yui-nav {
    font-family: var(--title-font);
    gap: 0;
    background-color: rgb(var(--tab-background-color));
}

.yui-navset.yui-navset-top > ul.yui-nav li {
    background-color: transparent;
    border-bottom: none;
}

.yui-navset.yui-navset-top > ul.yui-nav {
    font-size: 1.1em;
}

/* DIVs */

.hor {
    height: 10rem;
}

.hors {
    height: 5rem;
}

blockquote,
.blockquote,
div.blockquote {
    background-color: rgba(var(--basalt-secondary-color));
}

div.blockquote.warning-panel {
    width: 36rem;
    max-width: 95%;
    border: solid var(--thin-border-width) rgb(var(--basalt-undertone));
}

div.blockquote.warning-panel::before {
    color: rgb(var(--basalt-undertone));
}

div.blockquote.warning-panel::after {
    pointer-events: none;
    opacity: var(--logo-opacity, 0.1);
}

.blockquote.candela-mail {
    position: relative;
    width: auto;
    margin-inline: min(4rem, 4vw);
    border: solid var(--thin-border-width) transparent;
    background-color: rgba(var(--basalt-primary-color), 0.8);
}

.blockquote.candela-mail::before {
    content: "";
    position: absolute;
    left: calc(var(--thin-border-width) * -1);
    top: calc(var(--thin-border-width) * -1);
    width: 100%;
    height: 100%;
    border: solid var(--thin-border-width) rgb(var(--basalt-undertone));
    mask-image: linear-gradient(to right, #000, transparent);
    -webkit-mask-image: linear-gradient(to right, #000, transparent);
    pointer-events: none;
}

.blockquote.candela-mailInfo {
    border-left-width: var(--thin-border-width);
    padding: 0 1rem;
}

.blockquote.candela-story {
    border-left: none;
    background-color: rgba(var(--basalt-secondary-color));
}

.blockquote.candela-document {
    border-radius: 3rem;
    border-inline-end: solid 0.175rem rgb(var(--basalt-undertone));
    padding: 1.125rem 2.45rem;
}

:is(.image-block, .scp-image-block) img {
  border: solid var(--thin-border-width) rgb(var(--basalt-undertone));
}

/* {$classic}/

:root {
    --lumina-background: url(https://scp-wiki.wdfiles.com/local--files/theme%3Alumina/bg-galaxy.jpg);
    --lumina-footer-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Alumina/earth.png);

    --basalt-sub-text-color: 190, 213, 216;
    --basalt-bright-element-color: 72, 182, 255;
    --basalt-dark-element-color: 20, 120, 233;

    --basalt-primary-color: 20,28,34;
    --basalt-secondary-color: 102,114,130,.1325;
    --basalt-tertiary-color: 51, 57, 65;
}

/{$classic} */

/* {$narrow}/

div#page-content {
    grid-template-columns: [full-width-start] var(--main-content-side-margin) [breakout-start] minmax(0,1fr) [content-start] minmax(0,3.6fr) [content-end] minmax(0,1fr) [breakout-end] var(--main-content-side-margin) [full-width-end];
}

/{$narrow} */

@media screen and (max-width: 1024px) {
    div#page-content {
        grid-template-columns: [full-width-start] var(--main-content-side-margin) [breakout-start] minmax(0,1fr) [content-start] minmax(0,11fr) [content-end] minmax(0,1fr) [breakout-end] var(--main-content-side-margin) [full-width-end];
    }
}

[[/module]]
