[[collapsible show="+ Don't Mind Me!" hide="- Nevermind Then"]]
[[code type="css"]]
    :root {
        /* S-CSS-P INTEGRATION */
        /* If you're making a new CSS theme, please include the following three variables at minimum. */
        --theme-base: "black-highlighter";
        /* must be either "black-highlighter" or "sigma9" */
        --theme-id: "caretaker-theme";
        /* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
        --theme-name: "Caretaker Theme";
        /* set this to your theme's full name */

        /* Header Elements */
        --logo-image: url("https://scp-wiki.wikidot.com/local--files/theme:caretaker-theme/AEEDLogoWhite.png");
        --header-subtitle: "Anomalous Entity Engagement Division";

        /* Theme Colors */
        --bright-accent: 255, 96, 144;
        --medium-accent: 255, 96, 144;
        --dark-accent: 255, 96, 144;
        --gray-monochrome: 163, 0, 107;
        --dark-gray-monochrome: 83, 0, 52;

        /* Header Gradient */
        --header-gradient-color-bottom: 179, 68, 109;
        --header-gradient-color-middle: 225, 21, 133;
        --header-gradient-color-top: 225, 21, 133;
        --diagonal-stripes: unset;

        /* Background Gradient */
        --background-gradient-color: 244, 194, 194;

        /* Link Colors */
        --link-color: var(--bright-accent);
        --visited-link-color: var(--header-gradient-color-bottom);
        --hover-link-color: var(--header-gradient-color-bottom);

        /* Menu Colors */
        --swatch-menubg-color: 83, 0, 52;
    }

    #interwiki,
    #side-bar {
        --sidebar-links-text: 255, 255, 255;
}
[[/code]]
[[/collapsible]]

