[[collapsible show="展开源代码" hide="关闭"]]
[[module CSS show="true"]]
/*
    Tema del CFO
    [Tema di Wikidot creato nel 2022]
    Di DrGraph
    Basato su e ispirato da:
         Penumbra Theme di EstrellaYoshte
         Thalassophobia Theme di fish^12
   Si ringrazia Lekter per l'aiuto offerto nella risoluzione di problemi legati alla visualizzazione da mobile.


**COLORI USATI**
**Titolo header:** #EFEFEF
**Testo:** #EEEEEE
**Sfondo:** #302838
**Sfondo finestre, collassabili e popup:** #303030

**Colore nuovi link:** #D58936
**Colore link visitati:** #A44200
**Colore link visitati nella sidebar:** #B66833
**Colore link inesistenti:** #D3AF37

**Gradiente pulsante ricerca:** #404040, #222222, #000000
**Bordo sidebar:** #A44200
**Selezione elementi pagina:** #D2691E
**Hover su link ed elementi topbar:** #A44200
*/

/*FONT */

@import url('https://fonts.googleapis.com/css2?family=Orelega+One&family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@100;300;400;500;700;900&display=swap');

/*DIMENSIONI TESTO*/

#page-content {
     font-family: 'Poppins', "Noto Sans SC", sans-serif;
}

/* BODY */
body {
     font-size: 15px;
     background: #302828;
     color: #eeeeee;
     background-repeat: no-repeat;
}

/* HEADER */

div#container-wrap {
     background-image: url('http://scpsandboxit.wikidot.com/local--files/dr-graph/CFOheader-v1.png');
}

div#header {
    background: none;
}

div#extra-div-1 {
    height: 164px;
    width: 100%;
    top: 0;
    position: absolute;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: 50% 50%;
    z-index: -1;
}

#header h1, #header h2 { margin-left: 0; float: none; text-align: center; }

#header h2 { margin-top: 0.45rem; }

#header h1 span, #header h2 span { font-size: 0; display: none; }

#header h1 a::before, #header h2::before {
  color: var(--accentColor);
  text-shadow: none;
}

#header h1 a::before {
  font-family: 'Orelega One', serif;
  content: var(--header-title, "⎰ 神秘法西斯委员会 ⎱");
  font-weight: 250;
  font-size: 1.02em;
  color: #f9f9f9;
}

#header h2::before {
  font-family: 'Orelega One', serif;
  content: var(--header-subtitle, "混沌铸就秩序");
  font-weight: 500;
  font-size: 1.5em;
}

/* HEADER CELLULARI */
@media (max-width: 500px) {
#header h1 a::before {
  font-family: 'Orelega One', serif;
  content: var(--header-title, "⎰ 神秘法西斯委员会 ⎱");
  font-weight: 600;
  font-size: 0.8em;
  color: #f9f9f9;
}

#header h2::before {
  font-family: 'Orelega One', serif;
  content: var(--header-subtitle, "混沌铸就秩序");
  font-weight: 300;
  font-size: 1.2em;
}
}

/*LOGIN*/

#login-status {
    color: #ededed;
}
#login-status a {
    color: #D58936;
}
#login-status ul a {
    color: #ffffff;
    background: #303030;
}
#login-status ul a:hover {
    color: ##69140E;
}

#account-topbutton{
    border: inset 2px #a44200;
    background-color: #303030;

}

#footer, #footer a {
    background: transparent;
    color: #ededed;
}
#license-area {
     color: #ededed;
}

#lock-info {
    background-color: #303030;
}


/* BARRA SUPERIORE */

#top-bar {
     top: 8.4rem;
     display: flex;
     justify-content: center;
     right: 0;
}

@media (max-width: 768px) {
    .mobile-top-bar {
         display: flex;
         justify-content: left;
         max-width: 100%;
         width: 100%;
         left: 0;
    }
}

#top-bar, #top-bar a {
     color: #eeeeee;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #303030;
    color: #eeeeee;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #a44200;
    transition: color 0.15s linear width 0.5s;
}

/* BARRA DI RICERCA */

#search-top-box {
    top: 2.75em!important;
    right: 8px;
}

#search-top-box-form input[type=submit] {
     border: outset 2px #eeeeee;
     border-radius: 0px;
     padding: 3px 5px;
     font-size: 90%;
     font-weight: bold;
     color: white;
     background: linear-gradient(to bottom, #404040, #222222, #000000);
     background-color: #303030;
     box-shadow: 0 1px 3px rgba(0,0,0,.5);
     cursor: pointer;
 }
 #search-top-box-form input[type=submit]:hover,
 #search-top-box-form input[type=submit]:focus {
     border: inset 2px #a44200;
     color: #a44200;
     background: linear-gradient(to bottom, #dddddd, #808080, #404040);
     text-shadow: 0 0 1px rgba(255,255,255,.25);
     box-shadow: 0 1px 3px ##69140E;
 }

#search-top-box-form > input[type=text] { display: none; }

/* BARRA LATERALE */

#side-bar .side-block {
     background-color: #404040;
     border-radius: 0px;
     border: solid 1px #a44200;
}

#side-bar .side-block.resources {
     background-color: #303030;
     color: #a44200;
}

#side-bar .heading {
     color: #a44200;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #D58936;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     color: #D58936;
}

#side-bar .side-block.media {
     background-color: #303030;
}

/* TABVIEW E TAB */
.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{
     background-color:inherit;
     background-image:inherit;
}

.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{
     background:inherit;
     background-image: transparent;
     border: 0 0 0px;
}

.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{
      color:inherit;
      background:inherit;
}

.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{
     border-color:#inherit;
}

.yui-navset li{
     line-height:inherit;
}

.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    border: solid 2px #202020;
    background: #303030;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-width: 0 0 0px;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
  background-color: #404040;
  border: 0;
  color: #eeeeee;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
  border: solid 1px #eeeeee;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
  background: #303030;
  border: solid 2px #a44200;
  color: #eeeeee;
  font-weight: bold;
}


/* SELEZIONE ELEMENTI PAGINA */
::selection {
    background-color: #d2691e;
    color: #000000;
}

/* FINESTRE POPUP */

.owindow {
    background-color: #303030;
    border-color: var(--accentColor);
}

.owindow .modal-header {
    background-color: #303030;
}

.owindow .modal-body img {
    background-color: transparent !important;
}

.owindow .title {
    background-color: #303030;
    color: #var(--accentColor);
    border-bottom: 1px solid #2F333C;
}

.owindow .button-bar a {
    background-color: #21252E;
    border-color: var(--accentColor);
    color: #ededed;
}
.owindow .button-bar a:hover {
    background-color: var(--accentColor);
}

/*FOOTNOTE E HOVERTIP*/
.hovertip {
    font-size: .9rem;
    background-color: #303030!important;
    border: solid 2px #a44200;
}

/* BLOCCO CITAZIONI */
blockquote, div.blockquote,
#toc {
    background-color: #303030;
    color: #eeeeee;
    border: 1px solid #202020;
    border-radius: 0px;
    padding: 2px;
    padding-left: 1.4rem;
    padding-right: 1.4rem;
}

/*CONTENUTI*/

#page-title {
    text-align: center;
}

/*COLORE LINK*/
a {
     color: #D58936;     text-decoration: none;
     background: transparent;
}

/*COLORE LINK VISITATI*/
#side-bar a:visited, a:visited {
    color: #B66833;

}

/*COLORE LINK PAGINE INESISTENTI*/

a.newpage {
     color: #D3AF37;
     text-decoration: none;
     background: transparent;
}

/*HOVER SUI LINK*/
 a:hover {
     text-decoration: underline;
     background-color: #a44200;
     color: #eeeeee;
     transition: color 0.15s linear;
}

#side-bar a:visited:hover {
    color: #ffffff;
}

/*MODULO DI VOTO*/

.page-rate-widget-box {
     margin: unset;
     border-radius: 0px;
     border: solid 2px #a44200;
     box-shadow: 3px 0px 0px 0px var(--accentColor);
     background-color: #404040;
     margin-top: 4px;
     margin-bottom:4px;
     margin-right: 3px;
}

.page-rate-widget-box .rate-points {
    background-color: #404040!important;
    border: none;
    color: #ededed !important;
    text-transform: capitalize;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #202020;
    border-top: none;
    border-bottom: none;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #ededed;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #a44200;
    color: var(--accentColor);
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #404040;
    border: none;
}
.page-rate-widget-box .cancel a {
    color: #ededed;
}
.page-rate-widget-box .cancel a:hover {
    background: #a44200;
    color: var(--accentColor);
}

.creditButtonStandalone {
    background: #404040 !important;
    border: 2px solid #a44200 !important;
    margin: unset;
    border-radius: 0px;
    box-shadow: 3px 0px 0px 0px var(--accentColor) !important;
    margin-top: 4px;
    margin-bottom: 4px;
    margin-right: 3px;
}

.creditButtonStandalone .fa-info {
    background: #404040;
    border-radius: 0px !important;
}

.creditButtonStandalone .fa-info:hover {
    background: #a44200;
    border-radius: 0px !important;
    color: #404040 !important;
}

/*COLLASSABILI*/
#page-content .collapsible-block {
    position: relative;
    padding: 0.5em;
    margin: 0.5em;
    overflow-wrap: break-word;
    background-color: #303030;
    border: solid 2px #202020;
}

#page-content .collapsible-block-unfolded{
    color: white;
    overflow-wrap: break-word;
}

#page-content .collapsible-block-unfolded-link {
    text-align:center;
    background-color: #303030;
}

#page-content .collapsible-block-folded {
    text-align: center;
    color: dimgrey;
}

#page-content .collapsible-block-link {
    font-weight: bold;
    color: white;
    text-align: center;
}
.collapsible-block blockquote {
    position: relative;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.42);
    background-color: #303030;
    border: 1px solid #ddd;
    color: #fff;
    margin: 2%;
    font-weight: normal !important;
}
.collapsible-block blockquote h3 {
    color: #fff;
    font-weight: normal !important;
}



/*IMMAGINI*/

.scp-image-block {
    border: solid 1px #303030;
    border-bottom: solid 0px #21252E;
    box-shadow: 0px 0.26rem 0px 0px var(--accentColor);
    box-sizing: border-box;
}


.scp-image-block .scp-image-caption {
    background-color: #404040;
    border: solid 1px #303030;
    color: #eeeeee;
    font-size: 80%;
    font-style: italic;

}

/*TABELLE*/
#page-content .wiki-content-table tr th {
    border: solid 1px var(--accentColor);
    color: var(--accentColor);
    background-color: #505050;
    /* set border for table title */
}
#page-content .wiki-content-table tr td {
    border: solid 1px #505050;
    /* set border for table content */
}


/* ELEMENTI DEL TESTO */

h1 {
    color: #D58936;
    font-weight: bold;
}

h2, h3, h4, h5, h6 {
    color: #efefef;
    font-weight: bold;
}

#page-title {
    color: #D58936;
    font-size: 1.65rem;
    text-align: center;
    border-color: #ededed;
}
[[/module]]
[[/div]]
[[/collapsible]]
------
