[[module CSS]]
/*
    CMST Theme
    [2023 Wikidot Theme]
    Based on Aelanna's Sigma 9 and PeppersGhost's SPC Theme
    Edited by Sifen_Xingfeng for CMST
    CMST logo by Sifen_Xingfeng.
*/

a:visited {
    color: #666bbb;
}
body {
    background: #eaeaf2;
}
blockquote {
    border:1px dashed #9191c7;
    background:#cacae5;
}
:root {
    --themColor:#666bbb;
    --accentColor:#666bbb;
    counter-reset:counter 0;
}
/* HEADER */
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(https://s3.bmp.ovh/imgs/2023/06/19/ff61cb0a740bd562.png) 10px 35px no-repeat;
}
#header h1 a {
    content: "开米斯特超化研究中心";
}

#header h1 a::before {
    content: "开米斯特超化研究中心";
    color: #404040;
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
    font-size: 0px;
}

#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0px;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "化学·唯物·探索·技术";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #898989;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

div#container-wrap {
    background-image: url(https://s3.bmp.ovh/imgs/2022/06/24/2728e34748fe1a1b.png);
}
/* SIDEBAR */
#side-bar .side-block {
    border: 1px solid #656565;
    box-shadow: 0 2px 2px #666666;
}

#side-bar .side-block:not(.media) img {
  width:13px;
  height:13px;
  box-sizing:border-box;
  padding-left: 13px;
  background: url(https://s3.bmp.ovh/imgs/2023/06/22/ed776c70c7ff51e6.png) left top no-repeat;
}

#side-bar .heading {
    color: #474747;
    border-bottom: solid 1px #474747;
}

#side-bar div.menu-item.inactive a {
    color: #7f82b2;
}

#side-bar a:visited {
    color: #666bbb;
}

#side-bar div.menu-item .sub-text {
    color: #7f82b2;
}

#side-bar .collapsible-block-folded {
    background-image: none;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #7f82b2;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #7f82b2;
}

.interwiki_title {
    color: #7f82b2;
}

.interwiki_entry a {
    color: #7f82b2;
}

div.yui-navset div.yui-content {
    background: #cacae5;
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #cfcfe5;
    color: #000000;
    border: solid#9191c7;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #aeaecc;
    color: #000000;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #666bbb;
    color: #ffffff;
    border: solid#9191c7;
}
/* LINKS */

a {
    color: #666bbb;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #666bbb;
    text-decoration: none;
    background: transparent;
}

/* INFO PANE */
.rate-box-with-credit-button {
    background-color: #616687 !important;
    border-color: #7f82b2 !important;
}

.page-rate-widget-box{
    border: none !important;
}

.rate-box-with-credit-button .fa-info {
    border-color: #7f82b2 !important;
    color: #f3f3f3 !important;
}

.rate-box-with-credit-button .fa-info:hover {
    color: #8694cc !important;
}

.rate-box-with-credit-button .cancel {
    border: none !important;
}

.close-credits{
    filter: hue-rotate(210deg);
}

.modalbox {
    box-shadow: 0px 1px 10px rgb(39,130,221,0.3) !important;
}

/* RATING MODULE */
.page-rate-widget-box .rate-points {
    background-color: #7f82b2 !important;
    border: solid 1px #7f82b2;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #ebedff;
    border-top: solid 1px #7f82b2;
    border-bottom: solid 1px #7f82b2;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: #7f82b2;
    color: #ffffff;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #6b6d95;
    color: #e0e0e0;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #7f82b2;
    border: solid 1px #7f82b2;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #ffffff;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #6b6d95;
    color: #e0e0e0;
    text-decoration: none;
}

/* PAGE TITLEs */
h1, #page-title {
    color: #666bbb;
}

/* NAV DROPDOWN */

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #666bbb;
}

/* SEARCH BAR */
#search-top-box-input {
    border: solid 1px #7f82b2;
    background-color: #666bbb;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #66698f;
    background-color: #54589c;
}

#search-top-box-form input[type=submit] {
    border: solid 1px #7f82b2;
    background-color: #666bbb;
    color: #ffffff;
    background: linear-gradient(to bottom, #666bbb,#898feb,#767bd1);
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #66698f;
    color: #e0e0e0;
    background-color: #54589c;
    background: linear-gradient(to bottom, #5b60a5,#767bd1,#666bbb);
}

/* LOGIN STATUS */
#login-status ul a {
    color: #666bbb;
}

/* Changes background for collapsed sidebar */
@media (max-width: 767px) {
#side-bar{background-color: #7f82b2}
}

/* INTERWIKI */
#interwiki .side-block {
    border-color: #7f82b2;
    box-shadow: 0 2px 6px rgba(0,0,51,.5);
}

#interwiki .side-block img {
  width:13px;
  height:13px;
  box-sizing:border-box;
  padding-left: 13px;
  background: url(https://s3.bmp.ovh/imgs/2023/06/22/ed776c70c7ff51e6.png) left top no-repeat;
}

#interwiki .heading {
    color: #7f82b2;
    border-bottom: solid 1px #7f82b2;
}

#interwiki .side-block div.menu-item a {
    color: #666bbb;
    text-decoration: none;
    background: transparent;
}
[[/module]]

[[iftags +版式]]

