@charset "utf-8";

@import url('https://fonts.googleapis.com/earlyaccess/nanumgothic.css');
@import url('https://fonts.googleapis.com/earlyaccess/nanumgothiccoding.css');
@import url('https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css');
@import url('https://fonts.googleapis.com/css2?family=Xanh+Mono&display=swap');


/*
    Tema de la Comisaría (CES)
    [Tema Sigma-9 de 2022]
    [Hecho en la Rama Hispana (Ñ)]
    Creado para la Comisaría Especial de Seguridad (CES)
    Diseñado y codificado por Morhadow y Dc_Yerko
    Creado en base en base al Lasagneception de LazyLasagne
    Logo hecho por DrReach, retocado por Dc_Yerko
*/

/* CUERPO */

body {
    background: #f0f0f0;
    font-size: 0.80em;
    color: #333;
}

/* FONDO DE CABECERA */

div#container-wrap {
    background: url(http://lafundacionscp.wdfiles.com/local--files/theme%3Acomi/Fondo-Comi.png) top left repeat-x;
}

/* LOGO DE CABECERA */

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background-image: url("http://lafundacionscp.wdfiles.com/local--files/theme%3Acomi/comi-bigger-blanco.png");
    background-repeat: no-repeat;
    background-size: 100px;
    background-position: 10px 25px;
}

/* BARRA Y BOTÓN DE BÚSQUEDA */

#search-top-box {
    position: absolute;
    top: 79px;
    right: 9px;
    width: 250px;
    text-align: right;
}

#search-top-box-input {
    border: solid 1px #EDEDED;
    border-radius: 5px;
    color: #F22424;
    background-color: #1C1818;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #efeaea;
    color: #fff;
    background-color: #5d5b5b;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
    border: solid 1px #EDEDED;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #ddd;
    background-color: #282828;
    background: linear-gradient(to bottom, #878787, #5b5b5b, #303030);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25)
    background-color: #a09a9a;
    background: linear-gradient(to bottom, #d1cfcf,#878787,#5b5a5a);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

#login-status {
    color: #aaa;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: #ddd;
}
#login-status ul a {
    color: #700;
    background: transparent;
}

#account-topbutton {
    background: #ccc;
    color: #700;
}

/* TÍTULO DE CABECERA */

#header h1 {
    margin-left: 120px;
    margin-top: -15px;
    padding: 0;
    float: left;
    max-height: 95px;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    content: "";
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Xanh Mono', monospace;
    font-size: 113%;
    text-decoration: none;
    text-shadow: none;
}

#header h1 a::before {
    content: "特别安全委员会";
    color: #eee;
    text-shadow: 3px 3px 5px #242424;
    font-family: 宋体;
    margin-top: -20px;
}

@media (max-width: 767px) {
  #header h1 a::before {
    content: "委员会";
  }
}

/* SUBTÍTULO DE CABECERA */

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
    font-family: 'Xanh Mono', monospace;
}

#header h2 span::before {
    content: '在西班牙！';
     display: block;
     margin: 0;
     line-height: 0px;
     max-height: 0px;
     font-weight: bold;
     font-family: 宋体;
     color: #f0f0c0;
     text-shadow: 1px 1px 1px #000;
     text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* BARRA SUPERIOR */

#top-bar a {
    color: #FFFFFF;
    background: transparent;
}

#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #777;
    color: #f7f4f4;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #444;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #999;
    text-decoration: none;
}

/* BARRA LATERAL */

#side-bar .side-block {
    padding: 10px;
    border: solid 1px black;
    background: #fafafa;
    margin-bottom: 15px;
    border-radius: 2px;
}

#side-bar .side-area {
    padding: 10px;
}
#side-bar .heading {
    color: #474747;
    border-bottom: solid 1px #600;
    padding-left: 15px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}

#side-bar div.menu-item {
    margin: 2px 0;
}

#side-bar div.menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
}
#side-bar div.menu-item a {
    font-weight: bold;
}
#side-bar div.menu-item.inactive img {
    opacity: 0.25;
}

#side-bar div.menu-item.inactive a {
    color: #999;
}

#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #666;
}

#side-bar .side-block.media {
    background: url(http://lafundacionscp.wdfiles.com/local--files/theme%3Acomi/pixels.png);
    border: solid 1px black;
}

#side-bar .side-block.nuevos {
    background: #bebebe;
    border: solid 1px black;
}

#side-bar .side-block.guias {
    background: #d2d2d2;
    border: solid 1px black;
}

#side-bar .side-block.borradores {
    background: #e6e6e6;
    border: solid 1px black;
}

#side-bar .side-block.cc {
    background: #e6e6e6;
    border: solid 1px black;
    font-family: Verdana;
    padding: 10px 0;
    display: block;
}

#side-bar .side-block.recurso {
    background: #d2d2d2;
    border: solid 1px black;
}

#side-bar .collapsible-block-folded {
 aaa
}

#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
}

#side-bar .collapsible-block-folded {
    background: url(http://lafundacionscp.wdfiles.com/local--files/theme%3Acomi/expand.png) 0 2px no-repeat;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #474747;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    text-decoration: none;
}

@media (max-width: 767px) {
    #side-bar{
        background: rgba(0,0,0, 0.5);
    }
}

/* PUNTUACIÓN */

.page-rate-widget-box {
    margin-right: 0px;
}

.creditRate {
    margin-right: 0px!important;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  background-color: transparent !important;
  border: 0;
  text-transform: capitalize;
  font-weight: bold;
    color: #FFF!important;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    margin-right: 0px;
    margin-left: 0px;
}
div.page-rate-widget-box {
    background-color: #676767 !important;
    border: solid #000000 1px!important;
    border-radius: 0px;
    box-shadow: none;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #676767 !important;
    border: 1px #676767;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #FFF !important;
    margin-right: 0px;
    margin-left: 0px;
    color: #000!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000!important;
}
div.page-rate-widget-box span.rateup a:hover {
    background: #fabe00 !important;
    color: #FFF!important;
}
div.page-rate-widget-box span.ratedown a:hover {
    background: #ae0d12 !important;
    color: #FFF!important;
}
div.page-rate-widget-box span.cancel a {
    color: #FFF!important;
}
div.page-rate-widget-box span.cancel a:hover {
    background: #676767!important;
    color: #ae0d12 !important;
    border-radius: 0px;
}

/* PUNTUACIÓN CON INFO */

.rate-box-with-credit-button > div.page-rate-widget-box {
     border-width: 0px!important;
}
#page-content .rate-box-with-credit-button {
    background-color: #676767;
    border: solid 1px #000000;
    border-radius: 0;
    box-shadow: none;
    overflow: hidden;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}
 #page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}
.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .fa-info {
     color: #FFF;
     line-height: 15px;
     padding-top: 1px;
}
@media (max-width: 450px) {
#page-content .rate-box-with-credit-button .fa-info {
        line-height: 14px;
    }
}
@media not all and (min-resolution:.001dpcm)
{ @supports (-webkit-appearance:none) and (stroke-color:transparent) {
    #page-content .rate-box-with-credit-button .fa-info {
        line-height: 14px;
        padding-top: 2px;
    }
}}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: #fabe00;
}
#page-content .creditButtonStandalone p a {
    background-color: #f4f4f4;
    border: solid 1px #000000;
    border-radius: 0;
    box-shadow: none;
    color: #000000;
}
#page-content .creditButtonStandalone p a:hover {
    color: #ff0000;
}

#page-content .modalbox {
     background: #f4f4f4 !important;
     color: #000000;
     box-shadow: none;
}
.close-credits,
.credit-back {
     filter: hue-rotate(240deg);
}

/* TABLAS */

table.wiki-content-table th {
    background-color: #676767;
    color: #FFFFFF;
    border: solid 1px #888888;
}

/* TABS */

.yui-navset .yui-content {
    border-radius: 2px;
    background-color: #e5e5e5;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#303030;
    background-image: url(http://d3g0gp89917ko0.cloudfront.net/v--3b8418686296/common--theme/shiny/images/yuitabs.png);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: #4D4D4D; /* selected tab background */
    color: white;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #b5b5b5;
    text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #636363;
}
.yui-navset li {
    line-height: normal;
}

/* INTERWIKI */

#interwiki .side-block {
    padding: 10px;
    border: 1px solid #474747;
    background: #FFFFFF;
    margin-bottom: 15px;
    border-radius: 2px;
}

#interwiki .heading {
    border-bottom: solid 1px #600;
    padding-left: 15px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}

[[/code]]
[[/collapsible]]
[[/iftags]]
