[[module CSS show="true"]]
@import url('https://lafundacionscp.wdfiles.com/local--code/theme%3Acomi/1');

@font-face {
    font-family: 'HS두꺼비';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_three@1.0/HS두꺼비체.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*
    SCP Sigma 9
    [2014 Wikidot Theme]
    Created for the SCP Foundation by Aelanna
    Edited for SCP Foundation by Dr Devan
    Imported and edited for Lasagneception LazyLasagne
    Logo made by Fan de Iván. Page in SCP Foundation Wikia: https://es.scp.wikia.com/wiki/Usuario:Fan_de_Iván
*/


#header h1 a {
    font-family: BauhausLTDemi, 'HS두꺼비', Arial, sans-serif;
}

/* Keycap */

.keycap {
    font-family: inherit;
}

/* For FireFox */
@-moz-document url-prefix() {
.emph {
    /* For the environments which comply with CSS3. */
    font-family: monospace;
}
}

/* For IE10 */
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
.emph {
    /* For the environments which comply with CSS3. */
    font-family: monospace;
}
}

/* off-canvas */

@media (max-width: 767px) {

#navi-bar, #navi-bar-shadow {
    display: none;
}

.open-menu a {
    font-family: 'Nanum Gothic', san-serif;
}
}

#header h1 a {
    content: "안보특수판무처";
    font-family: 'BauhausLTDemi', 'HS두꺼비', Arial, sans-serif;
}

#header h1 a::before {
    content: "안보특수판무처";
}

#header h2 span::before {
    content: '¡상승하라 에스파냐!';
}
[[/module]]
