[[code type="CSS"]]
@import url('https://scp-wiki.wdfiles.com/local--code/theme%3Acreepypasta/1');
@import url('https://fonts.googleapis.com/css?family=PT+Mono&display=swap');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnTaza.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnYetgul.css');

@font-face {
    font-family: 'MonoplexKR_Regular';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_Monoplex-kr@1.0/MonoplexKR-Regular.woff2') format('woff2');
    font-weight: 400;
    font-style: normal;
}

/*
    Powered on 2019/05/09 06:09:42
    [2019 Wikidot Theme]
    Created by The Great Hippo
    CC BY-SA 3.0
*/

body {
    font-family: 'PT Mono', 'MonoplexKR_Regular', monospace;
}

#page-title, h1, h2, h3, h4, h5, h6 {
    font-family: 'PT Mono', 'UnTaza', 'UnYetgul', monospace;
}
.bibitems .title {
    font-family: 'PT Mono', 'UnTaza', 'UnYetgul', monospace;
}

/* HEADER */

div#header {
    background: url(https://scpko.wdfiles.com/local--files/theme%3Acreepypasta/parawatch.png) 10px 40px no-repeat;
}

.code {
    font-family: "Courier", "Courier New", "Roboto Mono", monospace;
}

.licensebox .collapsible-block-link {
    margin-left:.25em;
    padding: .25em;
    font-weight: bold;
    opacity: .5;
    color: inherit;
    -webkit-transition: opacity 0.5s ease-in-out;
    -moz-transition: opacity 0.5s ease-in-out;
    transition: opacity 0.5s ease-in-out;
}
.licensebox .collapsible-block-link:hover {
    opacity:1;
}
.licensebox .collapsible-block-link:active {
    opacity:1;
}

/* MOBILE CODE */
@media (max-width: 767px) { 
    div#header {
        background-size: 22em;
        background-position: 1em;
    }

    #top-bar .open-menu a {
        border: 0.2em solid var(--bg-light);
        background-color: #787878;
        color: var(--bg-light);
  }
}

.ko-footer hr {
  background-color: var(--fg-accent-dark);
  margin: 1em 0;
}
[[/code]]

[[/iftags]]
