[[code type="css"]]
/*
    Darkcode Theme
    [2021 Wikidot Theme]
    By hoah2333
    Based on:
      Anderson Robotics Theme by Croquembouche
      BASALT Theme by AZAMO
      Monotypical Theme by EstrellaYoshte
*/

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@400;700&display=swap');

/* -----变量----- */

:root {
    --dark-bg: #1E1F26;
    --darker-bg: #060606;
    --sidebar-bg: var(--dark-bg);
    --topbar-bg: var(--darker-bg);
    --main-bg: #131417;
    --code-bg: #3c4252;
    --text-color: #bcc9c7;
    --strong-text-color: #9ea9b7;
    --header-color: #aaaebc;
    --link-color: #F9F9F9;
    --link-hover-color: #9b9dad;
    --border-color: #444857;
    --main-blue: #0ebeff;
    --main-green: #47cf73;
    --dark-green: #117a28;
    --main-red: #f74545;
    --dark-red: #703232;
    --main-yellow: #F9F1A5;
    --dark-bg-trans: rgba(120, 124, 152, 0.1);
    --scrollbar-color: var(--border-color);
    --terminal-bg: #222333;
    --terminal-text-color: #F4F4F4;

    --title: "SCP基金会";
    --subtitle: "控制 · 收容 · 保护";
    --sidesubtitle: var(--subtitle);

    --logo: url('https://cdn.jsdelivr.net/gh/Nu-SCPTheme/Black-Highlighter@gh-pages/img/logo.svg');
    --sidelogo: var(--logo);

    --body-font: verdana, arial, helvetica, sans-serif; /* Wikidot 默认字体 */
    --ui-font: 'Noto Sans SC', sans-serif;
    --mono-font: 'Fira Code', '幼圆', Cousine, monospace;
}


/* -----全局改动----- */

body {
    color: var(--text-color);
}

body,
input,
textarea,
select,
button,
meter,
progress,
input.text,
input.checkbox,
div.note,
#lock-info {
    font-family: var(--body-font);
}

div#container-wrap {
    background: var(--main-bg);
}

#content-wrap {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    max-width: 100vw;
    margin: 0 auto;
}


/* -----顶栏改动----- */

#header {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    height: 80px;
    max-width: calc(100vw - 18em);
    width: 100%;
    margin: 0 0 0 auto;
    background: none;
    padding: 0;
}

#extra-div-1 {
    z-index: 9;
    position: fixed;
    top: 0;
    right: 0;
    max-width: calc(100vw - 17em);
    width: 100%;
    height: 65px;
    background: var(--topbar-bg);
    border-bottom: solid 1px var(--border-color);
}

#extra-div-2 {
    z-index: 9;
    position: fixed;
    top: 0;
    right: 0;
    max-width: calc(100vw - 17em);
    width: 100%;
    height: 5rem;
}

#extra-div-1::before {
    content: var(--title);
    position: fixed;
    margin-left: 3rem;
    top: 0.2rem;
    color: var(--link-color);
    font-family: var(--ui-font);
    font-weight: 700;
    font-size: 27.45px;
}

#extra-div-2::before {
    content: var(--subtitle);
    position: fixed;
    font-size: 0.9rem;
    margin-left: 3rem;
    top: 2.5rem;
    color: var(--link-hover-color);
    font-family: var(--ui-font);
    font-weight: 500;
}

#header h1 {
    position: fixed;
    margin-left: 10px;
    font-size: 100%;
    opacity: 0;
    width: 10rem;
    height: 65px;
}

#header h1 a {
    padding: 1em 0;
    font-family: var(--ui-font);
}

#header h2 span {
    display: none;
}

#top-bar {
    position: fixed;
    top: 0;
    left: 45em;
}

#top-bar ul li a {
    height: 65px;
    padding: 32.5px 2em;
    color: var(--link-color);
    font-family: var(--ui-font);
}

#top-bar .top-bar>ul>li.sfhover>a,
#top-bar .top-bar>ul>li:hover>a,
#top-bar .mobile-top-bar>ul>li.sfhover>a,
#top-bar .mobile-top-bar>ul>li:hover>a,
#top-bar ul li.sfhover a:hover,
#top-bar ul li a:hover {
    color: var(--link-hover-color);
    background: var(--dark-bg);
    text-decoration: none;
    border-color: transparent;
}

#top-bar ul li ul {
    border-color: var(--border-color);
    border-width: 0 1px;
}

#top-bar .top-bar ul li.sfhover ul li a,
#top-bar .mobile-top-bar ul li.sfhover ul li a {
    background: var(--darker-bg);
    border: solid var(--border-color);
    border-width: 1px 1px 1px 0;
    color: var(--link-color);
}

#top-bar .top-bar ul li.sfhover ul li.sfhover a,
#top-bar .top-bar ul li.sfhover ul li:hover a,
#top-bar .mobile-top-bar ul li.sfhover ul li.sfhover a,
#top-bar .mobile-top-bar ul li.sfhover ul li:hover a {
    color: var(--link-hover-color);
    background: var(--dark-bg);
}

#top-bar ul {
    float: left;
}

#top-bar .top-bar ul li:last-of-type ul,
#top-bar .mobile-top-bar ul li:last-of-type ul {
    left: 0;
}

#search-top-box {
    position: fixed;
    top: 35px;
    right: 2rem;
    width: auto;
}

#search-top-box-form {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
}

#search-top-box #search-top-box-form>input[type=submit],
#search-top-box #search-top-box-form>input[type=submit]:hover,
