}

.inline-diff ins::before {
    color: var(--main-green);
}

.inline-diff del {
    background-color: var(--dark-red);
}

.inline-diff del::before {
    color: var(--main-red);
}

::-webkit-scrollbar {
    max-width: 5px;
    width: 5px;
    height: 5px;
}

::-webkit-scrollbar-thumb {
    background: var(--scrollbar-color);
}

* {
    scrollbar-color: #444857 #1b1c22;
    scrollbar-width: thin;
}


/* -----移动端适应----- */

#top-bar .open-menu a {
    position: fixed;
    top: 0.5em;
    left: 0.5em;
    z-index: 5;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    background-color: var(--darker-color);
    color: var(--link-color);
    border: none;
    border-radius: 0;
}

@media (max-width: 1350px) {
    #top-bar {
        left: 35em;
    }
}

@media (max-width: 1250px) {
    #header,
    #extra-div-1,
    #extra-div-2 {
        max-width: 100%;
    }
    #extra-div-1::before,
    #extra-div-2::before {
        margin-left: 4.7rem;
    }
    #top-bar {
        left: 20em;
    }
    #header h1 {
        left: 4.7em;
    }
    #main-content {
        max-width: calc(100vw - 10em);
        margin: 0 auto;
    }
    .yui-navset,
    blockquote,
    .blockquote {
        width: calc(100% - 5em);
    }
    .code {
        max-width: calc(100vw - 2em) !important;
    }
    #side-bar {
        display: block;
        position: fixed;
        top: 66px;
        left: -20em;
        width: 17.75em;
        height: 100%;
        margin: 0;
        overflow-y: auto;
        z-index: 10;
        padding: 1em 1em 0 1em;
        -webkit-transition: left 0.4s ease-in-out;
        -o-transition: left 0.4s ease-in-out;
        transition: left 0.4s ease-in-out;
        scrollbar-width: thin;
    }
    #side-bar:target {
        left: 0;
    }
    #top-bar .close-menu {
        margin-left: 19.75em;
        opacity: 0;
    }
    #side-bar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        z-index: -1;
    }
    #top-bar .open-menu a:hover {
        text-decoration: none;
    }
}

@supports selector(:focus-within) {
    @media (max-width: 1250px) {
        #top-bar .open-menu a {
            pointer-events: none;
        }
        #side-bar .close-menu {
            display: block;
            pointer-events: none;
            -webkit-user-select: none;
            -moz-user-select: none;
            -ms-user-select: none;
            user-select: none;
            z-index: -1;
        }
        #side-bar .close-menu::before {
            content: "";
            position: fixed;
            z-index: 5;
            display: block;
            top: 0.5em;
            left: 0.5em;
            border: 0.2em solid transparent;
            width: 30px;
            height: 30px;
            font-size: 30px;
            line-height: 0.9em;
            pointer-events: all;
            cursor: pointer;
        }
        #side-bar:focus-within {
            left: 0;
        }
        #side-bar:focus-within .close-menu::before {
            pointer-events: none;
        }
    }
}

@media (min-width: 768px) {
    #top-bar .mobile-top-bar {
        display: block;
    }
    #top-bar .mobile-top-bar li {
        display: none;
    }
}

@media (max-width: 1100px) {
    #extra-div-1::before {
        font-size: 22px;
        top: 0;
    }
    #extra-div-2::before {
        display: none;
    }
    #top-bar {
        top: 30px;
        left: 4.7rem;
    }
    #top-bar .top-bar>ul>li>a,
    #top-bar .mobile-top-bar>ul>li>a {
        height: 43px;
        padding: 17px 1em 17px 1em;
    }
    #top-bar .mobile-top-bar {
        position: relative;
    }
    #search-top-box #search-top-box-form>input[type=text] {
        display: none;
    }
    #search-top-box {
        top: 4px;
        right: 0.3rem;
    }
}

@media (max-width: 767px) {
    #top-bar {
        top: 18px;
    }
    #main-content {
        max-width: calc(100vw - 5em);
    }
}


/* -----DLC----- */

.dcflex2,
.dcflex3,
.dcflex4,
.dcflex5 {
    /* Safari */
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: horizontal;
    -webkit-box-direction: normal;
    -ms-flex-flow: row wrap;
    flex-flow: row wrap;
    -webkit-box-pack: justify;
    -ms-flex-pack: justify;
    justify-content: space-between;
}

.dcflex2>.dcbox,
.dcflex2>.dcimg {
    -ms-flex-preferred-size: calc(100% / 2 - 0.3%);
    flex-basis: calc(100% / 2 - 0.3%);
}

.dcflex3>.dcbox,
.dcflex3>.dcimg {
    -ms-flex-preferred-size: calc(100% / 3 - 0.3%);
    flex-basis: calc(100% / 3 - 0.3%);
}

.dcflex4>.dcbox,
.dcflex4>.dcimg {
    -ms-flex-preferred-size: calc(100% / 4 - 0.3%);
    flex-basis: calc(100% / 4 - 0.3%);
}

.dcflex5>.dcbox,
.dcflex5>.dcimg {
    -ms-flex-preferred-size: calc(100% / 5 - 0.3%);
    flex-basis: calc(100% / 5 - 0.3%);
}

@media (max-width: 767px) {
    .dcflex3>.dcbox,
    .dcflex3>.dcimg {
        -ms-flex-preferred-size: calc(100% / 3 - 3%);
        flex-basis: calc(100% / 3 - 3%);
    }
    .dcflex4>.dcbox,
    .dcflex4>.dcimg {
        -ms-flex-preferred-size: calc(100% / 3 - 3%);
        flex-basis: calc(100% / 3 - 3%);
    }
    .dcflex5>.dcbox,
    .dcflex5>.dcimg {
        -ms-flex-preferred-size: calc(100% / 3 - 3%);
        flex-basis: calc(100% / 3 - 3%);
    }
}

.dcbox,
.dcimg {
    background: var(--dark-bg);
    padding: 0.625rem 1.5rem 0.625rem;
    margin: 1rem 0;
    border: 2px solid var(--border-color);
    border-radius: 10px;
    position: relative;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}

.dcimg {
    margin-top: 2.5rem;
}

.dcimg>img:first-of-type {
    margin: -2.5rem 0 1.5rem 1.5rem;
    width: 55px;
    border: 2px solid var(--border-color);
}

.dcterminal {
    background: var(--terminal-bg);
    color: var(--terminal-text-color);
    border: 2px solid var(--border-color);
    padding: 0 0.7rem;
    margin: 0.7rem 0;
    font-size: 14px;
    font-family: var(--mono-font);
}

.dcterminal .cursor {
    -webkit-animation: blink .5s step-end infinite alternate;
    animation: blink .5s step-end infinite alternate;
    white-space: nowrap;
    overflow: hidden;
    padding-right: 0.1rem;
    border-right: 1px solid var(--terminal-text-color);
}

@-webkit-keyframes blink {
    50% {
        border-color: transparent;
    }
}

@keyframes blink {
    50% {
        border-color: transparent;
    }
}
