[[include :fondationscp:theme:dcd]]
[[module CSS show="true"]]
/* IMPORT */

@font-face {
    font-family: 'Jaemin';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_20-10-21@1.0/Jaemin.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+KR&display=swap');

/* TITRE DE LA PAGE */

#page-title {
  font-family: 'Special Elite', 'Jaemin', cursive;
}

/* HEADINGS */
h1 {
  font-family: 'Special Elite', 'Jaemin', cursive;
}

h2,
h3 {
  font-family: Merriweather, 'Noto Serif KR';
}

/* TOP BARRE */
#top-bar {
    font-family: 'Special Elite', 'Jaemin', cursive;
}

/* SIDEBAR */

#side-bar .side-block,
#side-bar .heading,
#side-bar a,
#side-bar a:hover {
    font-family: 'Special Elite', 'Jaemin', cursive;
}

/* NOM UTILISATEUR */

#login-status,
#login-status a,
#login-status ul a,
#account-topbutton {
    font-family: 'Special Elite', 'Jaemin', cursive;
}

/* COLLAPSIBLE */

.collapsible-block-link {
     font-family: Merriweather, 'Noto Serif KR';
}

/* LIENS */

.page-options-bottom a {
    font-family: 'Special Elite', 'Jaemin', cursive;
    font-size: 120%;
}

/* Adjust Mobile Image Size */
@media (max-width: 321px) {

#page-title,
#login-status,
#login-status a,
#login-status ul a,
#account-topbutton {
  font-family: 'Special Elite', 'Jaemin', cursive;
}
}

@media (min-width: 322px) and (max-width: 425px) { 
#page-title,
#login-status,
#login-status a,
#login-status ul a,
#account-topbutton {
  font-family: 'Special Elite', 'Jaemin', cursive;
}
}
[[/module]]
[[/div]]
