  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li > a::before { font-size: 0.8rem; }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(1) > a::before {
     content: "SCP 인터나쇼날 허브";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(2) > a::before {
     content: "로씨야 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(5) > a::before {
     content: "뽈스까 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(6) > a::before {
     content: "에스빠냐 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(7) > a::before {
     content: "타이 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(9) > a::before {
     content: "도이췰란드 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(10) > a::before {
     content: "이딸리아 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(12) > a::before {
     content: "뽀르뚜갈 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(13) > a::before {
     content: "체스꼬·슬로벤스꼬 SCP";
  }
  #top-bar .top-bar > ul > li:nth-child(5) > ul > li:nth-child(15) > a::before {
     content: "윁남 SCP";
  }

  #side-bar {
    position: fixed;
    z-index: 1;
    top: 315px;
    left: var(--towerleft);
    bottom: 15px;
    margin-left: 31px;
    overflow-y: scroll;
    z-index: 15;
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  #side-bar::-webkit-scrollbar {
    display: none;
  }

  #side-bar .side-block, #side-bar .red, #side-bar .green, #side-bar .blue {
    background: none;
    box-shadow: none;
    border: none;
    filter: brightness(1.2);
  }

  #page-content {
    font-size: 105%;
  }
}

@media(max-width: 767px) {
  :root {
    --towerleft: -100px;
    --towerwidth: 160px;
  }

  #top-bar .mobile-top-bar > ul > li:nth-child(3) > ul > li:nth-child(1) > a {
    font-size: 0;
    width: 144px;
    padding: 0.6rem 1.2rem 0.4rem 0.76rem;
  }
  #top-bar .mobile-top-bar > ul > li:nth-child(3) > ul > li > a::before { font-size: 0.8rem; }
  #top-bar .mobile-top-bar > ul > li:nth-child(3) > ul > li:nth-child(1) > a::before {
