  }

  #header h1 a::before {
    content: var(--slogan1m) "　\A　" var(--slogan2m) "\A";
    font-size: 150%;
  }

  #login-status, #login-status a { font-size: 0; }
  #account-topbutton { display: none; }

  #main-content {
    padding-left: 50px;
  }

  #top-bar .mobile-top-bar { display: flex; }
  #top-bar .open-menu a {
