/* Add: officeテーマをimport */
  @import url("https://scp-jp.github.io/files/theme/scp-offices-theme/style0.css");

/* #a86c6b */

  :root {

    --header-title:"欺瞞部門";
    --header-subtitle:"確保、収容、保護";

    --white-monochrome: 216, 216, 216;
    --pale-gray-monochrome: 233, 233, 233;
    --light-gray-monochrome: 147, 147, 147;
    --gray-monochrome: 127, 127, 127;
    --dark-gray-monochrome: 101, 101, 101;
    --black-monochrome: 25, 20, 16;
    --bright-accent: 168, 108 ,107;
    --medium-accent: 168, 108, 107;
    --dark-accent: 105, 63, 63;

    --logo-image: url("http://scp-jp-storage.wikidot.com/local--files/file:2859452-192-bj8q/deception_logo_theme.png");

  }

/* Add: ロゴ画像のサイズ調整 */

  @supports (display: grid){
    @media only screen and (max-width: 56.25rem){
      #header {
          background-position: top calc(var(--header-height-on-desktop) * -0.15) left -2.5rem;
          background-size: calc(var(--header-height-on-desktop) * 1.3) auto;
      }
    }
  }

[[/code]]

