[[code type="css"]]
@import url('https://scp-wiki.wdfiles.com/local--code/theme:decom-dept/1');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnBatangBold.css');

h1,h2,h3,h4,h5,h6,#page-title {
    font-family: 'Roboto Slab', '바탕', 'UnBatangBold', serif;
}

/* ---- HEADER ---- */

div#header h1 a:before {
    content: "퇴역부";
    font-family: 'Roboto Slab', 'UnBatangBold', serif;
}

div#header h2:after {
    content: "확보, 격리, 보호";
}

@media (max-width: 1000px) {
    div#header h1 a:before {
    content: "퇴역부";
    }
}

/* ---- SEARCH ---- */

#search-top-box-form input[type=submit]:focus {
    background: #c3c3c3;
}
#search-top-box-form input[type=submit] {
    background: #909090;
}
#search-top-box-form input[type=submit]:hover {
    background: #aaa;
}
[[/code]]
