[[code type="css"]]

@import url('httpss://fonts.googleapis.com/css2?family=Kosugi&display=swap');

@import url('httpss://fonts.googleapis.com/css2?family=BIZ+UDPMincho:wght@200&display=swap');


/* HEADER */
div#header {
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4589204-221-4s2v/墓碑部門.logo%28白%29) 5px 25px no-repeat;
    background-size: 125px;
    background-position: top 15px left 10px ;
}

div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "SCP財団";
    color: #fff;
    font-family: 'BIZ UDPMincho', serif;
    text-shadow: none;
    font-size: 12px;
    position: absolute;
    top: 50px;
    left: 135px;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:before {
    content: "墓碑部門";
    white-space: pre;
    font-size: 30px;
    color: #fff;
    text-shadow: none
    padding-top: 2px;
    line-height: 17px;
    font-family: 'BIZ UDPMincho', serif;
    font-weight: normal;
    position: absolute;
    top: 69px;
    left: 135px;
}

div#header h2:after {
    content: "無銘の墓に墓碑銘を刻む";
    white-space: pre;
    font-size: 15px;
    color: #fff;
    text-shadow: none;
    padding-top: 2px;
    line-height: 17px;
    font-family: 'BIZ UDPMincho', serif;
    font-weight: Lighter;
    position: absolute;
    top: 98px;
    left: 134px;
}

div#container-wrap {
    height: 160px;
    background: #333;
    background-position: 50% 50%;
}

/* BODY */
body {
    color: #000000;
}

.scp-image-block .scp-image-caption{
    background: #eeeeee;
}

/* TOP-BAR */
#top-bar {
    position: absolute;
    top: 138px;
    margin: 0px;
    display: flex;
    justify-content: center !important;
}

#top-bar a {
    background: none;
    color: #fff;
    font-family: 'BIZ UDPMincho', serif;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #333333;
    border-right: none;
    border-left: none;
    color: #ffffff;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #ffffff;
    color:#000000;
    font-family: 'BIZ UDPMincho', serif;
}

/* SIDEBAR */
.open-menu a {
     position: fixed;
     top: 20px;
     left: 0px;
     z-index: 15000;
     font-size: 0px;
     width: 25px;
     height: 110px;
     text-align: center;
     padding-top: 5px;
     border: transparent !important;
     background-color: rgba(0,0,0,0.6) !important;
     border-radius: 0px 15px 15px 0px;
     color: transparent !important;
    }

.open-menu a::before {
     content: "";
     background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4589204-216-pnrn/menu◀) 3px no-repeat;
     background-size: 20px;
     width: 30px;
     display: block;
     height: 110px;
     border-radius: 0px 15px 15px 0px;
}

.open-menu a:hover {
     filter: invert(100%);
     box-shadow: 2px black;
}


div#side-bar div.side-block {
    border-radius: 0px;
    box-shadow: 0 1px 1px #000000;
    background: #ffffff;
    font-family: 'BIZ UDPMincho', serif;
}

#interwiki .side-block {
    border-radius: 0px;
    box-shadow: 0 1px 1px #000000;
    font-family: 'BIZ UDPMincho', serif;
}

/* SEARCHBOX */
#search-top-box-input {
    display: none;
}

#search-top-box-form input[type=submit] {
    border: solid 1px #000000;
    border-radius: 50%;
    padding: 2px 5px;
    font-size: 0%;
    font-weight: lighter;
    color: none;
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4589204-204-yiaw/search) center no-repeat;
    background-size: 15px;
    box-shadow: none;
    width: 20px;
    height: 20px;
    cursor: pointer;
    position: relative;
    top: 3px;
    right: 0px;
    margin: auto;
    filter: invert(100%);
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
    border: solid 1px white;
    border-radius: 50%;
    padding: 2px 5px;
    font-size: 0%;
    font-weight: lighter;
    color: none;
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4589204-204-yiaw/search) center no-repeat;
    background-size: 15px 15px;
    background-color: white;
    box-shadow: none;
    width: 20px;
    height: 20px;
    filter: invert(0%);
    box-shadow: 2px white;
}

#search-top-box {
    position: absolute;
    top: 5px;
    right: 0px;
    width: 250px;
    text-align: right;
}

 /* LOGIN SETTING */
#login-status{
    top: 6px;
    right: 30px;
    color: #ffffff;
}

#header .printuser{
    font-size: 0;
}

#header .printuser img.small{
    margin: 0;
    transform: translate(6px, 4px);
}

#my-account{
    display: none;
}

#account-topbutton{
    margin-left: 2px;
    font-size: 100%;
    color: #000000;
    border: solid 1px white;
}


 #page-title{
    border-bottom: solid 2px #000000;
    text-align: center;
    color: #000;
    font-weight: lighter;
    position: relative;
    top: -15px;
    font-family: 'BIZ UDPMincho', serif;
}
.pseudocrumbs,
 #breadcrumbs{
    font-size: 90%;
    position: relative;
    top: -10px;
}

/* RATEBOX */
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background: #333333;
    border: solid #333333 1px!important;
    border-radius: 0em;
    font-family: 'Noto Serif JP', serif;
    box-shadow: none;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #333333 1px!important;
    border-radius: 0em;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
{
    color: rgb(255, 255, 255);
    font-family: 'Noto Serif JP', serif;
}
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: rgb(255, 255, 255);
    border-radius: 0em;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #333333!important;
    box-shadow: none;
    border-radius: 0em;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #fff!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: White!important;
    color: #333333!important;
}


#page-content .rate-box-with-credit-button .creditButton {
    background: #333333;
    border: solid #333333 1px!important;
    border-radius: 0em;
    color: white;
    height: 18px;
    width: 25px;
    position: relative;
    left: -5px;
    box-shadow: none;
}
#page-content .rate-box-with-credit-button .creditButton:hover {
    background: #ffffff;
    border: 1px solid #111111;
    color: #333333!important;
    box-shadow: none;
    filter: invert(0%)
}
#page-content .rate-box-with-credit-button .creditButton a:hover {
    color: #333333;
}
#page-content .modalbox .credit {
    color: black;
}
#page-content .modalbox h2 {
    color: #ffffff;
}
.modalbox .modalbox-title {
    background: #333333!important;
    border-radius: 0 !important;
}
.modalbox {
    box-shadow: 0 2px 6px #cccccc !important;
    border-radius: 0 !important;
    background-color: White  !important;
}


/* TAB */

.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background-color: inherit;
    background-image: inherit
}

.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus {
    background: inherit;
    text-decoration: inherit
}

.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    color: inherit;
    background: inherit
}

.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-color: inherit
}

.yui-navset li {
    line-height: inherit
}


.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    display: flex;
    flex-wrap: wrap;
    width: calc(100% - .125rem);
    margin: 0 auto;
    border-color: #333333;
    box-shadow: none;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    color: #333333;
    background-color: #efefef;
    border: unset;
    box-shadow: none;
    box-shadow: none;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    color: #ffffff;
    background-color: #333333;
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    position: relative;
    display: flex;
    flex-grow: 2;
    max-width: 100%;
    margin: 0;
    padding: 0;
    color: #ffffff;
    background-color: #ffffff;
    border-color: transparent;
    box-shadow: none;
}

.yui-navset .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset-bottom .yui-nav li a {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
}

.yui-navset .yui-nav li em {
    border: unset;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    padding: .35em .75em;

    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected {
    flex-grow: 2;
    margin: 0;
    padding: 0;
    background-color: #333333;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border: none;
}

.yui-navset .yui-nav .selected a {
    width: 100%;
    color: #ffffff;
}

.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:active {
    color: #ffffff;
    background-color: #333333;
}

.yui-navset .yui-content {
    background-color: #ffffff;
    box-shadow: 0px 0px 4px #999999;
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    padding: .5em;
    border: none;
}

/* blockquote */
blockquote {
    border-left: solid 5px #BDBDBD;
    border-right: solid 1px #BDBDBD;
    border-top: solid 1px #BDBDBD;
    border-bottom: solid 1px #BDBDBD;
    background: #f2f2f2;
    padding: 5px;
}

/* toc */
div#toc {
   background: #ffffff;
   border-left: solid 10px #555555;
   border-right: solid 1px #555555;
   border-top: solid 1px #555555;
   border-bottom: solid 1px #555555;
}

/* report box */
.report_box {
   border-left: solid 5px #333333;
   border-right: solid 1px #ffffff;
   border-top: solid 1px #ffffff;
   border-bottom: solid 1px #ffffff;
   background :#ffffff;
   margin: 1em auto;
   padding: 1em;
   width: 90%;
   box-shadow: 2px 5px 10px 0px rgba(182, 182, 182, 1);
}


/* fadeIn */
@media screen and (prefers-reduced-motion: no-preference) {
  #page-title, #breadcrumbs, #page-content > * {
    animation-name: fadeIn;
    animation-duration: 1s;
    animation-iteration-count: 1;
    animation-timing-function: ease-out;
    animation-fill-mode: backwards;
  }
}

#page-title { animation-delay: 0s; }

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translate(0,30px);
  }
  to {
    opacity: 1;
    transform: translate(0,0);
  }
}



/* MOBILE-THEME */

@media (min-width: 769px) {

.mobile-top-bar {
   display: block;
}

.mobile-top-bar li {
    display: none;
}

#main-content {
    max-width: 708px;
    margin: 0 auto;
    padding: 0;
    transition: max-width 0.2s ease-in-out;
}

.open-menu a {
    position: fixed;
    top: 20px;
    left: 0px;
    z-index: 15000;
    font-size: 0px;
    width: 25px;
    height: 110px;
    text-align: center;
    padding-top: 5px;
    border: transparent !important;
    background-color: rgba(0,0,0,0.6) !important;
    border-radius: 0px 15px 15px 0px;
    color: transparent !important;
    }

.open-menu a::before {
    content: "";
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4589204-216-pnrn/menu◀) 3px no-repeat;
    background-size: 20px;
    width: 30px;
    display: block;
    height: 110px;
    border-radius: 0px 15px 15px 0px;
}

.open-menu a:hover {
    filter: invert(100%);
    box-shadow: 2px black;
}

#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 17em;
    height: 100%;
    background-color: rgb(0,0,0,0.3);
    overflow-y: auto;
    z-index: 10;
    border: solid 1px #BB0000;
    border-radius: 0px;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);
}

#side-bar:target {
    display: block;
    left: 0;
    width: 17em;
    margin: 0;
    border: 1px solid #dedede;
    z-index: 10;
}

#side-bar:target + #main-content {
    left: 0;
}

#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
}
}

