[[collapsible show="+ 显示版式代码" hide="- 隐藏版式代码"]]
[[code type="css"]]

/*
   Día de Muertos Theme
   [2022 Wikidot Theme]
   by Kilerpoyo
          an extension of Old Money Theme by djkaktus & Woedenaz.
*/

@import url('https://scp-wiki.wdfiles.com/local--files/theme%3Adia-de-muertos/papelpicadostyle.css');
@import url('https://scp-wiki.wdfiles.com/local--files/theme%3Adia-de-muertos/doubled-crossed.css');
@import url('https://scp-wiki.wdfiles.com/local--files/theme%3Adia-de-muertos/colus.css');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC&display=swap');
@import url('https://static.zeoseven.com/zsft/404/main/result.css');
@font-face { 
 font-family: 'XinYuGongCangSangTi';
 font-style: normal; font-weight: 400; font-display: swap;
  src: url('https://fastly.jsdelivr.net/gh/Etinjat/Fonts@main/FONT_XinYuGongCangSangTi.woff2') format('woff2');
}

:root {

    --theme-id: "dia-de-muertos";
    --theme-name: "Dia de Muertos Theme";

    --logo-image: url("https://scp-wiki.wikidot.com/local--files/theme:dia-de-muertos/muertoswhite.png");
    --header-title: ":SCP&";
    --header-subtitle: "Nadie sale vivo de esta vida.";

    /* Typefaces */
    --body-font: Verdana, serif;
    --header-font: 'Colus', Verdana, serif;
    --title-font: 'Colus', Verdana, serif;
    --UI-font: --UI-font: Roboto, sans-serif;
    --mono-font: monaco, monospace;
    --decorative-font: 'papel_picadoregular', 'Colus', Verdana;
    --decorative-font2: 'double_crossedregular', 'Colus', Verdana;

    /* Día de Muertos theme palette */
    --mexican-pink: 228, 0, 124;
    --pink-flamingo: 184, 0, 101;
    --yellow-submarine: 255, 247, 0;
    --deep-purple: 113, 23, 145;
    --cempasuchil-fields: 255, 128, 0;
    --pale-lady: 243, 241, 238;
    --charro-negro: 12, 12, 12;


    /* THEME ASSIGNMENTS */
    --white-monochrome: var(--pale-lady);
    --gray-monochrome: var(--mexican-pink);
    --dark-gray-monochrome: var(--pink-flamingo);
    --black-monochrome: var(--charro-negro);
    --pale-accent: var(--cempasuchil-fields);
    --bright-accent: var(--cempasuchil-fields);
    --medium-accent: var(--pink-flamingo);
    --dark-accent: var(--deep-purple);
    --alt-accent: var(--yellow-submarine);

    /* MISCH TWEAKS */
    --swatch-tertiary-color: var(--pink-flamingo);
    --link-color: var(--yellow-submarine);
    --visited-link-color: var(--cempasuchil-fields);

}

:root:lang(cn) {
    --logo-image: url("https://scp-wiki.wikidot.com/local--files/theme:dia-de-muertos/muertoswhite.png");
    --header-title: ":SCP&";
    --header-subtitle: "无人活着离开世间。";


    --body-font: Verdana, 'Noto Sans SC', serif;
    --header-font: 'Colus', Verdana, 'WSQuanXing', sans-serif;
    --title-font: 'Colus', Verdana, 'WSQuanXing', sans-serif;
    --UI-font: Roboto, sans-serif;
    --mono-font: monaco, monospace;
    --decorative-font: 'papel_picadoregular', 'Colus', Verdana, XinYuGongCangSangTi, sans-serif;
    --decorative-font2: 'double_crossedregular', 'Colus', Verdana, XinYuGongCangSangTi, sans-serif;
}

/* Webkit Scrollbar */
::-webkit-scrollbar-thumb,
::-webkit-scrollbar-thumb {
    background: rgba(var(--mexican-pink), 1);
}

/* YUI NAV BAR */
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:active,
.yui-navset .yui-nav a:focus {
    color: rgb(var(--pale-lady));
}

/* TOP BAR */
#top-bar {
    --topmenu-hover-border-color: var(--bright-accent);
}

h1, h2, h3, h4, h5, h6{
    font-synthesis:none;
}

/* HEADER H1 */
#header h1 a::before {
    font-size: 2em;
    --wght: 100;
    background-blend-mode: normal;
    font-family: var(--decorative-font);
}

#header h1 a::before {
    background-image: linear-gradient(left, rgb(var(--mexican-pink)) 0em, rgb(var(--mexican-pink)) 1.1em, rgb(var(--cempasuchil-fields)) 1.1em, rgb(var(--cempasuchil-fields)) 2.75em, rgb(var(--pale-lady)) 2.75em, rgb(var(--pale-lady)) 20em);
    background-image: -webkit-linear-gradient(left, rgb(var(--mexican-pink)) 0em, rgb(var(--mexican-pink)) 1.1em, rgb(var(--cempasuchil-fields)) 1.1em, rgb(var(--cempasuchil-fields)) 2.75em, rgb(var(--pale-lady)) 2.75em, rgb(var(--pale-lady)) 20em);
    background-size: 20em;
    background-clip: text;
    -webkit-background-clip: text;
    text-fill-color: transparent;
    -webkit-text-fill-color: transparent;
}

/* HEADER H2 */
#header h2 span {
    margin-top: calc(((var(--header-height-on-desktop) / 2) + (var(--header-h1-font-size) / 2) - var(--header-h2-font-size) + 2em));
    font-size: 1em;
}

/* PAGE TITLES */
#page-title,
.meta-title,
#page-content h1,
#page-content h2,
#page-content h3,
#page-content h4,
#page-content h5,
#page-content h6 {
    -webkit-background-clip: none;
    background-clip: none;
    background-color: rgb(var(--yellow-submarine));
    background-image: none;
    background-blend-mode: normal;
    -webkit-text-fill-color: rgb(var(--yellow-submarine));
}

/* SIDE BAR HEADING */
#side-bar .heading p,
#interwiki .heading p {
    background-image: none;
    -webkit-background-clip: none;
    background-clip: none;
    color: rgb(var(--swatch-menutxt-dark-color));
    color: rgb(var(--bright-accent));
    -webkit-text-fill-color: rgb(var(--bright-accent));
    overflow-wrap: normal;
}

/* TABLE WIKI */
table.wiki-content-table th {
    background-color: rgb(var(--swatch-primary-darker));
    background-image: none;
}


/* MOBILE SUPPORT */
@media only screen and (max-width: 56.25rem) {

    /* HEADER H1 */
    #header h1 a {
     margin-top: calc(((var(--header-height-on-mobile) / 2)) - 0.75em);
     margin-left: calc(var(--header-height-on-mobile) - 3.5rem);
     font-size: clamp(1.1em, 7vw, 3em);
     line-height: 0.5;
    }

    #header h1 a::before {
     font-size: 3.5rem;
    }

    /* HEADER H2 */
    #header h2 span {
     margin-top: calc(((var(--header-height-on-mobile) / 2) + 1.5em));
     margin-left: calc(var(--header-height-on-mobile) - 3.5rem);
     font-size: 0.75em;
     line-height: 1;
     text-align: left;
    }
}
[[/code]]
