content:
[[module CSS]]
/*
    对话部门版式
    Created at 2020
    Created by dog_punch
    CC BY-SA 3.0
*/
@import url(https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@200;300;400;500;600;700;900&display=swap);
div#container-wrap {
    background: linear-gradient(45deg, #fcfcfc, #fff);
}
a:hover {
    color: #78b0c2b5;
}
body {
    color: #000;
}

a {
    color: #78B0C5;
    text-decoration: none !important;
}
#main-content a {
    transition: .3s;
}
div#header {
    width: 100%;
    max-width: none;
    background: url(http://scp-jp-storage.wikidot.com/local--files/file:6057712-1-ftes/logo.png), linear-gradient(45deg, #383838 55%, #000);
    background-size: 100px, 100%;
    background-attachment: fixed;
    background-position: center 8px, 0;
    padding-bottom: 21;
    height: 165px;
    background-repeat: no-repeat;
}

#header:after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    bottom: -1px;
    right: 0;
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 90%, #181818 100%);
}

#header h1 a span {
    display: none;
}

#header h1 a:after {
    content: '对话部门';
    text-shadow: 1px 1px 3px #000;
}

#header h1 a {
    padding: 120px 0 25px;
    font-family: 'Noto Serif SC';
    font-size: 180% !IMPORTANT;
}

#header h1 {
    text-align: center;
    z-index: 100;
    margin: 0;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    font-size: 170% !Important;
}

div#header h2 {
    margin: 0;
    width: 100%;
    text-align: center;
    z-index: 100;
    color: #fff;
    font-family: 'Noto Serif SC';
    text-shadow: 0px 0px 3px #000;
    transform: translateY(143px);
}

#header h2 span {
    display: none;
}

#header h2:after {
    content: '对话，理解，心疗';
}

#search-top-box-form input[type=submit] {
    display: none;
}

input#search-top-box-input {
    border-radius: 0;
    background: linear-gradient(45deg, black, #292929);
    padding: 3px;
    border: 2px solid #999 !IMPORTANT;
    width: 6em;
    transition: 0.3s;
    outline: none;
    color: #999 !IMPORTANT;
}

input#search-top-box-input:-internal-autofill-selected {
    background: linear-gradient(45deg, #181818, #252525);
    color: #999;
}

input#search-top-box-input:focus {
    width: 11em;
}

#search-top-box {
    top: 35px;
    z-index: 150;
}

#search-top-box input.empty {
    color: transparent !important;
}

#page-title,
#main-content h1 {
    border-color: #bbb;
    color: #203A43;
    background: -webkit-linear-gradient(to left, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to left, #0f2027, #203a43, #2c5364);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-family: 'Noto Serif SC';
    font-weight: 600;
}

#top-bar {
    top: 166px;
    right: 0em;
    left: 0;
    margin: 0;
    max-width: none;
}

#top-bar ul {
    float: none;
    display: flex;
    width: 100%;
    justify-content: space-around;
}
#top-bar ul > li > ul {
    flex-wrap: wrap;
    border-width: 0;
}
#header>#top-bar div.mobile-top-bar {
    max-width: none;
    width: 100%;
    left: 0;
}

#top-bar ul li {
    border: 0;
    position: relative;
        flex-basis: 100%;
        flex-grow: 1;
        flex-shrink: 1;
}
#top-bar ul li ul li,
#top-bar ul li ul li.sfhover,
#top-bar ul li ul li,
#top-bar ul li ul li:hover {
    width: 100%;
}
#top-bar li ul li a {
    background-color: rgba(10,10,10,.8);
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    width: 100%;
    box-sizing: border-box;
    transition: .3s background;
}

#top-bar ul li a {
    background: linear-gradient(0deg, #181818, transparent);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
        background: rgba(30,30,30,.8);
        color: #fff;
    border-: none !IMPORTANT;
}

#top-bar ul>li:hover>ul>li a,
#top-bar ul>li.sfhover>ul>li a {
    padding: 5px;
    font-size: 102%;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
        background: rgba(10,10,10,.8);
}

#top-bar ul li a {
    border: none !IMPORTANT;
}
div.page-rate-widget-box {
    box-shadow: none !important;
    border-bottom: 2px solid #292929;
    border-radius: 0;
}
.creditRate .rateBox {
    display: inline-flex;
    border-bottom: 2px solid #292929;
}
.creditRate .rateBox div.page-rate-widget-box {
    border-bottom: none !Important;
}
.page-rate-widget-box .rate-points {
    background-color: transparent !important;
    border: none;
    border-radius: 0;
    color: #292929;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: transparent;
        border: none;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: transparent !important;
    color: #29292942 !important
}

.page-rate-widget-box .cancel {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: #292929 !Important;
}

.page-rate-widget-box .cancel a:hover {
    color: #29292942 !important;
    background: transparent !important;
}

.modalbox .page-rate-widget-box {
    position: absolute;
    left: 50%;
    transform: translateX(-50%)
}

.rate-box-with-credit-button .creditButton {
    background-color: transparent !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: none !Important;
}

a.fa.fa-info {
    color: #292929 !important
}

a.fa.fa-info:hover {
    color: #29292942 !important
}

.modalbox .modalbox-title {
    background: rgba(1, 1, 1, 0.9) !important;
    color: #fff !important;
    border-radius: 0 !important;
}

.modalbox {
    box-shadow: 0 2px 6px #ccc !important;
    border-radius: 0 !Important;
    background-color: rgba(365, 365, 365, 0.9) !important;
}

.rate-box-with-credit-button a {
    text-decoration: none !important
}

.page-rate-widget-box .cancel a {
    color: #292929 !important
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #292929 !important
}

#login-status #account-topbutton:not(:focus):not(:focus-within):not(:hover):not(:active)+#account-options {
    display: none!important;
}

#header #login-status #account-topbutton+#account-options:focus,
#header #login-status #account-topbutton+#account-options:focus-within,
#header #login-status #account-topbutton+#account-options:hover,
#header #login-status #account-topbutton+#account-options:active {
    display: block!important;
}

#account-options ul {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
}

#account-options ul>li {
    flex-grow: 1;
    text-align: center;
    width: 40%;
}

#account-options ul>li a {
    transition: all 0.25s ease-in-out;
}

#account-options ul>li a:hover {
    color: #fff;
    background: linear-gradient(to left, #181818, rgba(64, 64, 64, 0.8), #181818);
    text-decoration: none;
    text-shadow: none;
}

#login-status ul a {
    color: #000;
}

#login-status {
    color: #fff;
    z-index: 150;
    text-shadow: 0px 0px 3px #fff;
}

#side-bar .side-block {
    border-radius: 0;
    position: relative;
    border: none;
    box-shadow: 0px 0px 4px #292929;
    color: #203A43;
    background: #fff;
}

#side-bar .side-block a {
    color: #203A43;
    background: -webkit-linear-gradient(to left, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to left, #0f2027, #203a43, #2c5364);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

#side-bar .collapsible-block-unfolded-link,
#side-bar .collapsible-block-unfolded {
    border-color: #203A43;
}

#side-bar .heading {
    border-color: #203A43;
    color: #203A43;
    background: -webkit-linear-gradient(to left, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to left, #0f2027, #203a43, #2c5364);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

#side-bar div.menu-item img {
    filter: hue-rotate(180deg);
}

#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link {
    position: relative;
    z-index: 0;
}

#side-bar .collapsible-block-folded::before,
#side-bar .collapsible-block-unfolded-link::before {
    content: '';
    position: absolute;
    top: 0px;
    bottom: 0px;
    left: 0px;
    right: 0px;
    background: inherit;
    filter: hue-rotate(180deg)brightness(1.7);
    ;
    z-index: -1;
}

img.image[title="SCP-JP Discord Chat"],
img.image[title="SCP-JP Twitter"] {
    filter: hue-rotate(310deg) !important;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    border: none;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: transparent;
    border: 0;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: none;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: none;
    color: #fff;
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    margin: 0;
    border: 0;
    flex-grow: 1;
    text-align: center;
    transition: 1s;
    color: #000;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    color: #000;
}

.yui-navset .yui-nav li:hover,
.yui-navset .yui-navset-top .yui-nav li:hocer {
    background: #ccc;
    color: #000;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border: 1px solid #181818;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    background: #fff;
    border-bottom-width: 3px;
}

.yui-navset .yui-nav .selected a em {
    padding: 0.25em .75em;
}

.yui-navset .yui-nav li:hover,
.yui-navset .yui-navset-top .yui-nav li:hover {
    background: rgba(0, 0, 0, 0.1);
}

.yui-navset .yui-nav li:hover a,
.yui-navset .yui-navset-top .yui-nav li:hover a {
    color: #000;
}

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected {
    margin: 0;
    animation: taiwan 0.5s ease-in-out both;
}

@keyframes taiwan {
    100% {
     background: #181818;
    }
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-navset-top .yui-nav .selected a {
    animation: taiwan2 0.5s ease-in-out both;
}

@keyframes taiwan2 {
    100% {
     color: #fff;
    }
}

.yui-navset .yui-content {
    background-color: #fff;
}


#page-content .yui-navset .yui-content>div[style*="block"] {
    animation: taiwan-tab 1.2s ease-in-out 0s 1 both;
    overflow: hidden;
}

@keyframes taiwan-tab {
    0% {
     max-height: 25px;
    }
    20% {
     max-height: 0px;
    }
    99% {
     max-height: 100vh;
    }
    100% {
     max-height: 9999rem;
    }
}

div.page-options-bottom {
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    margin: 0;
}

a.btn-default {
    flex-grow: 1;
    text-align: center;
    margin: 0 1px !Important;
    margin-bottom: 2px !Important;
    color: #fff;
    padding: 4px 10px;
    background: #1f5162;
    border: 2px solid #d5d5d5;
    transition: 0.5s;
}

div#page-options-bottom {
    margin-top: 3px;
}

a.btn-default:hover {
    text-decoration: none;
    background: #23657c;
}

.hovertip {
    width: 20em;
    padding: 0.5em;
    border: 2px solid #292929 !important;
    background: #292929 !important;
    box-shadow: 0 0 6px 1px #000000aa;
    z-index: 100;
    color: #fff;
    animation: hoverfade .5s ease-in-out both;
    border-radius: 7px;
}

@keyframes hoverfade {
    0% {
     opacity: 0;
    }
    100% {
     opacity: 1;
    }
}

.hovertip .f-heading {
    color: #cccccc;
}

div.owindow {
    background: #1e1e1ee3;
    border: 2px solid #fff;
    color: #fff;
}

.owindow .title.modal-header {
    background: #181818;
    border-bottom: #555 1px solid;
}

div.owindow>div.content>img {
    background: transparent !important;
}

div.owindow>div.content>h1 {
    color: #78b0c2;
}

.owindow .button-bar a:hover {
    background: #181818;
}
.owindow .button-bar a {
    background: #3b3b3b;
    color: #fff;
    transition: .3s;
}
.owindow .title {
    background-color: #090c16cc;
}
#footer {
    background: linear-gradient(to right, #0f2027, #203a43, #2c5364);
    color: #fff;
    padding: 11px 3px;
}

#license-area {
    color: #fff;
    background: #162d35;
    border-top: 1px solid #fff;
}

.odialog-shader {
    background-color: #3e5166;
    filter: alpha(opacity=80);
    opacity: .3;
}

@media (max-width: 767px) {
    #side-bar {
     background-color: rgba(20, 20, 70, .7);
    }
}

div.open-menu a[href="#side-bar"] {
    border: 5px solid #2c5364 !important;
    color: #2c5364 !IMPORTANT;
    border-radius: 5px;
    background: transparent !IMPORTANT;
    box-shadow: 0 0 5px #fff;
    text-shadow: 0 0 5px #fff;
}
::-webkit-scrollbar {
    border: none;
}

#side-bar::-webkit-scrollbar {
    width: 10px;
}
@media (max-width: 767px)
#side-bar:target .close-menu {
    background: rgb(1 134 255 / 14%) 1px 1px repeat;
}
hr {
    height: 0.8px;
}
.modalbox>.modalbox-title>h2>span {
    font-family: 'Noto Serif SC';
}
div.buttons input, input.button, button, file, a.button {
    margin: 0;
    padding: 3px;
    background-color: #265c6e;
    color: #fff;
    transition: .3s;
    border: 2px solid #dadada;
}
div.buttons input:hover, input.button:hover, button:hover, a.button:hover {
    background-color: #4f79cd;
    color: #fff;
}
input:focus,textarea:focus  {
    outline: none !Important;
}
#lock-info {
    background-color: #254a53;
    border: 2px solid #ccc;
    color: #fff;
}
.page-rate-widget-box .rate-points {
    color: #292929 !Important;
}
[[/module]]
